    return fract(sin(dot(_st,
                         vec3(12.9898,78.233,123.24647)))*
      43758.5453123);
}

vec3 hsl2rgb( in vec3 c )
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );

    return c.z + c.y * (rgb-0.5)*(1.0-abs(2.0*c.z-1.0));
}

float noise3 (in vec3 _st) {
    vec3 i = floor(_st);
    vec3 fr = fract(_st);

    // Four corners in 2D of a tile
    float a = random3(i);
    float b = random3(i + vec3(1.0, 0.0, 0.0));
    float c = random3(i + vec3(0.0, 1.0, 0.0));
    float d = random3(i + vec3(1.0, 1.0, 0.0));

    float e = random3(i + vec3(0.0, 0.0, 1.0));
    float f = random3(i + vec3(1.0, 0.0, 1.0));
    float g = random3(i + vec3(0.0, 1.0, 1.0));
    float h = random3(i + vec3(1.0, 1.0, 1.0));

    vec3 u = fr * fr * (3.0 - 2.0 * fr);

    float bf = mix(a,b,u.x);
    float bb = mix(c,d,u.x);

    float bot = mix(bf,bb,u.y);

    float tf = mix(e,f,u.x);
    float tb = mix(g,h,u.x);

    float top = mix(tf,tb,u.y); 

    return mix(bot,top,u.z);
}

#define NUM_OCTAVES 6

float fbm3 ( in vec3 _st) {
    float v = 0.0;
    float a = 0.5;
    vec3 shift = vec3(100.0);
    float offset = 0.;
    for (int i = 0; i < NUM_OCTAVES; ++i) {
        //v += a * sin((_st.y + _st.x+ _st.y)*10.)*1.5;
        offset += a*2.5/1.5;
        v += a * noise3(_st)*2.5;
        _st = _st * 2. + shift;
        a *= 0.5;
    }
    return v/offset;
}

float fbmN(vec3 _st, int n){
	float v = 0.;
    for (int i = 0;i<4;i++){
        if(i>=n) break;
        v= fbm3(_st + v*4.);
    }

    return v;
}

float map(vec3 pos){
    float circle = length(pos) - 1.;
    float plane = pos.y;
    //plane += fbmN(vec3(pos.xz,0.),2)/1.;
    plane += fbm3(vec3(pos.xz,0.))/1.;
    //plane += noise3(vec3(pos.xz,0.))/1.;
    //plane += random3(vec3(pos.xz,0.))*10.;
    float d= plane;
    return d*0.5;   
}

//int MAX_STEPS = 100;
//float MAX_DIST = 10.;
//float SURF_DIST = 0.05;

#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .001

float rayMarch(vec3 ro, vec3 rd){
    float d = 0.;

    for(int i = 0;i<MAX_STEPS; i++){
        vec3 p = ro + rd*d;
        float dS = map(p);
        d += dS;
        if(d>MAX_DIST || abs(dS)<SURF_DIST) break;
    }

    return d;
}

vec3 GetNormal(vec3 p) {
	float d = map(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        map(p-e.xyy),
        map(p-e.yxy),
        map(p-e.yyx));

    return normalize(n);
}

float GetLight(vec3 p,vec3 lightPos) {
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l)*.5+.5, 0., 1.);
    float d = rayMarch(p+n*SURF_DIST*2., l);
   // if(p.y<.01 && d<length(lightPos-p)) dif *= .5;

    return dif;
}

vec3 R(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = p+f*z ,
        i = c + uv.x*r + uv.y*u ,
        d = normalize(i-p);
    return d;
}

