    vec3 ro = vec3(0, 4, -5);
    ro.yz *= Rot(-m.y*3.14+1.);
    ro.xz *= Rot(-m.x*6.2831);
    ro += offset;

    vec3 rd = R(uv, ro, vec3(0,1,0)+ offset, 1.);

    vec3 col = vec3(0.0);
    float t = 0.0;

    float d = rayMarch(ro,rd);

    if(d<MAX_DIST){
        vec3 p = ro + rd*d;
        float dif = GetLight(p,vec3(3, 5, 4));
