float hash(vec2 n) { 
	return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

// 2d perlin noise
float noise2d(in vec2 x) {
	vec2 i = floor(x);
    vec2 f = fract(x);
	f = f * f * (3. - 2. * f);

	float res = mix(
		mix(hash(i), hash(i + vec2(1., .0)), f.x),        // <----------
		mix(hash(i + vec2(.0, 1.)), hash(i + vec2(1., 1.)), f.x), f.y);

	return res * res;
}

// 3d perlin noise - from IQ's shader
float noise3d_1(in vec3 x) {
    vec3 p = floor(x);
    vec3 f = fract(x);
	f = f * f * (3. - 2. * f);
	vec2 uv = (p.xy + vec2(37., 17.) * p.z) + f.xy;        // <----------
	vec2 rg = textureLod(iChannel0, (uv + .5) / 256., 0.).yx;
	return mix(rg.x, rg.y, f.z);
}

// 3d perlin noise - from IQ's shader too
float noise3d_2(in vec3 x) {
    vec3 p = floor(x);
    vec3 f = fract(x);
	f = f*f*(3.0-2.0*f);

#if 1
	vec2 uv = (p.xy + vec2(37., 239.) * p.z) + f.xy;        // <----------
    vec2 rg = textureLod(iChannel0, (uv + .5) / 256., 0.).yx;
#else
    ivec3 q = ivec3(p);
	ivec2 uv = q.xy + ivec2(37, 239) * q.z;

	vec2 rg = mix(mix(texelFetch(iChannel0,(uv           )&255,0),
				      texelFetch(iChannel0,(uv+ivec2(1,0))&255,0),f.x),
				  mix(texelFetch(iChannel0,(uv+ivec2(0,1))&255,0),
				      texelFetch(iChannel0,(uv+ivec2(1,1))&255,0),f.x),f.y).yx;
#endif    
	return -1.0+2.0*mix( rg.x, rg.y, f.z );
}


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (2. * fragCoord - iResolution.xy) / min(iResolution.x, iResolution.y);

    vec3 col = vec3(noise2d(uv * 4. + iTime));

    fragColor = vec4(col, 1.);
}

// Black Lines are the main tiling
// White lines are the dual 

// Honestly, the code isn't a very tidy read, so you might want to look at some other resources
// if you'd like to do this yourself.

// Here's some other takes on Wythoff tiling:
// by fizzer: https://www.shadertoy.com/view/3tyXWw
// by mla: https://www.shadertoy.com/view/ttGSRy


#define rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define pal(a,b,c,d,e) ((a) + (b)*sin((c)*(d) + (e)))
#define pi acos(-1.)
#define tau (2.*pi)

float id = 0.;

vec2 refl(vec2 p, vec2 reflectionPlane, float offs){
    float dotReflectionPlane = dot(p + reflectionPlane*offs,reflectionPlane);
    dotReflectionPlane = max(abs(dotReflectionPlane),0.)*sign(dotReflectionPlane);
    p -= min(dotReflectionPlane,0.)*2.*reflectionPlane;
    id+= float(dotReflectionPlane<0.)*4. + float(dotReflectionPlane<0.)*dotReflectionPlane*.84;
    return p;
}

vec3 get(vec2 fragCoord){
    vec3 col = vec3(0);
    id = 0.;

    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    uv *= 3. + sin(iTime*0.4)*0.2;
    float cntSides = 3.;

    if(iMouse.x/iResolution.x<0.33){
        cntSides = 3.;
    } else if(iMouse.x/iResolution.x<0.66){
        cntSides = 4.;
    } else {
        cntSides = 6.;
    } 

    float cntIters = 20.;

    vec2 p = uv;


    float radiusInscribedCircle = 0.4;

    for(float iter = 0.; iter < cntIters; iter++){
        vec2 sidePlane = vec2(1.,0.);

        for(float side = 0.; side < cntSides; side++){
            sidePlane *= rot(tau/cntSides);

            p = refl( p,sidePlane,radiusInscribedCircle);

        }
        vec2 reflectionPlane = vec2(1.,0.);
        for(float side = 0.; side < cntSides; side++){


            p = refl( p, reflectionPlane, 0.);


            reflectionPlane *= rot(pi/cntSides);

        }

    }


    float hexCircumCircleRadius = radiusInscribedCircle/(sqrt(3.)/2.);
    float triangleRadius = radiusInscribedCircle*1.44;

    col += pal(0.5,vec3(0.5,.3,0.1),vec3(1.5,2.6,4.4),1.,id + iTime );

    float d = length(p.y - radiusInscribedCircle * (
                + 1.*float(cntSides == 4.)
                + triangleRadius*float(cntSides == 3.)
                ) 
            );


    vec2 dualness = mix(vec2(0),
        0.7 + 0.1*vec2(sin(iTime), cos(iTime)),
        0. + iMouse.y/iResolution.y
    );

    if (cntSides == 6.){
        d = length((p*rot(pi/6.)).y - hexCircumCircleRadius*0.85);                
    }

    // Positioning duals
    if(cntSides == 3.){
        // some number crunching here
        p.x += 0.175*triangleRadius;
        p = refl( p, -vec2(0,1.), hexCircumCircleRadius*0.5 );
        p.x -= 0.175*triangleRadius;


        p.y -= dualness.y*0.5*hexCircumCircleRadius;

        p.x -= 0.5*dualness.x*0.7*triangleRadius;

        //p.x -= 0.01
        //p.x -= 0.7*dualness.x*0.66*triangleRadius;
        //p.y += -0.5*dualness.y*0.45*triangleRadius;


    } else if(cntSides == 6.){    
        p.y -= 1.5*dualness.y*0.6*hexCircumCircleRadius;
        p.x -= 1.5*dualness.x*0.15*radiusInscribedCircle;

        p *= rot(0.666*pi*float(cntSides==6.));  // the number of the beast .-.

    }  else if(cntSides == 4.) {
        p.x -= dualness.x*0.4*radiusInscribedCircle;
        p.y -= dualness.y*0.9*radiusInscribedCircle;
    }


    float dDual = max(length(p.x), - p.y );
    dDual = min( dDual, 
            max( length(p.y), p.x)
        );

    p *= rot(0.325*pi*float(cntSides==3. || cntSides==6.));
    p *= rot(0.25*pi*float(cntSides==4.));


    dDual = min( dDual, 
            max( length(p.y), -p.x)
        );
    if (cntSides == 6.){
        //dDual = d + 0.*(d = dDual); // oh my, not the prettiest line of code, haha.  
    }


    d = min(d, length(p) - 0.03);

    d -= 0.01;
    dDual -= 0.007;

    col = mix(col,vec3(1.), smoothstep(dFdx(uv.x), 0., dDual));
    col = mix(col,vec3(0.), smoothstep(dFdx(uv.x), 0., d));


    col = max(col,0.);
    return col;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(0);


    float aa = 3.;

    for(float i =0.; i < aa*aa + min(float(iFrame),0.)   ; i++){
    	col += get(fragCoord + 0.66*vec2(mod(i,aa),floor(i/aa))/aa);
    }
    col /= aa*aa;    

    //col = mix(col, smoothstep(0.,1.,col*vec3(1.6,1.2,1.4)),0.5);

    col = pow(col,vec3(0.8545));
    fragColor = vec4(col,1.0);
}

#define t (iTime+1324.)

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = (gl_FragCoord.xy-.5*iResolution.xy) / iResolution.y;

	float b = 0.0;
	float size = 0.1;
	float blur = 0.02;

	for(float i = 5.0; i < 50.0; i++){
		size += (i/10000.0);
		b +=  smoothstep(size, size-blur, length(uv - (vec2(sin(t/5./+i+5./sin(i)), cos(t/5./(4.-i))/2.))));
	}

	fragColor = vec4( b/4.,b/6.,b/2., 1.0 );
}

// Dave Hoskins hash w/o sine: www.shadertoy.com/view/4djSRW
vec3 hash32(vec2 p){
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy+p3.yzz)*p3.zyx);
}

float hash12(vec2 p){
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

float hash11(float p){
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}

vec3 hash31(float p){
   vec3 p3 = fract(vec3(p) * vec3(.1031, .1030, .0973));
   p3 += dot(p3, p3.yzx+33.33);
   return fract((p3.xxy+p3.yzz)*p3.zyx); 
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float n = 12.;
    float t = iTime*0.0001;

    vec2 uv = fragCoord/iResolution.xy;

    // vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    float c = 0.;
  // vec3 c = vec3(0.);

    for(float i = 0.; i < 9.; i++){

        float cx = mix( hash11(3.*i+floor(uv.x*n)+t), hash11(3.*i+floor(1.+(uv.x*n))+t), fract(uv.x*n) );
        float cy = mix( hash11(3.*i+floor(uv.y*n)+t), hash11(3.*i+floor(1.+(uv.y*n))+t), fract(uv.y*n) );

      //  vec3 cx = mix( hash31(3.*i+floor(uv.x*n)+t), hash31(3.*i+floor(1.+(uv.x*n))+t), fract(uv.x*n) );
      //  vec3 cy = mix( hash31(3.*i+floor(uv.y*n)+t), hash31(3.*i+floor(1.+(uv.y*n))+t), fract(uv.y*n) );

        c += sin(4.*(iTime+cx+cy))*0.5+0.5;
        c *= 0.33;
    }

    fragColor = vec4( vec3(c, cos(2.+c)*0.4+0.3, cos(2.+c)*0.3+0.3) ,1.0);
    fragColor.xyz *= 4.*fragColor.xyz;
}

#define sat(x) clamp(x, 0.0, 1.0)
const float PI = 3.1415926;

float RemapSinple(float t1, float t2, float x)
{
    return sat((x-t1)/ (t2-t1));
}

float Remap(float t1, float t2, float s1, float s2, float x)
{
    return sat((x-t1)/ (t2-t1)* (s2-s1)+ s1);
}

vec2 RectangleboxRemap(vec2 uv, vec4 rect)
{
    //x=left boundary, y=top boundary, z=x scale, w=y scale
    return (uv-rect.xy)/ (rect.zw-rect.xy);
}


vec4 Head(vec2 uv, float t)
{
    //Base Color
    vec4 col = vec4(mix(vec3(0.9333, 0.6431, 0.2118), vec3(0.9529, 0.0667, 0.0667), t), 1.0);
    float d = length(uv);
    col.a = smoothstep(0.5, 0.49, d);

    //Edge Darken
    float edgeval = RemapSinple(0.35, 0.5, d);
    edgeval *= edgeval;
    col.rgb *= 1.0- edgeval* 0.5;

    //Outline
    col.rgb = mix(col.rgb, mix(vec3(1.0, 0.6, 0.0), vec3(0.9, 0.0667, 0.0667), t), smoothstep(0.48, 0.5, d));

    //Highlight
    float highlight = smoothstep(0.4, 0.39, d);
    highlight *= Remap(0.4, -0.1, 0.75, 0.0, uv.y);
    highlight *= smoothstep(0.18, 0.19, length((uv-vec2(0.21, 0.1))));
    col.rgb = mix(col.rgb, vec3(1.0), highlight);

    //Cheek
    d = length(uv- vec2(0.25, -0.2));
    float cheek = smoothstep(0.2, 0.01, d);
    col.rgb = mix(col.rgb, mix(vec3(0.9216, 0.3451, 0.6627), vec3(0.9529, 0.4667, 0.0667), t), cheek);

    return col;    
}

vec4 Eye(vec2 uv, float side, vec2 m, float t)
{
    uv -= 0.5;
    uv.x *= side;

    //Base Color
    vec4 col = vec4(1.0);
    float d = length(uv);
    col.a *= smoothstep(0.5, 0.48, d);

    //Eye White
    vec3 eyewhite = vec3(0.1647, 0.6039, 0.6588);
    col.rgb = mix(col.rgb, eyewhite, smoothstep(0.1, 0.7, d));
    col.rgb *= 1.0- smoothstep(0.45, 0.5, d)* 0.5* sat(-uv.y-uv.x*side);        

    //Pupil
    d = length(uv-m*0.45);
    col.rgb = mix(col.rgb, vec3(0.0), smoothstep(0.3, 0.28, d));
    eyewhite *= 1.0+ smoothstep(0.28, 0.02, d);
    float eyemask = smoothstep(0.28, 0.25, d);
    col.rgb = mix(col.rgb, eyewhite, eyemask);

    d = length(uv-m*0.6);
    float pupilsize = mix(0.4, 0.16, t);
    col.rgb = mix(col.rgb, vec3(0.0), smoothstep(pupilsize, pupilsize*0.85, d)* eyemask);

    //Highlight
    float t2 = iTime* 3.0;
    vec2 offset = vec2(sin(uv.x* 25.0+ t2), sin(uv.y* 25.0+ t2));
    offset *= 0.01*(1.0-t);
    uv += offset;

    float highlight = smoothstep(0.1, 0.09, length((uv-vec2(-0.15, 0.15))));
    highlight += smoothstep(0.07, 0.06, length((uv+vec2(-0.08, 0.08))));
    col.rgb = mix(col.rgb, vec3(1.0), highlight);

    return col;
}

vec4 Mouth(vec2 uv, float t)
{
    uv -= 0.5;

    //Base Color
    vec4 col = vec4(0.502, 0.2196, 0.149, 1.0);
    float d = length(uv);

    //Teeth Shape Origin
    vec2 toptuv = uv;   
    toptuv.x *= mix(3.0, 1.0, t);
    toptuv.y += abs(toptuv.x)* 0.5* (1.0-t);
    float topteeth = length(toptuv- vec2(0.0, mix(0.6, 0.4, t)));
    float buttomteeth = length(uv+ vec2(0.0, mix(1.0 ,0.6, t)));


    //Mouth Shape and Color
    uv.y *= 1.5;
    uv.y += uv.x* uv.x* 2.5* t;
    uv.x *= mix(2.5, 1.0, t);
    d = length(uv);    
    col.a *= smoothstep(0.5, 0.48, d);

    //Tongue Shape
    float tongue = length(uv+ vec2(0.0, mix(0.5, 1.0, t)));

    //Teeth and Tongue Color
    vec3 teethcol = vec3(1.0) * smoothstep(0.6, 0.35, d);
    col.rgb = mix(col.rgb, vec3(0.9255, 0.549, 0.8118), smoothstep(0.5, 0.2, tongue));
    col.rgb = mix(col.rgb, teethcol, smoothstep(0.5, 0.48, topteeth));
    col.rgb = mix(col.rgb, teethcol, smoothstep(0.5, 0.48, buttomteeth));


    return col;
}

vec4 Brow(vec2 uv, float t)
{
    //Warp the Shape
    uv.y += uv.x* mix(0.5, -0.8, t)+ 0.03;
    uv.x += mix(-0.05, 0.15, t);
    uv -= 0.5;

    //Brow Shape
    vec4 col = vec4(0.0);
    float d1 = length(uv);
    float s1 = smoothstep(0.4, 0.3, d1);
    float d2 = length(uv+ vec2(0.1, mix(-0.2, 0.2, t))* 0.7);
    float s2 = smoothstep(0.4, 0.3, d2);
    float browmask = sat(s2-s1);

    //Brow Color
    float browhighlight = smoothstep(0.45, mix(0.0, 0.35, t), length(uv));
    vec4 browcol = mix(vec4(0.0), vec4(0.5137, 0.2078, 0.0039, 1.0), browmask);
    browcol.rgb = mix(browcol.rgb, vec3(1.0, 0.0667, 0.0), browhighlight);

    //Brow Shadow
    float shadowblur = mix(0.3, 0.1, t);
    uv.y += mix(0.15, 0.0, t);
    d1 = length(uv);
    s1 = smoothstep(0.4, 0.4-shadowblur, d1);
    d2 = length(uv+ vec2(0.1, mix(-0.2, 0.2, t))* 0.7);
    s2 = smoothstep(0.4, 0.4-shadowblur, d2);    
    float shadowmask = sat(s2-s1);
    col = mix(vec4(0.0), vec4(vec3(0.0), 1.0), smoothstep(0.0, 1.0, shadowmask)* 0.5);

    col = mix(col, browcol, smoothstep(0.45, 0.65, browmask));

    return col;
}

vec4 Face(vec2 uv, vec2 m, float t)
{
    vec4 col = vec4(0.0);
    float side = sign(uv.x);
    uv.x = abs(uv.x);

    vec4 head = Head(uv, t);
    vec4 eye = Eye(RectangleboxRemap(uv, vec4(0.03, -0.1, 0.4, 0.25)), side, m, t);
    vec4 mouth = Mouth(RectangleboxRemap(uv, vec4(-0.3, -0.4, 0.3, -0.1)), t);
    vec4 brow = Brow(RectangleboxRemap(uv, vec4(0.03, 0.2, 0.4, 0.45)), t);
    col = mix(col, head, head.a);
    col = mix(col, eye, eye.a);
    col = mix(col, mouth, mouth.a);
    col = mix(col, brow, brow.a);

    return col;

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv -= 0.5;
    uv.x *= iResolution.x/ iResolution.y;

    //Time -> [0.0, 1.0]
    float t = sin(iTime- PI/2.0)* 0.5+ 0.5;

    //Normalize Mouse
    vec2 m = iMouse.xy/ iResolution.xy;
    m -= 0.5;

    //Animate the shader when the mouse hasn't been used
    if (m.x < -0.49 && m.y < -0.49)
    {
        m = vec2(t-0.5, 0.0);           
    }

    //back to origin when exit fullscreen
    if (length(m) > 0.707) m*= 0.0;

    uv += m* (dot(uv, uv)- 0.25);

    // Output to screen
    vec4 col = Face(uv, m, t);
    fragColor = vec4(col);
}

// 3D gradient noise from iq's https://www.shadertoy.com/view/Xsl3Dl
vec3 hash( vec3 p ) // replace this by something better
{
	p = vec3( dot(p,vec3(127.1,311.7, 74.7)),
			  dot(p,vec3(269.5,183.3,246.1)),
			  dot(p,vec3(113.5,271.9,124.6)));

	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}
float noise( in vec3 p )
{
    vec3 i = floor( p );
    vec3 f = fract( p );

	vec3 u = f*f*(3.0-2.0*f);

    return mix( mix( mix( dot( hash( i + vec3(0.0,0.0,0.0) ), f - vec3(0.0,0.0,0.0) ), 
                          dot( hash( i + vec3(1.0,0.0,0.0) ), f - vec3(1.0,0.0,0.0) ), u.x),
                     mix( dot( hash( i + vec3(0.0,1.0,0.0) ), f - vec3(0.0,1.0,0.0) ), 
                          dot( hash( i + vec3(1.0,1.0,0.0) ), f - vec3(1.0,1.0,0.0) ), u.x), u.y),
                mix( mix( dot( hash( i + vec3(0.0,0.0,1.0) ), f - vec3(0.0,0.0,1.0) ), 
                          dot( hash( i + vec3(1.0,0.0,1.0) ), f - vec3(1.0,0.0,1.0) ), u.x),
                     mix( dot( hash( i + vec3(0.0,1.0,1.0) ), f - vec3(0.0,1.0,1.0) ), 
                          dot( hash( i + vec3(1.0,1.0,1.0) ), f - vec3(1.0,1.0,1.0) ), u.x), u.y), u.z );
}

// Metaballs and analytic normals from Klems' https://www.shadertoy.com/view/4dj3zV
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 a, q, p, gradient, dir;
    float b, dist;
    dir = normalize(vec3((2.*fragCoord.xy-iResolution.xy)/min(iResolution.x,iResolution.y), 1.7));
    p = vec3(0, 0, -7);
    for(int i = 0; i < 100; i++) {
        q = p; // save current position
        p += dir * dist; // step
        gradient = vec3(0);
        dist = 0.;
        for(float j = 0.; j < 8.; j++) {
            vec3 ballp = sin(vec3(1,2,4) * j + iTime * .3) * 3.; // ball position
            b = dot(a = p - ballp, a);
            gradient += a / (b * b);
            dist += 1. / b;
        }
        dist = 1. - dist;
        if(dist < .001) { // if we've hit the metaballs
            dir = reflect(dir, normalize(gradient)); // set new reflected marching direction
            p = q; // restore previous position
            dist = 0.; // and don't step in this iteration
        }
     }
    vec3 col = dir + 1.;
    fragColor.rgb = noise(col * 2. + iTime * .3) * col * 2.;
}

#define Rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define antialiasing(n) n/min(iResolution.y,iResolution.x)
#define S(d,b) smoothstep(antialiasing(1.0),b,d)
#define BASE_COLOR vec3(0.3,0.6,0.3)

vec2 bend(vec2 p, float k){
    float c = cos(k*p.y);
    float s = sin(k*p.y);
    mat2  m = mat2(c,-s,s,c);
    vec2  q = p*m;
    return q;
}

// https://www.iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float sdVesica(vec2 p, float r, float d)
{
    p = abs(p);
    float b = sqrt(r*r-d*d);
    return ((p.y-b)*d>p.x*b) ? length(p-vec2(0.0,b))
                             : length(p-vec2(-d,0.0))-r;
}

// https://www.iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float sdUnevenCapsule( vec2 p, float r1, float r2, float h )
{
    p.x = abs(p.x);
    float b = (r1-r2)/h;
    float a = sqrt(1.0-b*b);
    float k = dot(p,vec2(-b,a));
    if( k < 0.0 ) return length(p) - r1;
    if( k > a*h ) return length(p-vec2(0.0,h)) - r2;
    return dot(p, vec2(a,b) ) - r1;
}

float paiselyDist(vec2 p, float sy, float scale) {
    vec2  q = bend(p,1.5);

    q.y*=sy*scale;
    q*=0.8*scale;
    float d = sdUnevenCapsule(q,0.15,0.02*scale,0.35*scale);
    return d;
}

vec3 paiselyTex(vec2 p, vec3 col, float dir, float t) {
    p*=Rot(radians(t*30.0*dir));
    p*=1.2;
    vec2 prevP = p;
    p = abs(p);
    p -= vec2(0.05,0.05);
    float d = abs(sdVesica(p*Rot(radians(45.0)),0.1,0.07))-0.005;
    col = mix(col,BASE_COLOR,S(d,0.0));

    p = prevP;

    p *= Rot(radians(45.0));
    p = abs(p);
    p -= vec2(0.05,0.05);
    d = abs(sdVesica(p*Rot(radians(45.0)),0.1,0.07))-0.005;
    col = mix(col,BASE_COLOR,S(d,0.0));

    return col;
}

vec3 paisely(vec2 p, vec3 col, float t) {
    vec3 baseCol = BASE_COLOR;
    vec2 pos = vec2(0.0,-0.1);
    vec2 prevP = p;
    float d = abs(paiselyDist(p-pos,0.9,0.88))-0.002;
    float d2 = abs(paiselyDist(p-pos,0.87,1.05))-0.001;
    float d3 = abs(paiselyDist(p-pos,0.85,1.25))-0.003;
    float d4 = abs(paiselyDist(p-pos,0.9,0.82))-0.001;
    col = mix(col,baseCol,S(d,0.0));
    col = mix(col,baseCol*1.2,S(d2,0.0));
    col = mix(col,baseCol,S(d3,0.0));
    col = mix(col,baseCol,S(d4,0.0));

    p*=3.2;
    col = paiselyTex(p-vec2(0.4,0.55),col,1.0,t);

    p = prevP;
    p*=1.8;
    col = paiselyTex(p-vec2(0.11,0.15),col,-1.0,t);

    p = prevP;
    col = paiselyTex(p-vec2(0.01,-0.11),col,1.0,t);

    p = prevP;
    p*=3.5;
    col = paiselyTex(p-vec2(-0.13,0.13),col,1.0,t);

    p = prevP;
    p*=2.8;
    col = paiselyTex(p-vec2(0.1,-0.82),col,-1.0,t);

    p = prevP;
    p*=3.2;
    col = paiselyTex(p-vec2(-0.2,-0.89),col,1.0,t);

    p = prevP;
    p*=3.2;
    col = paiselyTex(p-vec2(0.4,-0.78),col,1.0,t);

    p = prevP;
    p*=4.2;
    col = paiselyTex(p-vec2(1.15,1.25),col,1.0,t);

    p = prevP;

    p.x -=0.01;
    p.x = abs(p.x);
    p.x -= 0.15;
    d = length(p-vec2(0.01,0.0))-0.02;
    col = mix(col,baseCol,S(d,0.0));

    p = prevP;
    p.x = abs(p.x);
    p.x -= 0.18;
    d = length(p-vec2(0.0,-0.12))-0.02;
    col = mix(col,baseCol,S(d,0.0));

    p = prevP;
    d = length(p-vec2(-0.14,-0.22))-0.02;
    col = mix(col,baseCol,S(d,0.0));

    d = length(p-vec2(-0.072,0.11))-0.02;
    col = mix(col,baseCol,S(d,0.0));

    d = length(p-vec2(0.063,0.215))-0.013;
    col = mix(col,baseCol,S(d,0.0));

    d = length(p-vec2(0.187,0.13))-0.017;
    col = mix(col,baseCol,S(d,0.0));

    return col;
}

vec3 renderTexture(vec2 p, vec3 col, float t) {
    p*=1.3;

    vec2 prevP = p;

    p.x = mod(p.x,1.0)-0.5;
    p.y = mod(p.y,0.8)-0.4;
    col = paisely(p,col,t);
    p = prevP;

    p.y+=0.1;
    p.x+=0.475;
    p.x = mod(p.x,1.0)-0.5;
    p.y = mod(p.y,0.8)-0.4;

    p*=1.2;
    col = paisely(p*Rot(radians(-180.0)),col,t);
    p = prevP;

    p.x+=0.3;
    p.y+=-0.35;
    p.x = mod(p.x,1.0)-0.5;
    p.y = mod(p.y,0.8)-0.4;
    p*=1.8;
    col = paisely(p*Rot(radians(120.0)),col,t);
    p = prevP;

    p.x+=0.08;
    p.y+=-0.31;
    p.x = mod(p.x,1.0)-0.5;
    p.y = mod(p.y,0.8)-0.4;
    p*=1.2;
    col = paiselyTex(p,col,1.0,t);
    p = prevP;

    p.x+=0.28;
    p.y+=-0.57;
    p.x = mod(p.x,1.0)-0.5;
    p.y = mod(p.y,0.8)-0.4;
    p*=1.8;
    col = paiselyTex(p,col,-1.0,t);
    p = prevP;

    p.x+=0.56;
    p.y+=-0.3;
    p.x = mod(p.x,1.0)-0.5;
    p.y = mod(p.y,0.8)-0.4;
    p*=2.1;
    col = paiselyTex(p,col,1.0,t);

    return  col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
