}

#define TRANS_TIME 2.
#define IDLE_TIME 1.  //unwanted results if negative


vec3 scene1(vec2 uv)
{
    vec3 col = vec3(uv.xy, 0.);
    return col;
}

vec3 scene2(vec2 uv)
{
    vec3 col = vec3(1.) - scene1(uv);
    return col;
}

float transSwipe(vec2 uv)
{

    return uv.x + .5;
}

float transCircle(vec2 uv)
{


    return length(uv.xy * vec2(iResolution.x/iResolution.y, 1.));
}

float transDiamonds(vec2 uv)
{
    float a = 9.;
    float b = 10.;

    return (min(fract(uv.x*a), fract(1.-uv.x*a))
            + min(fract(uv.y*b), fract(1.-uv.y*b))
            * 0.5);
}


bool execTrans(float transition, float time)
{
    return transition > time;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float myTime = max(0., mod(iTime, TRANS_TIME + IDLE_TIME) - IDLE_TIME) / TRANS_TIME;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy - vec2(0.5);

    // To visualize transition function
    //fragColor = vec4(vec3(transDiamonds(uv)), 1.); return;


    bool chooseScene = execTrans(transCircle(uv), myTime);

    chooseScene = mod((ceil(iTime/(TRANS_TIME+IDLE_TIME))-1.), 2.) == 1. ? !chooseScene : chooseScene;

    vec3 col = chooseScene ? scene1(uv) : scene2(uv);

    // To visualize transition animation
    //col = chooseScene ? vec3(1.) : vec3(0.);

    fragColor = vec4(col, 1.);
}

