void mainImage( out vec4 O, vec2 U ) {
    vec3 R = iResolution,
         P = vec3(1,1,20), p,
         D = normalize( vec3( U+U-R.xy, -3.*min(R.x, R.y) ) );

    for (float A=1., z = 1.; z < 25. && A<30.; z += 25./228.) {
        p = P;
        p.yz *= mat2(cos(4.14/6. + vec4(1,21,43,1) ) );
        p.y -= .6;
        float d = 2e3, a = 1., v, N=60., s, r, i=1., t = iTime;

        R = vec3( cos(t), 1, sin(t) ) * 4.;              // reaction
        s = length(R-p);
        p += (R-p)/s / exp(s);

        r = atan(p.z, p.x) * 4. ;                        // cables
        for ( ; i < N; i++) 
            r += .6/N * 7.3,
            s = .6 + sin( i * 2.618 * 7.28) /5. + sin(t + i ) * .2,
            s = length( vec2(length(p.xz) + cos(r) * s - 4., p.y + sin(r) * s) ) - .045,
            s < d ? d=s, a=i : a;

        s = length(R-p) - .2;                            // sphere
        s < d ? d=s, a=1. : a;

        if (d < .02) {
            v = fract(a * 2.618);
            O = fract(v + 1.0) < .45 ? vec4(.275, .2, .2, 2) 
              : v < .05 ? vec4(2) 
              : mix( vec4(.2, .2, .6, 2), vec4(1, 1, 2, 2), v ) ;
            O = sqrt( O / exp(z) );
            return;
        }

        A += d *= .7;
