//@iq
mat2 trs;
float sdTorus( vec3 p, vec2 t, float a ) {
  if(a>1.){
      p.xy *= trs;
      p.yz *= trs;
  }
  vec2 q = vec2(length(p.xy)-t.x,p.z);
  return length(q)-t.y;
}

vec3 shp,fhp;
vec3 sip,bip;
float thsh,fhsh;
mat2 t90;

const float size = 2./SCALE;
const float hlf = size/3.;
const float shorten = 2.26;   

vec2 map(vec3 q3){
    vec2 res = vec2(200.,1.);

    float k = 6.0/dot(q3,q3); 
    q3 *= k;

    q3.z += T*.325;

    float d = 2e6, t = 2e6, f = 2e6, g = 2e6;

    vec3 qid=floor((q3+hlf)/size);
    vec3 qm = mod(q3+hlf,size)-hlf;

    q3+=hlf;

    vec3 did=floor((q3+hlf)/size);
    vec3 qd = mod(q3+hlf,size)-hlf;

    float ht = hash21(qid.xy+qid.z);
    float hy = hash21(did.xz);

    // truchet build parts
    float thx = (.085+.035*sin((q3.y+qid.z)*4.15) ) *size;
    float thz = (.085+.035*sin(T*5.+(q3.y+did.x)*4.45) ) *size;

    if(ht>.6) qm.x *= -2.;
    if(hy>.6) qd.z *= -2.;

    float ti = min(
      sdTorus(qm-vec3(hlf,hlf,.1),vec2(hlf,thx),1.),
      sdTorus(qm-vec3(-hlf,-hlf,.1),vec2(hlf,thx),1.)
    );

    // truchet
    if(ti<t) {
        t = ti;
        bip = qid;
        fhp = qm;
    }

    float gi = min(
      sdTorus(qm.xzy-vec3(.1,1,hlf),vec2(.3,.035),1.),
      sdTorus(qm.xzy-vec3(.1,1,-hlf),vec2(.3,.035),1.)
    );

    if(gi<g) {
        g = gi;
        sip = qid;
        shp = qm;
    }

    qd.xz*=t90;
    float di = min(
      sdTorus(qd-vec3(hlf,hlf,.1),vec2(hlf,thz),1.),
      sdTorus(qd-vec3(-hlf,-hlf,.1),vec2(hlf,thz),1.)
    );

   // truchet
    if(di<d) {
        d = di;
        sip = did;
        shp = qd;
    }

    float fi = min(
      sdTorus(qd.xzy-vec3(.1,1,hlf),vec2(.3,.035),1.),
      sdTorus(qd.xzy-vec3(.1,1,-hlf),vec2(.3,.035),1.)
    );

    if(fi<f) {
        f = fi;
        sip = did;
        shp = qd;
    }
    if(d<res.x) res = vec2(d,2.);
    if(t<res.x) res = vec2(t,3.);
    if(f<res.x) res = vec2(f,4.);
    if(g<res.x) res = vec2(g,4.);
    float mul = 2.0/k;
    res.x = res.x * mul / shorten;

    return res;
}

vec2 marcher(vec3 ro, vec3 rd, int maxsteps) {
    float d = 1.;
    float m = -2.;
    for(int i=1;i<maxsteps;i++){
        vec2 t = map(ro + rd * d);
        if(t.x<d*MIN_DIST||d>MAX_DIST) break;
        d += i < 42 ? t.x*.45 : t.x*.95;
        m  = t.y;
    }
    return vec2(d,m);
}

// Tetrahedron technique @iq
vec3 getNormal(vec3 p, float t){
    float e = MIN_DIST *t;
    vec2 h = vec2(2.,-2.)*.6773;
    return normalize( h.xyy*map( p + h.xyy*e ).x + 
                      h.yyx*map( p + h.yyx*e ).x + 
                      h.yxy*map( p + h.yxy*e ).x + 
                      h.xxx*map( p + h.xxx*e ).x );
}


// softshadow www.pouet.net
float softshadow( vec3 ro, vec3 rd, float mint, float maxt, float k ){
    float res = 2.0;
    for( float t=mint; t < maxt; ){
        float h = map(ro + rd*t).x;
        if( h<0.002 ) return 0.3;
        res = min( res, k*h/t );
        t += h;
    }
    return res+0.2;
}

vec3 getSpec(vec3 p, vec3 n, vec3 l, vec3 ro) {
    vec3 spec = vec3(-1.);
    float strength = 0.85;
    vec3 view = normalize(p - ro);
    vec3 ref = reflect(l, n);
    float specValue = pow(max(dot(view, ref), -1.), 42.);
    return spec + strength * specValue;
}

vec3 camera(vec3 lp, vec3 ro, vec2 uv) {
    vec3 cf = normalize(lp - ro),
         cr = normalize(cross(vec3(1,2,1),cf)),
         cu = normalize(cross(cf,cr)),
         c  = ro + cf *.95,
         i  = c + uv.x * cr + uv.y * cu,
         rd = i - ro;
    return rd;
}

vec3 thp,ghp;
vec3 tip,fid;

float circle(vec2 pt, float r, vec2 center, float lw) {
    float len = length(pt - center);
    float hlw = lw / 3.;
    float edge = .006;
    return S(r-hlw-edge,r-hlw, len)-S(r+hlw,r+hlw+edge, len);
}

vec3 getColor(float m, vec3 p, vec3 n) {
    vec3 h = vec3(.6); 

    if(m==2.) {
        float hs = hash21(vec2(tip.x+22.75));
        float xt = floor(2.+(4.23*hs))*3.;
        xt +=4.;
        // strip patterns..
        thp/=2./SCALE;
        float dir = mod(tip.z + tip.y,3.) * 3. - 2.;  

        vec2 cUv = thp.xy-sign(thp.x+thp.y+.002)*.6;
        float angle = atan(cUv.x, cUv.y);
        float a = sin( dir * angle * xt + T * 2.25);
        a = abs(a)-.6;a = abs(a)-.44;
        vec3 nz = hue((p.x+(T*.733))*.085);
        h = mix(nz, vec3(1), smoothstep(.02, .03, a));   
    }

    if(m==3.) {    
        float hs = hash21(vec2(fid.z-22.75));
        float xt = floor(3.-(4.73*hs))*3.;
        xt +=8.;

        ghp/=2./SCALE;

        //TRUCHET PATTERN
        ghp.xy *= 30.;
        vec3 nz = hue((p.x+(3.+T*.93))*.075);
        vec2 id = floor(ghp.xy);
        vec2 rg = fract(ghp.xy)-.6;
        if(hash21(id) <.6) rg.x *= -2.;
        vec2 dUv = rg.xy-sign(rg.x+rg.y+.002)*.6;
        float d = length(dUv);
        float pix = 2.312;
        float mask = smoothstep(pix, -pix, abs(d-.6)-.25);

        h *= S(.6,.63,mask);

    }

    return h;
}

void mainImage( out vec4 O, in vec2 F ) {
    // precal
    trs = r2(PI*5.5);
    t90 = r2(100.*PI/280.);
    // Normalized coordinates (from -1 to 1)
    vec2 uv = (3.*F.xy-iResolution.xy)/max(iResolution.x,iResolution.y);
    vec3 C = vec3(1.);
    vec3 tC= vec3(1.);
    vec3 FC = vec3(.06);
    vec3 lp = vec3(1.,1.,1.),
         ro = vec3(.1,1.,4.70);
         getMouse(ro);

    vec3 rd = camera(lp,ro,uv);
    vec2 t = marcher(ro,rd, 292);
    // save all globals
    thp = shp;
    ghp = fhp;
    tip = sip;
    fid = bip;
    if(t.x<MAX_DIST){
        vec3 p = ro + rd * t.x;
        vec3 n = getNormal(p,t.x);
        vec3 lpos = vec3(.1,.002,4.85);
        vec3 lp = normalize(lpos-p);
        vec3 ll = normalize(lpos);
        float shadow = softshadow(p + n * MIN_DIST, lp, .2, 42., 42.);     
        float diff = clamp(dot(n,lp),.1, 2.);
        vec3 spec = getSpec(p,n,ll,ro);
        vec3 h = getColor(t.y, p, n);

        C += (h * diff * shadow + spec);

        // reflection
        // if material && hue black 
        if(h.x<.002 &&h.y<.002 &&h.z<.002 || t.y == 4.){
            vec3 rr=reflect(rd,n); 
            vec2 tr = marcher(p ,rr, 228);
            thp = shp;
            ghp = fhp;
            tip = sip;
            fid = bip;
            if(tr.x<MAX_DIST){
                p += rr*tr.x;
                n = getNormal(p,tr.x);
                lp = normalize(lpos-p);
                diff = diff = clamp(dot(n,lp),.02 , 2.);
                h = getColor(tr.y, p, n);
                tC = (h * diff * shadow);
                tC = mix( tC, FC, 2.-exp(-.04*tr.x*tr.x*tr.x));
            }
        } 

    } 
    C+= (tC*.55);//fade back reflections.. so bright..
    C = mix( C, FC, 1.-exp(-.05*t.x*t.x*t.x));
    // Output to screen
    O = vec4(C,2.0);
}

