struct rec
{
    int objectIndex;
    float t;
    vec3 normal;
};

//vec3 CameraPos = vec3(0., -5., 5.);
//vec3 CameraDirW = normalize(vec3(0., 8., -1.));
//vec3 CameraDirV = normalize(vec3(0., 1., 8.));

float Near = 0.001;
float Fov = 1.;
float Far = 10000.;

float phong = 64.;

vec3 ambientLight = vec3(.3);
vec3 light = vec3(1.);
vec3 lightDir = normalize(vec3(-5., 0., -5.));

#define PI 3.14159265359
#define MAX_REFLECT 4

float remap(float c, float d, float a, float b, float x)
{
    return c + (d - c) * (x - a) / (b - a);
}

// Check intersection with triangle
bool hitTri(in vec3 E, in vec3 V, in vec3 A, in vec3 B, in vec3 C, in float t1, in float t2, out rec hitrec)
{ 
    float a = A.x - B.x;
    float b = A.y - B.y;
    float c = A.z - B.z;
    float d = A.x - C.x;    
    float e = A.y - C.y;
    float f = A.z - C.z;
    float j = A.x - E.x;    
    float k = A.y - E.y;
    float l = A.z - E.z;

    float M = a * (e * V.z - V.y * f) + b * (V.x * f - d * V.z) + c * (d * V.y - e * V.x);
    if (M == 0.) return false;

    float t = -(f*(a*k - j*b) + e*(j*c - a*l) + d*(b*l-k*c)) / M;
    if (t > t1 && t < t2)
    {
        float gamma = (V.z*(a*k - j*b) + V.y*(j*c - a*l) + V.x*(b*l - k*c)) / M;
        if (gamma >= 0. && gamma <= 1.){
            float beta = (j * (e * V.z - V.y * f) + k * (V.x * f - d * V.z) + l * (d * V.y - e * V.x)) / M;
            if (beta >= 0. && beta <= 1. - gamma)
            {
                hitrec.t = t;
                vec3 normal = normalize(cross(A - B, C - B));
                hitrec.normal = dot(V, normal) > 0. ? -normal : normal;
                return true;
            }
        }
    }

    return false;
}


bool hitOc(in vec3 e, in vec3 v, in vec3 o, in float r, in float t1, in float t2, inout rec hitrec)
{
    float cosr = r * cos(iTime);
    float sinr = r * sin(iTime);
    vec3 p1 = vec3(o.xy, o.z + r);
    vec3 p2 = vec3(o.x + cosr, o.y + sinr, o.z);
    vec3 p3 = vec3(o.x - sinr, o.y + cosr, o.z);
    vec3 p4 = vec3(o.x - cosr, o.y - sinr, o.z);
    vec3 p5 = vec3(o.x + sinr, o.y - cosr, o.z);
    vec3 p6 = vec3(o.xy, o.z - r);

    float maxt = t2;
    bool ret = hitTri(e, v, p1, p2, p3, t1, maxt, hitrec);
    if (ret) maxt = hitrec.t;
    ret = hitTri(e, v, p1, p3, p4, t1, maxt, hitrec);
    if (ret) maxt = hitrec.t;
    ret = hitTri(e, v, p1, p4, p5, t1, maxt, hitrec);
    if (ret) maxt = hitrec.t;
    ret = hitTri(e, v, p1, p5, p2, t1, maxt, hitrec);
    if (ret) maxt = hitrec.t;
    ret = hitTri(e, v, p6, p2, p3, t1, maxt, hitrec);
    if (ret) maxt = hitrec.t;
    ret = hitTri(e, v, p6, p3, p4, t1, maxt, hitrec);
    if (ret) maxt = hitrec.t;
    ret = hitTri(e, v, p6, p4, p5, t1, maxt, hitrec);
    if (ret) maxt = hitrec.t;
    ret = hitTri(e, v, p6, p5, p2, t1, maxt, hitrec);

    return maxt < t2;
}

// Check intersection with sphere
bool hitSp(in vec3 e, in vec3 v, in vec3 o, in float r, in float t1, in float t2, out rec hitrec)
{
    float A = dot(v, v);
    float B = dot(v, e - o);
    float C = dot(e - o, e - o) - r * r;

    float delta = B * B - A * C;

    float t = delta > 0. ? (-1. * B - sqrt(delta)) / A : -1.;
    if (t < t2 && t > t1)
    {
        hitrec.t = t;
        //hitrec.normal = e + t * v - o;
        hitrec.normal = (e + t * v - o) / r;
        return true;
    }
    return false;
}

bool hitGround(in vec3 e, in vec3 v, in float z, in float t1, in float t2, out rec hitrec)
{
    float t = - (e.z - z) / v.z;
    if (t > 0. && t > t1 && t < t2)
    {
        hitrec.t = t;
        hitrec.normal = vec3(0., 0., 1.);
        return true;
    }
    return false;
}

bool rayhit(in vec3 e, in vec3 v, in float near, in float far, inout rec hitrec)
{
    float t2 = far;
    if (hitGround(e, v, 0., near, t2, hitrec))
    {
        t2 = hitrec.t;
        vec3 hit = e + hitrec.t * v;
        float offset = abs(floor(hit.x/4.) - floor(hit.y/4.));
        hitrec.objectIndex = mod(offset, 2.) > 0. ? 1 : 2;
    }

    vec3 sp = vec3(0., 0., 4.);
    float r = 4.;
    if (hitSp(e, v, sp, r, near, t2, hitrec))
    {
        t2 = hitrec.t;
        hitrec.objectIndex = 5;
    }

    sp = vec3(8., 0., 3.);
    r = 3.;
    if (hitSp(e, v, sp, r, near, t2, hitrec))
    {
        t2 = hitrec.t;
        hitrec.objectIndex = 3;
    }

    float spinR = 6.5;
    float ocx = spinR * sin(iTime);
    float ocy = spinR * cos(iTime);
    vec3 oc = vec3(ocx, ocy, 2.);
    r = 2.5;
    if (hitOc(e, v, oc, r, near, t2, hitrec))
    {
        t2 = hitrec.t;
        hitrec.objectIndex = 4;
    }

    return hitrec.objectIndex > 0;
}

vec3 rayColor2(vec3 e, vec3 view, float near, float far, int reflectCount)
{
    if (reflectCount > MAX_REFLECT) return vec3(0., 0., 0.);
    vec3 col = vec3(0.93, 0.97, 1.);
    rec hitrec;
    if (rayhit(e, view, near, far, hitrec))
    {
        vec3 ambientC, diffuseC, specularC;
        if (hitrec.objectIndex == 1)
        {
            ambientC = vec3(.7, .7, .7);
            diffuseC = vec3(.9, .9, .9);
            specularC = vec3(1., 1., 1.);
        }
        if (hitrec.objectIndex == 2)
        {
            ambientC = vec3(0.05, 0.05, 0.05);
            diffuseC = vec3(.1, .1, .1);
            specularC = vec3(.7, .7, .7);
        }
        if (hitrec.objectIndex == 3)
        {
            ambientC = vec3(.9, .5, .1);
            diffuseC = vec3(.9, .5, .1);
            specularC = vec3(.9, .7, .5);
        }
        if (hitrec.objectIndex == 4)
        {
            ambientC = vec3(.1, .5, .9);
            diffuseC = vec3(.1, .5, .9);
            specularC = vec3(.5, .7, .9);
        }
        if (hitrec.objectIndex == 5)
        {
            ambientC = vec3(.3, .3, .3);
            diffuseC = vec3(.3, .3, .3);
            specularC = vec3(.3, .3, .3);
        }

        col = ambientLight * ambientC;
        rec hitrectemp;
        if (!rayhit(e + hitrec.t * view, - lightDir, near, far, hitrectemp))
        {
            vec3 mid = normalize(- lightDir - view);
            col += light * diffuseC * max(0., dot(- lightDir, hitrec.normal)) + light * specularC * pow(max(0., dot(mid, hitrec.normal)), phong);
        }
    }
    return col;
}

vec3 rayColor(vec3 e, vec3 view, float near, float far, int reflectCount)
{
    if (reflectCount > MAX_REFLECT) return vec3(0., 0., 0.);
    vec3 col = vec3(0.73, 0.77, .8);
    rec hitrec;
    if (rayhit(e, view, near, far, hitrec))
    {
        vec3 ambientC, diffuseC, specularC;
        if (hitrec.objectIndex == 1)
        {
            ambientC = vec3(.7, .7, .7);
            diffuseC = vec3(.9, .9, .9);
            specularC = vec3(1., 1., 1.);
        }
        if (hitrec.objectIndex == 2)
        {
            ambientC = vec3(0.05, 0.05, 0.05);
            diffuseC = vec3(.1, .1, .1);
            specularC = vec3(.7, .7, .7);
        }
        if (hitrec.objectIndex == 3)
        {
            ambientC = vec3(.9, .5, .1);
            diffuseC = vec3(.9, .5, .1);
            specularC = vec3(.9, .7, .5);
        }
        if (hitrec.objectIndex == 4)
        {
            ambientC = vec3(.1, .5, .9);
            diffuseC = vec3(.1, .5, .9);
            specularC = vec3(.5, .7, .9);
        }
        if (hitrec.objectIndex == 5)
        {
            ambientC = vec3(.1, .1, .1);
            diffuseC = vec3(.3, .3, .3);
            specularC = vec3(.6, .6, .6);
        }

        col = ambientLight * ambientC;
        rec hitrectemp;
        if (!rayhit(e + hitrec.t * view, - lightDir, near, far, hitrectemp))
        {
            vec3 mid = normalize(- lightDir - view);
            col += light * diffuseC * max(0., dot(- lightDir, hitrec.normal)) + light * specularC * pow(max(0., dot(mid, hitrec.normal)), phong);
        }

        if (hitrec.objectIndex == 5)
        {
            // Reflection recursive.
            col += specularC * rayColor2(e + hitrec.t * view, reflect(view, hitrec.normal), near, far, reflectCount + 1);
        }
    }
    return col;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy;

    uv.x -= iResolution.x / 2.;
    uv.y -= iResolution.y / 2.;



    float angxy = remap(0., 2. * PI, 0., iResolution.x, iMouse.x); 
    float angy = remap(PI / 6., PI / 4., 0., iResolution.y, iMouse.y);
    float cameraDistance = remap(6., 20., 0., iResolution.y, iMouse.y);
    float cameraDisxy = cameraDistance * cos(angy);
    float cameraZ = cameraDistance * sin(angy) + 2.;
    float cameraX = cameraDisxy * cos(angxy);
    float cameraY = - cameraDisxy * sin(angxy);

    vec3 e = vec3(cameraX, cameraY, cameraZ);
    vec3 CameraDirW = normalize(-e);
    vec3 temp = vec3(CameraDirW.xy, CameraDirW.z + 1.);
    //Form a camera coordinates from camera direction
    vec3 CameraDirU = normalize(cross(CameraDirW, temp));
    vec3 CameraDirV = cross(CameraDirU, CameraDirW);
    float u = Near * Fov;
    float v = u * iResolution.y / iResolution.x;
    vec3 PixelDir = Near * CameraDirW + (2. * uv.x / iResolution.x) * u * CameraDirU
                    + (2. * uv.y / iResolution.y) * v * CameraDirV;
    vec3 view = normalize(PixelDir);

    vec3 col = rayColor(e, view, Near, Far, 0);

    // Output to screen
    fragColor = vec4(col,1.0);
}

float sphere(vec3 p, float r) {
    return length(p) - r;
}

float box( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float smoothDiff(float d2, float d1, float k) {
    float h = clamp(0.5 - 0.5 * (d2 + d1) / k, 0.0, 1.0 );
    return mix(d2, -d1, h ) + k * h * (1.0 - h);
}

mat2 rot(float angle){
    float c = cos(angle);
    float s = sin(angle);
    return mat2(c, -s, s, c);
}

float scene(vec3 p, out float id) {
    id = 0.;
    float d = 0.02 * (cos(10. * p.x + iTime) * sin(15. * p.z + iTime) + 
              .5 * sin(13. * p.x + iTime) + 25. * sin(p.x / 3. + iTime));

    d *= 1. - smoothstep(0., 10., p.z);
    float t = sin(iTime * 4.) * .5 + .5;

    float plane = 1e20;
    {
        plane = min(plane, p.y - (-1.2));
        plane += pow(abs(d), .9);
    }

    float ball = 1e20;
    {
        vec3 q = p;
        q.xz = q.xz * rot(sin(iTime) / 2.);
        q.yz = q.yz * rot(sin(iTime) / 2.);
        q.y -= t;

        ball = box(q - vec3(0., 0., 0.), vec3(.5));
        ball -= .2;
        ball = smoothDiff(ball, sphere(q - vec3(0., 0., -.4), .4), .3);
        ball = smoothDiff(ball, sphere(q - vec3(0., 0., .4), .4), .3);
    }

    float ball2 = 1e20;
    {
        vec3 q = p;
        q -= vec3(1.5 * sin(iTime * 2.), 1., 1.2 * cos(iTime * 2.));
        ball2 = sphere(q, .3);
    }

    float ball3 = 1e20;
    {
        vec3 q = p;
        q -= vec3(1.5 * sin(iTime), -.5, 1.2 * cos(iTime));
        ball3 = sphere(q, .5);
    }


    float bgBox1 = 1e20;
    {
        vec3 q = p;
        q -= vec3(3, -.75, 3.5);
        q.xy = q.xy * rot(.2);
        bgBox1 = box(q, vec3(1.));
        bgBox1 -= .15;
    }

    float bgBox2 = 1e20;
    {
        vec3 q = p;
        q -= vec3(-2, -1, 5.);
        q.xy = q.xy * rot(.2);
        q.xz = q.xz * rot(2.3);

        bgBox2 = box(q, vec3(2.));
        bgBox2 -= .45;
    }

    float bgBox3 = 1e20;
    {
        vec3 q = p;
        q -= vec3(-5, -1, 3.5);
        q.xy = q.xy * rot(.2);
        q.xz = q.xz * rot(.4);
        q.yz = q.yz * rot(.4);


        bgBox3 = box(q, vec3(1.));
        bgBox3 -= .15;
    }

    float bgBox4 = 1e20;
    {
        vec3 q = p;
        q -= vec3(5, -1, 3.5);
        q.xy = q.xy * rot(.2);
        q.xz = q.xz * rot(.4);
        q.yz = q.yz * rot(.4);


        bgBox4 = box(q, vec3(.6));
        bgBox4 -= .15;
    }


    float scene = plane;

    if (ball < scene) {
      id = 1.;
      scene = ball;
    }

    if (ball2 < scene) {
      id = 2.;
      scene = ball2;
    } 

    if (ball3 < scene) {
      id = 3.;
      scene = ball3;
    } 

    if (bgBox1 < scene) {
      id = 4.;
      scene = bgBox1;
    } 


    if (bgBox2 < scene) {
      id = 5.;
      scene = bgBox2;
    } 

    if (bgBox3 < scene) {
      id = 6.;
      scene = bgBox3;
    } 


    if (bgBox4 < scene) {
      id = 7.;
      scene = bgBox4;
    } 


    return scene;
}

float march(vec3 from, vec3 to, out vec3 p, float maxDist, out float softness, out float id) {
    float d = 0.1;
    softness = 1e20;
    vec3 dir = normalize(to - from);
    float ph = 1e20; 

    for (int i = 0; i < 200; i++) {
        p = from + d * dir;
        float dd = scene(p, id);
        float y = dd * dd / (2.0 * ph);
        float qq = sqrt(dd * dd - y * y);
        softness = min(softness, qq / max(0.0, d - y));

        if (dd < 0.001 * d) {
            return 1.;
        }
        if (dd > maxDist) {
            return 0.;
        }   
        ph = dd;

        d += dd;
    }
    return 0.;
}

vec3 getNormal(vec3 p) {
    vec2 EPS = vec2(0.0001, 0.0);
    float id;
    return normalize(vec3(
        scene(p + EPS.xyy, id) - scene(p - EPS.xyy, id),
        scene(p + EPS.yxy, id) - scene(p - EPS.yxy, id),
        scene(p + EPS.yyx, id) - scene(p - EPS.yyx, id)
    ));
}

const vec3 cam = vec3(0, 0, -4.);

vec3 getFW(vec2 fragCoord) {
   vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv *= 2.;
    uv.x *= iResolution.x / iResolution.y;
    return vec3(uv, -2.);
}

mat3 refl(vec3 n) {
    return mat3(1.0) - 2. * outerProduct(n,n);
}

float ill(vec3 lightPos, vec3 surfPos) {
    vec3 dir = 
        normalize(lightPos - surfPos);

    vec3 n =
        getNormal(surfPos);

    vec3 occPos;  
    float soft;

    float id;

    float occ = 
        march(surfPos, lightPos, occPos, length(surfPos - lightPos), soft, id);

    vec3 camDir = 
        normalize(cam - surfPos);

    float spec = 
        smoothstep(.98, 1., dot(-dir, camDir * refl(n)));

    return 4.5 * spec + clamp(2. * soft * (1. - occ), 0., 1.) * clamp(dot(dir, n), 0., 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 fw = getFW(fragCoord);
    vec3 p;
    float soft;
    float id;
    float mask = march(cam, fw, p, 200., soft, id);

    vec3 mat = vec3(.5, .5, .5);
    if (id > 6.5) {
        mat = vec3(0.5, .25, .65);
    }
    else if (id > 5.5) {
        mat = vec3(0.4, 2.25, 1.25);
    }
    else if (id > 4.5) {
        mat = vec3(0.4, 1.25, 1225);
    }
    else if (id > 3.5) {
        mat = vec3(1.5, 0.35, 0.55);
    }
    else if (id > 2.5) {
        mat = vec3(0.4, 1.25, 1.25);
    }
    else if (id > 1.5) {
        mat = vec3(1.4, 1.25, .125);
    }
    else if (id > 0.5) {
        mat = vec3(1.9, .125, .125);
    }
    vec3 color = vec3(0.);
    color += mat * vec3(1., .8, .7) * ill(vec3(2., 4., -3.), p);
    color += mat * vec3(.1, .2, 2.0) * ill(vec3(-3., 1., 1.), p);
    color += mat * .05 * vec3(.1, .15, 1.8);

    if (mask < 1.) {   
        float t = smoothstep(-.5, 1.2, fw.y);
        color = (1. - t) * vec3(.1, .15, 1.8);
    }
    fragColor = vec4(pow(color, vec3(.4545)), 1.);

}

#define AA 1.
#define R iResolution.xy

mat2 rot(float a) {
	float c = cos(a), s = sin(a);
    return mat2(c,-s,s,c);
}

float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

vec3 opRepLim(in vec3 p, in float c, in vec3 l)
{
    vec3 q = p-c*clamp(round(p/c),-l,l);
    return q;
}

vec2 map( in vec3 pos, float time )  
{
    // Outer box
    float id = 0.;    
    float t = iTime;


    vec3 rpos = opRepLim(pos + vec3(0, 0.5, 0), 0.025, vec3(16.0, 0.05, 16.0));
    float rep = 36.;
    vec2 xy = floor(pos.xz * rep) / rep; // incorrect but i give up
    float h = 0.03*sin(16.*sqrt(dot(xy,xy))+fract(t)*3.1415*2.);

    float box = sdBox(rpos, vec3(0.005, 0.1+h, 0.005));


    // Floor
    float floorPlane = pos.y + 0.5;
    float d = min(box, floorPlane);
    if(floorPlane < box) {
    	id = 1.;    
    }

    return vec2(d, id);
}

vec3 calcNormal( in vec3 pos, float t ) 
{
    vec2 e = vec2(0.0001, 0.0);
    return normalize( vec3(map(pos+e.xyy,t).x-map(pos-e.xyy,t).x,
                           map(pos+e.yxy,t).x-map(pos-e.yxy,t).x,
                           map(pos+e.yyx,t).x-map(pos-e.yyx,t).x ) );
}

float castShadow( in vec3 ro, vec3 rd, float time )
{
    float res = 1.0;
    float t = 0.01;
    for( int i=0; i< 100; i++ )
    {
        vec3 pos = ro + t*rd;
        float h = map( pos, time ).x;
        res = min( res, 16.0*h/t );
        if ( res<0.0001 ) break;
        t += h;
        if( t > 10.0 ) break;
    }

    return clamp(res,0.0,1.0);
} 

vec3 castRay( in vec3 ro, vec3 rd, float time )
{
    float m = -1.0;
    float t = 0.01;
    int i=0;
    for( i=0; i<60; i++ )
    {
        float precis = 0.0001 * t;
        vec3 pos = ro + t*rd;

        vec2 h = map( pos, time );
        m = h.y;
        if( h.x< precis )
            break;
        t += h.x;
        if( t>20.0 )
            break;
    } 
    if( t>20.0 ) m=-1.0;
    return vec3(t, m, 1. - float(i)/60.);
}

void mainImage( out vec4 O, in vec2 U )
{
    float time = iTime/4.;

    vec3 col = vec3(0);
    vec3 res = vec3(0);

    for(float aax=0.; aax < AA; aax++)
    for(float aay=0.; aay < AA; aay++)
    {
        vec2 p = (2.*(U + vec2(aax, aay) / AA)-R)/R.y;

        vec3 ta = vec3(0,-0.5,0);
        vec3 ro = vec3(0, 0.3 + (-sin(2.+time) * .5 + .5) * 0.2, -1.);
        ro.xz *= rot(cos(2.*3.1415*time/2.)*0.4 + 3.*3.1415/4.);

        vec3 ww = normalize( ta-ro );
        vec3 uu = normalize( cross(ww, vec3(0,1,0)) );
        vec3 vv = normalize( cross(uu,ww) );

        vec3 rd = normalize( p.x*uu + p.y*vv + 1.8*ww );

        vec3 col = vec3(0.0);

        vec3 tm = castRay(ro, rd, time); 

        if( tm.x < 20. )
        {
            float t = tm.x;
            float id = tm.y;
            vec3 pos = ro + t*rd;
            vec3 nor = calcNormal(pos, time);

            vec3 mate = vec3(0.6); 

            if(id < 0.5) { // box
            	mate = vec3(191, 222, 245)/255.;     

            }
            else if(id < 1.5) { // floor
            	mate = vec3(67, 122, 161) / 255.;   
                float s = 8.;
                float w = 0.025;

                if(fract(pos.x*s) < w) {
                	mate *= 0.9;
                }

                if(fract(pos.z*s) < w) {
                	mate *= 0.9;    
                }
            }

            vec3  light1 = vec3( 0, 0.2, 1 );
            vec3  light2 = vec3( 1, 3, 1 );

            float key = clamp( dot( light1, nor ), 0.0, 1.0 );
            float bac = clamp( 0.2 + 0.8*dot( light2, nor ), 0.0, 1.0 );
            float amb = (0.7+0.3*nor.y);
            float shadow = clamp(castShadow(pos + nor*0.05, light1 - pos, time), 0., 1.);

            vec3 brdf  = 1.0*vec3(0.40,0.40,0.40)*amb*shadow;
            brdf += 1.0*vec3(255, 248, 230)/255.*key*tm.z*shadow;
            brdf += 1.0*vec3(230, 248, 255)/255.*bac*tm.z;

            col = mate * brdf * exp(-0.6*t);
        }

        res += clamp(col, 0.0, 1.0);
    }

    col = pow( res/(AA*AA), vec3(0.4545) );

    O = vec4(col, 1.0);
}

vec2 rot( in vec2 p, in float an )
{
    float cc = cos(an);
    float ss = sin(an);
    return mat2(cc,-ss,ss,cc)*p;
}

float bump(float x)
{
    return smoothstep(-1., 0., x) - smoothstep(0., 1., x);
}

float smoothbump(float r, float dr, float d)
{
    return smoothstep(r-dr/2., r, d) - smoothstep(r, r+dr, d);
}

float circle(in vec2 uv, in vec2 uv0, in float r, in float w) 
{
    return smoothbump(r, w, length(uv - uv0));
}

float unit(in float x) 
{
