// Analytic Ambient Occlusion

// In the simple setting of a "step" in the middle of an infinite plane,
// the integral of ambient occlusion can be computed exactly.

// An approximation of low-order color bleeding in this specific setup is also given.
// Left = without bleeding, right = with bleeding.

vec3 worldColor( vec3 ro, vec3 rd )
{
    vec3 col1 = vec3(0.5); // Back floor color
    vec3 col2 = vec3(0.5); // Wall color
    vec3 col3 = vec3(0.8,0.5,0.1); // Front floor color


    // Calculate ray intersection with each plane
    float tmin = 1e10;
    vec3 col = vec3(1);
    float t; vec3 p;

    // Plane 1 is z=1 for x<0 : "back floor"
    // AO = 1.0
    t = (1.0-ro.z)/rd.z;
    p = ro+t*rd;
    if(t > 0.0 && p.x < 0.001 && t < tmin)
    {
        tmin = t;
        col = col1;
    }


    // Plane 2 is x=0, for z between 0 and 1 : "wall"
   	t = -ro.x/rd.x;
    p = ro+t*rd;
    if(t > 0.0 && p.z > 0.0 && p.z < 1.0 && t < tmin)
    {
        tmin = t;
        // Analytic ambient occlusion (exact):
        // The floor occludes exactly half of the hemisphere visible from the wall
        col = col2*0.5;

        if(p.y > 0.)
        {
            float zp1sq = (p.z+1.)*(p.z+1.);

            // Bleeding from the floor.
            // Approximation obtained by fitting some terms with
            // exponentials, so they can be integrated by hand (whereas the
            // actual terms can't).
            // The formula is only valid for this specific setting for now.

            // First order bleeding (approximation)
            //col += col0*col0*0.25*(2.*zp1sq-1.)/zp1sq;

            // Second order bleeding (approximation)
            col += col2*col3*0.5*mix(0.5*col2, vec3(1), (zp1sq-0.5)/zp1sq);
        }
    }


    // Plane 3 is z=0, for x>0
    t = -ro.z/rd.z;
    p = ro+t*rd;
    if(t > 0.0 && p.x > -0.001 && t < tmin)
    {
        tmin = t;
        // Analytic Ambient Occlusion (exact)
        // If the distance from the wall is x, then
        // AO = 0.5+0.5*x/sqrt(1.0+x*x)
        col = col3*(0.5+0.5*p.x/sqrt(1.0+p.x*p.x));

        if(p.y > 0.)
        {
            // Bleeding from the wall, first order (exact):
            // the wall occupies exactly the remaining portion of the hemisphere
            // and is lit by 0.5*col2.
            col += col3*0.5*col2 * (0.5-0.5*p.x/sqrt(1.0+p.x*p.x));
        }
    }

    return col;
}


