  float color = 0.;
  float capas = 40.;
  for (float i = 0.; i < capas; i += 1.)
    {
      color += dibuja_capas(uv, i);
      uv.x += iTime * .002;
    }

  fragColor = vec4(vec3(color), 1.);
}

