// ----- distance functions modifiers ----- //
// source: https://www.iquilezles.org/www/articles/smin/smin.htm

vec3 arrayOp(vec3 p, vec3 offset) {
    return mod(p + 0.5 * offset, offset) - 0.5 * offset;
}

vec3 arrayOp(vec3 p, vec3 offset, vec3 count) {
    return p - offset * clamp(round(p/offset),vec3(0.0), count);
}

float unionOp( float d1, float d2) { return min(d1, d2);}
float subOp( float d1, float d2) { return max(d1, -d2); }
float interOp( float d1, float d2) { return max(d1,d2); }


// ----- distance functions for 3D primitives ----- //
// source: http://iquilezles.org/www/articles/distfunctions/distfunctions.htm


float boxSDF(vec3 p, vec3 boxDim) {
    vec3 q = abs(p) - boxDim;
    return length(max(q, 0.0)) + min(maxComp(q), 0.0);
}

float octahedronSDF(vec3 p, float s) {
  p = abs(p);
  float m = p.x+p.y+p.z-s;
  vec3 q;
       if( 3.0*p.x < m ) q = p.xyz;
  else if( 3.0*p.y < m ) q = p.yzx;
  else if( 3.0*p.z < m ) q = p.zxy;
  else return m*0.57735027;

  float k = clamp(0.5*(q.z-q.y+s),0.0,s); 
  return length(vec3(q.x,q.y-s+k,q.z-k)); 
}
/*
