  p = abs(p);
  return (p.x+p.y+p.z-s)*0.57735027;
}*/

float pyramidSDF(vec3 p, float size) {
	return interOp(octahedronSDF(p, size), -p.y);
}

float torusSDF( vec3 p, vec2 t ) {
  vec2 q = vec2(length(p.xz) - t.x, p.y);
  return length(q) - t.y;
}

float crossSDF(vec3 p, float size) {
	  float da = maxComp(abs(p.xy));
	  float db = maxComp(abs(p.yz));
	  float dc = maxComp(abs(p.zx));
	  return min(da,min(db,dc)) - size;
}

float menger(vec3 p, float boxSize, int iterations) {
	float d = boxSDF(p, vec3(boxSize));

	/* NICE
	float scale = 1.;
	for(int i=0; i<iterations; ++i) {

		vec3 q = arrayOp(p * scale, vec3(1.));
		d = subOp(d, crossSDF(q , 1./3.) / scale);
		scale *= 3.;
	}*/

	float anim = ((cos(iTime/2.)+1.)/2.+1.);
	// anim = 1;
	float count = 1.;
	for(int i=0; i<iterations; ++i) {
		vec3 q = arrayOp(p * anim , vec3(2.*boxSize/count));
		d = subOp(d, crossSDF(q, 1./(count*3.)));
		count *= 3.;
	}
	return d;
}

// sources http://blog.hvidtfeldts.net/index.php/2011/08/distance-estimated-3d-fractals-iii-folding-space/
float Sierpinski(vec3 z, int iterations) {
	vec2 D = vec2(1., 0.);
	vec3[5] SierpinskiOffset = vec3[5](D.xyy, -D.xyy, D.yyx, -D.yyx, D.yxy);
	float Scale = 2.;
	vec3 c;
	float dist, d;
	for(int n=0; n < iterations; n++) {
		c = SierpinskiOffset[0]; dist = length(z-SierpinskiOffset[0]);
		for(int i=1; i<5; i++) {
		     d = length(z-SierpinskiOffset[i]);
		     if (d < dist) { c = SierpinskiOffset[i]; dist=d; }
		 }
		z = Scale*z-c*(Scale-1.0);
	}

	return pyramidSDF(z* pow(Scale, float(-iterations)), pow(2., -float(iterations)));
}

