mat3 makeCamera( vec3 origin, vec3 target, float roll ) {
	vec3 up = vec3(sin(roll), cos(roll), 0.0);
	vec3 zz = normalize(target - origin);
	vec3 xx = normalize(cross(zz, up));
	vec3 yy = normalize(cross(xx, zz));
	return mat3( xx, yy, zz );
}

