    //point.x += 0.7 * mod( floor( qq.y ), 2. );

    return starSDF( point -.5, c, .19, .07 );



    float d = RAY_MARCH_TOO_FAR;
    const int rows = 3;
    const int cols = 5;



    for ( int row = 0 ; row < rows ; row++ ) {
        float r = float( row ) / float( rows );
        for ( int col = 0 ; col < cols ; col++ ) {
            float c = float( col ) / float( cols );
            c -= 0.25*mod(r,0.5);

            vec3 s = vec3( c * 9.4 - 3.8, r * 6.3 - 1.9, -1.3 );
            s = vec3( c * 1., r * 6.3 - 1.9, -1.3 );

            float n = noise( 1.2 * ( s.xy + t + r + c ) );
            s.xy += TRIG(.3,n * PI2 );

            d = min( d, starSDF( point, s, .19, .07 ) );
            break;
        }
        break;
    }   

    vec2 n = vec2( d, -d ) + 0.4 * abs(sin(t) ) + point.xy;
    float sparkle = fract( noise( 133. * n ) );

    return d - .003 * sparkle;
}

float ufo( vec3 point ) {
    vec2 t = TRIG( 1., iTime  *.8);
    vec3 u = vec3( t.x * 3., t.x * 0.2, 1.5 + t.y * 1. );
    //u = vec3(.2,-.1,2.8);
    float w = 1.57 + 0.02 - 0.04 * sin( iTime * 8. );
    return ufoSDF( point, u, w, .08 ); 
}

// this is kind of gross.. :-(
int whatsClosest( vec3 point ) {
    int what = 0;
    float d = RAY_MARCH_TOO_FAR;
    float q;

    q = planetSDF( point );
    if ( q < d ) {
        what = 1;
        d = q;
    }

    q = stars( point );
    if ( q < d ) {
        what = 2;
        d = q;
    }

    q = ufo( point );
    if ( q < d ) {
        what = 3;
        d = q;
    }
    return what;
}

float sceneDistance( vec3 point ) {
    float d = planetSDF( point );
    d = min( d, stars( point ) );
    d = min( d, ufo( point ) );   
    return d;
}

float rayMarch( in vec3 origin, in vec3 direction ) {
    float total = .0;
    for ( int i = 0 ; i < RAY_MARCH_STEPS ; i++ ) {
        vec3 point = origin + direction * total;

        float current = sceneDistance( point );
        total += current;
        if ( total > RAY_MARCH_TOO_FAR || abs(current) < RAY_MARCH_CLOSE ) {
            break;
        }
    }
    return total;
}

vec3 sceneNormal(vec3 p) {
	float d = sceneDistance(p);
    vec2 e = vec2(RAY_MARCH_CLOSE, .0);
    return normalize( d - vec3(
        sceneDistance(p-e.xyy),
        sceneDistance(p-e.yxy),
        sceneDistance(p-e.yyx))
    );
}

float pointLight( vec3 point, vec4 light ) {
    vec3 normal = sceneNormal( point );

    vec3 towardLight = light.xyz - point;
    float toLight = length( towardLight );
    towardLight = normalize( light.xyz - point );

    float diffuse = clamp( dot( normal, towardLight ), 0., 1. );

    vec3 lightStart = point + normal * RAY_MARCH_CLOSE * 2.;
    float d = rayMarch( lightStart, towardLight );
    diffuse *= 1. - 0.5 * smoothstep( d * 0.9, d, toLight );

    float lightStrength = .7 + .3 * light.w / dot( toLight, toLight );  
    return diffuse * lightStrength;
}

vec3 colorPoint( vec3 point ) {
    vec4 light    = vec4( .0, 7., 1., 6. );
    float ambient = 0.07;
    float gamma   = 1.33;

    float lighting = pointLight( point, light );
    lighting = ( 1. -  ambient ) * gamma * lighting;

    float d = dot( point, point );

    // ...

    vec3 rock = 1. * vec3( .9, .2, .1 );
    vec3 star = 2. * vec3( .9, .9, .3 );
    vec3 ufo  = 2. * vec3( .5, .6, .8 );

    vec3 color = rock;
    switch( whatsClosest( point ) ) {
        case 2: color = star; break;
        case 3: color = ufo; break;
    }

	return vec3( color * ambient + color * lighting );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = ( 2. * gl_FragCoord.xy - iResolution.xy ) / iResolution.y; 

    // camera settings

    float t = 0. * iTime + 1.57;
	vec3  eye  = vec3( 4. * cos( t ), 4., 4. * sin( t ) );  
    eye = vec3( 0., 0., 4. );
	vec3  look = vec3( .0 );
	float roll = 0.2 * cos( iTime );
    float zoom = 0.3 + 4. * ( 1. - abs( sin( iTime * .66 ) ) );
    zoom = 2.2;

    // setup and use the camera

	mat3 camera = makeCamera( eye, look, roll );
    vec3 direction = vec3( uv.xy, zoom );
    direction = normalize( camera * direction );

    // do the ray marching (sphere tracing)

    float distance_ = rayMarch( eye, direction );
    float tooFar = step( RAY_MARCH_TOO_FAR, distance_ );
    vec3 point = eye + direction * distance_;

	// the end
    vec3 background = vec3(0.);
    vec3 blue = vec3( .0, .0, .9 ); 

    vec2 at = uv + cos( iTime * .1 );
    background += blue * (0.1 + 0.9 * noise( at * 4. ) );

    background += vec3(.27)*step(0.5,noise(-at*99.));


    fragColor = vec4( mix( colorPoint( point ), background, tooFar ), 1. );
}

#define PI 	3.14159265
#define PI2 6.28318530
#define PI2INV 0.159154943

#define STEPSCALE 0.99
#define MAX_STEPS 60.
#define MAX_STEPS_SHADOW 1.
#define MAX_DIST 10.
#define SURF_DIST .001

#define n(a) (0.5 - 0.5 * a)

//float time = fGlobalTime / 60.;
#define time2 iTime

vec3 cp, cr;

vec4 sdSphere(in vec3 p, in float d, in vec3 col){
 return vec4(col, length(p) -d);   
}
vec4 sdTorus(in vec3 p, in float d, in float t, in vec3 col){
 return vec4(col, length(vec2(length(p.xz) - d, p.y)) - t);   
}
vec4 sdBox( vec3 p, vec3 b, vec3 col ){
  vec3 d = abs(p) - b;
  float ds = length(max(d,0.0)) + min(max(d.x,max(d.y,d.z)),0.0); // remove this line for an only partially signed sdf 
  return vec4(col, ds);
} 
vec4 sdBoxRounded(vec3 p, vec3 b, float roundness, vec3 col){
  vec4 d = sdBox(p,b,col);
  d.w -= roundness;
  return d;
}

float sminSq( in float a, in float b, float k) { float h = max( k - abs(a-b), 0.0); return min(a,b) - h*h /(k*4.0);}
vec4 smin( in vec4 a, in vec4 b, float k) {k = max(k,0.005); return vec4(mix(a.rgb, b.rgb, clamp((a.w - sminSq(a.w, b.w, 2.))- (b.w-a.w)*(1.0/k), 0.,1.)), sminSq(a.w,b.w,k));}
vec4 minn(in vec4 a, in vec4 b){ return vec4((a.w < b.w) ? a.rgb : b.rgb, min(a.w,b.w));}

vec3 opRep(vec3 p, vec3 r){ return mod(p, r) - r*0.5;}
vec3 opRepFloor(vec3 p, vec3 r){ vec3 f = (p/r) + 0.5*(1.-r);    return vec3(int(f.x), int(f.y), int(f.z));}

mat2 rot(float a){float c = cos(a); float s = sin(a); return mat2(c,-s,s,c);}
vec2 moda(vec2 p, float rep, float off) { vec2 inter = vec2(atan(p.y,p.x), length(p.xy)); inter.x /= PI2;  inter.x = (fract(inter.x*rep+0.5)-0.5)/rep;  inter.x *= PI2;
  return rot(off) * vec2(cos(inter.x),sin(inter.x))*inter.y;}

vec4 map (vec3 p){   
    vec4 d;

    if (length(p) < 5.)
    {

  vec4 d1 = sdSphere(p - vec3(0.7,0,0), 0.9, vec3(0.5,0.1,0));
  vec4 d2 = sdSphere(p - vec3(-0.7,0,0), 0.9, vec3(0.1,0.1,0.5));

  vec3 boxPos = p - vec3(sin(time2*0.5)*2.,0.8,0);
  vec4 b = sdBoxRounded(boxPos, vec3(0.5), 0.1, vec3(1,1,0));

  vec3 torusP = boxPos; torusP.xy *= rot(sin(-time2*0.5)* sin(time2)*PI2 * cos(1.5*sin(time2*2.)));
  vec4 torus = sdTorus(torusP, 0.7, 0.2, vec3(0.3,0.8,0));

  torusP.xy *= rot(PI*0.5);
  vec4 torus2 = sdTorus(torusP, 0.7, 0.2, vec3(0.3,0.8,0));

  torus = smin(torus, torus2, 0.03);

    b = smin(b,torus,0.15);
  d = smin(d1,d2,0.1);
  d = smin(d, b, n(sin(time2*3.)));
    }
    else
        d = vec4(1,1,1,length(p));


  // floor
  float checkerScale = 4.;
  d = minn(d, vec4(1. - 0.8*vec3( step(0., sin(p.x*checkerScale) * sin(p.z*checkerScale))), p.y+0.5));
  return d;  
}

vec3 getNormal(in vec3 p) {
 vec2 e = vec2(0, SURF_DIST); 
  return normalize(map(p).w - vec3(map(p - e.yxx).w,  map(p - e.xyx).w, map(p - e.xxy).w));
}

float softshadow( in vec3 ro, in vec3 rd,in float mint,in float maxt, float k)
{
    float res = 1.0;
    float stepDist = maxt / MAX_STEPS_SHADOW;
    float t = mint;

    for(int i = 0; i < 10000; i++)
    {
        float h = map(ro + rd*t).w;  
        if( h<SURF_DIST)
            return 0.0;
        res = min( res, k*h/t );
        t += min(h, stepDist*2.);
        if (t >= maxt)
			break;
    }
    return res;
}

float ao(vec3 ro, vec3 rd, float range, float strength)
{
    float totao = 0.;
    float sca = 1.;
    for (int aoi = 0; aoi < 5; aoi++)
    {
        float hr = SURF_DIST + range*SURF_DIST * float(aoi * aoi);
        vec3 aopos = ro + rd * hr;
        vec4 d = map(aopos); float dd = d.w;
        float ao = clamp(-(dd - hr), 0.0, 1.0);
        totao += ao * sca;
        sca *= 0.75;
    }
    const float aoCoef = 0.2;
    totao = pow(1.0 - clamp(aoCoef * totao, 0.0, 1.0), strength);
    return totao;
}

vec3 getLightSpecShadow(vec3 p, vec3 lightPos, vec3 n, float shininess) { 
  vec3 op = (p + n * 2. * SURF_DIST);
  vec4 d = map(op);

  vec3 l = normalize( lightPos - p);
  float ll = length(lightPos - p);
  vec3 nl = normalize(lightPos - op);
  vec3 r = normalize(-reflect(l,n));

  float diffuse = clamp(max(dot(n,l), 0.), 0., 1.); 
  float specular = clamp(pow(max(dot(r,-cr),0.0),shininess), 0.0, 1.0); 
  float shadow = softshadow(op, nl, SURF_DIST, ll, 16.);

  return vec3(diffuse, specular, shadow);
}

float pulse(float t, float lengthP1, float valP1, float lengthP2,float valP2){

  float totalLength = lengthP1 + lengthP2;
  float modT = mod(t, totalLength);

  if (modT < lengthP1){
     return valP1 * sin((modT / lengthP1) * PI * 0.5);
  }
  else
  {
     return valP2 * sin(((modT - lengthP1) / lengthP2) * PI * 0.5);
  }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
      // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord.xy/iResolution.xy;
