  uv.x *= iResolution.x / iResolution.y; 
  //time2 = mod(time2,200.);

    cp = vec3(0,2,-5);
    cr = normalize( vec3(uv,0.6) );


    cp.xz *= rot(PI);
    cr.xz *= rot(PI);

    cr.yz *= rot(-0.5);

    float r = 0.;
    vec3 p = cp;
    vec4 d = vec4(0.);

  float stepDist = MAX_DIST / MAX_STEPS;
  float dist = 0.;

    float ii;
    for (float i = 0.0; i < MAX_STEPS; i++){
        ii = i;
        d = map(p);
        dist += d.w * STEPSCALE; //min(d.w, stepDist*.25); 
        p = cp + cr*dist;

        if (d.w > MAX_DIST || d.w < SURF_DIST){
          break;
        }
    }

    // Done raycasting
    float zDepth = p.z-cp.z;
    vec3 n = getNormal(p);

    float iterGlow = ii / (MAX_STEPS);
