void mainImage( out vec4 O, vec2 U )
{
    ivec2 I = ivec2(U + 32.0 * iTime) & 65535;
    int x = I.x, y = I.y,
        i = (x - y ^ y + x);
    float f = fract(float(i) * 0.819);
    float v  = (floor(mod(iTime, 256.0) + 10.0) * 0.1875) + f;
    float v1 = (floor(mod(iTime, 256.0) + 11.0) * 0.1875) + f;
    O = vec4(mix(fract(v * v * v), fract(v1 * v1 * v1), fract(iTime)) * 3.0 - 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //Create basic glow mask
    float glowMask = pow(1.0-uv.y, 6.0);

    //create a wave along y
    float waveHeight = 0.05;
    float waveFrequency = 0.3;

    //make wave frequency more intuitive by multiplying by 2*Pi (not strictly necessary)
    waveFrequency *= 6.28318530718;

    float waveSpeed = 1.5;
    uv.y += waveHeight * sin((uv.x * waveFrequency) + (iTime * waveSpeed));

    //Create gradient mask
    float waveMask = pow(1.0-uv.y, 4.0);


    // Pixel color
    vec3 col = vec3(1.0, 0.0, 0.0);

    //More Fun Color
    //col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    //Apply the wave mask to the color
    col *= waveMask;

    //create a glow color and apply a mask
	vec3 glow = vec3(1.0, 1.0-uv.y, 1.0-uv.y) * glowMask;


    // Output to screen
    fragColor = vec4(col + glow,1.0);
}

// Using code from

//Morgan McGuire for the noise function
// https://www.shadertoy.com/view/4dS3Wd

#define time iTime
#define depth 70.0
#define fogSize 25.0
float fogCoef=1.0/(depth-fogSize);
float PI=acos(-1.0);

float random (in float x) {
	return fract(sin(x)*1e4);
}

float noise(in vec3 p) {
	const vec3 step = vec3(110.0, 241.0, 171.0);

	vec3 i = floor(p);
	vec3 f = fract(p);

	// For performance, compute the base input to a
	// 1D random from the integer part of the
	// argument and the incremental change to the
	// 1D based on the 3D -> 1D wrapping
	float n = dot(i, step);

	vec3 u = f * f * (3.0 - 2.0 * f);
	return mix( mix(mix(random(n + dot(step, vec3(0,0,0))),
	random(n + dot(step, vec3(1,0,0))),
	u.x),
	mix(random(n + dot(step, vec3(0,1,0))),
	random(n + dot(step, vec3(1,1,0))),
	u.x),
	u.y),
	mix(mix(random(n + dot(step, vec3(0,0,1))),
	random(n + dot(step, vec3(1,0,1))),
	u.x),
	mix(random(n + dot(step, vec3(0,1,1))),
	random(n + dot(step, vec3(1,1,1))),
	u.x),
	u.y),
	u.z);
}

mat2 rot(float a) {
	float ca=cos(a);
	float sa=sin(a);
	return mat2(ca,sa,-sa,ca);
}

float cloud(in vec3 p, vec3 centerPos, float scale,float radius ) {
	float l = length(p*0.1);
	vec3 d = vec3(p.x+sin(l+time)*2.0,p.y+sin(l)*2.0,0.0);
	float coef = max(length(d)-1.5,0.0);
	float c=1.0;
	float n1=1.0;
	for(int i=0; i<8; ++i) {
		n1+=1.0/c*abs(noise((p*c+time*1.0)*scale));
		c*=2.0;
	}
	return n1+(coef);
}

float mapHyper(vec3 p){
	return cloud(p,vec3(0,0,0),0.5,0.1);
}  

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
	uv -= 0.5;
	uv /= vec2(iResolution.y / iResolution.x, 1);
	vec3 s=vec3(0.5,0.5,100);
	float t2=(time*1.5);
	s.xz *= rot(sin(t2)*0.005);
	vec3 t=vec3(0,0,0);
	s.x += cos(t2*0.2)*0.10*sin(time*0.01);
	s.y += sin(t2*0.2)*0.10*sin(time*0.01+10.0);
	vec3 cz=normalize(t-s);
	vec3 cx=normalize(cross(cz,vec3(0,1,0)));
	vec3 cy=normalize(cross(cz,cx));
	vec3 r=normalize(uv.x*cx+uv.y*cy+cz*0.7);
	s.z+=time*-8.0;

	vec3 p=s;
	float d;
	float seuil=5.1;
	float c= 0.0;
	float distMax =100.0;
	float steps = 300.0;
	float color = 0.0;
	float cl;
	float dist = clamp((1.0-dot(vec3(0,0,-1.0),r))*4.0,0.0,1.0);
	int cc =int(mix(300.0,1000.0,dist));
	float uu =mix(1.0,0.25,dist);
	vec3 p3 = vec3(0);
	for(int i=0; i<cc; ++i) {
		float d2 ;
		float d;
		if(color<0.001)d = mapHyper(p);
		c =d;  
		if( c>seuil )
		{vec3 p2 =p;
			if(p3.x==0.0)p3=p;
			for(int j;j<20;j++)
			{
				if(color<0.2)d2= mapHyper(p2);
				else
				d2 = 5.2;
				if(d2>seuil)
				{
					color = color*0.9 + d2*0.02*0.1;
				}
				p2 +=normalize(vec3(-0.0,-0.0,-5.0))*0.42;
			} 
		}
		cl = 1.0-color;
        p+=r*distMax/steps*uu;
		//p+=r*distMax/float(cc)*uu;
	}

	vec2 off=vec2(1.1,0.0);
	vec3 n=normalize(mapHyper(p3)-vec3(mapHyper(p3-off.xyy), mapHyper(p3-off.yxy), mapHyper(p3-off.yyx)));

	//compositing
	vec3 col=vec3(0);
	col = mix(vec3(0.0,0.0,0.2),vec3(0.88,0.88,0.9),cl);
	float fog =  clamp((length(p3-s)-fogSize)*fogCoef,0.0,1.0);
	col = mix(col,vec3(0.88,0.88,0.9),fog);
	fragColor = vec4(col,1.0);
}

// Pencil sketched Voronoi

// Voronoi tesselation implementation borrowed from Inigo Quilez
// https://www.shadertoy.com/view/ldl3W8
// Pencil sketch borrowed from Shane 
// https://www.shadertoy.com/view/3dtBWX
// 
// Modifications were made to assign color and some kind of 
// identificator to each cell so they could be textured differently
//
// Look and feel inspired by my daughter's drawings

#define ANIMATE
#define PI 3.14159265

vec2 hash2( vec2 p )
{
    // procedural white noise	
	return fract(sin(vec2(dot(p,vec2(127.1,311.7)),dot(p,vec2(269.5,183.3))))*43758.5453);
}

// Standard 2D rotation formula.
mat2 rot2(in float a){ float c = cos(a), s = sin(a); return mat2(c, -s, s, c); }


// IQ's vec2 to float hash.
float hash21(vec2 p){  return fract(sin(dot(p, vec2(27.619, 57.583)))*43758.5453); }

// Cheap and nasty 2D smooth noise function with inbuilt hash function -- based on IQ's 
// original. Very trimmed down. In fact, I probably went a little overboard. I think it 
// might also degrade with large time values.
float n2D(vec2 p) {

	vec2 i = floor(p); p -= i; p *= p*(3. - p*2.);  

	return dot(mat2(fract(sin(vec4(0, 1, 113, 114) + dot(i, vec2(1, 113)))*43758.5453))*
               vec2(1. - p.y, p.y), vec2(1. - p.x, p.x) );

}

vec3 pencil(in vec3 col, in vec2 p, in float a){
    //Borrowed from Shane https://www.shadertoy.com/view/3dtBWX
    //Modified strokes to be more directional

    // Rough pencil color overlay... The calculations are rough... Very rough, in fact, 
    // since I'm only using a small overlayed portion of it. Flockaroo does a much, much 
    // better pencil sketch algorithm here:
    //
    // When Voxels Wed Pixels - Flockaroo 
    // https://www.shadertoy.com/view/MsKfRw
    //
    // Anyway, the idea is very simple: Render a layer of noise, stretched out along one 
    // of the directions, then mix a similar, but rotated, layer on top. Whilst doing this,
    // compare each layer to it's underlying greyscale value, and take the difference...
    // I probably could have described it better, but hopefully, the code will make it 
    // more clear. :)
    // 
    // Tweaked to suit the brush stroke size.
    vec2 q = p*4.;
    const vec2 sc = vec2(1, 12);
    float ang = a * 2.73543 * PI;
    q += (vec2(n2D(q*4.), n2D(q*4. + 7.3)) - .5)*.03;
    q *= rot2(ang);
    // I always forget this bit. Without it, the grey scale value will be above one, 
    // resulting in the extra bright spots not having any hatching over the top.
    col = min(col, 1.);
    // Underlying grey scale pixel value -- Tweaked for contrast and brightness.
    float gr = (dot(col, vec3(.299, .587, .114)));
    // Stretched fBm noise layer.
    float ns = (n2D(q*sc)*.64 + n2D(q*16.*sc)*.34);
    // Compare it to the underlying grey scale value.
    ns = gr - ns;
    //
    // Repeat the process with a couple of extra rotated layers.
    ang += PI * 0.2;
    q *= rot2(ang);
    float ns2 = (n2D(q*sc)*.64 + n2D(q*4.*sc)*.34);
    ns2 = gr - ns2;
    ang += PI * 0.1;
    q *= rot2(ang);
    float ns3 = (n2D(q*sc)*.64 + n2D(q*2.*sc)*.34);
    ns3 = gr - ns3;
    //
    // Mix the two layers in some way to suit your needs. Flockaroo applied common sense, 
    // and used a smooth threshold, which works better than the dumb things I was trying. :)
    //ns = min(min(ns, ns2), ns3) + .5; // Rough pencil sketch layer.
    ns = smoothstep(0., 1., min(min(ns, ns2), ns3) + .5); // Same, but with contrast.
    // 
    // Return the pencil sketch value.
    return vec3(ns);

}

vec4 voronoi( in vec2 x )
{
    // borrowed from IQ
    // modified to return index into color wheel 
    // and cell identifier to apply cell specific texture

    vec2 n = floor(x);
    vec2 f = fract(x);

    //----------------------------------
    // first pass: regular voronoi
    //----------------------------------
	vec2 mg, mr;
    float col = 0.5;
    vec2 globg;
    float t = iTime * 0.5;
    t = 0.;

    float md = 8.0;
    for( int j=-1; j<=1; j++ )
    for( int i=-1; i<=1; i++ )
    {
        vec2 g = vec2(float(i),float(j));
		vec2 o = hash2( n + g );
		#ifdef ANIMATE
        o = 0.5 + 0.5*sin(iTime + 6.2831*o );
        #endif	
        vec2 r = g + o - f;
        float d = dot(r,r);

        if( d<md )
        {
            md = d;
            mr = r;
            mg = g;
            col = o.x;
            globg = n + g;
        }
    }

    //----------------------------------
    // second pass: distance to borders
    //----------------------------------
    md = 8.0;
    for( int j=-2; j<=2; j++ )
    for( int i=-2; i<=2; i++ )
    {
        vec2 g = mg + vec2(float(i),float(j));
		vec2 o = hash2( n + g );
		#ifdef ANIMATE
        o = 0.5 + 0.5*sin( iTime + 6.2831*o );
        #endif	
        vec2 r = g + o - f;

        if( dot(mr-r,mr-r)>0.00001 )
        md = min( md, dot( 0.5*(mr+r), normalize(r-mr) ) );
    }

    return vec4( md, globg, col);
}

float rootstep(in float A, in float B, in float t) {
    return A + sqrt(sqrt(t)) * (B - A);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord - iResolution.xy * 0.5)/iResolution.xx;

    vec4 c = voronoi( 7.0*p );

    float q = 0.69;

    //rainbow pastel colors
    vec3 rnbCol = q + (1. - q)*cos(2. * PI *c.w + vec3(0, 1, 2)*1.5);

    vec3 col;
    col = sqrt(rnbCol);
    col = mix(rnbCol *.9, col, rootstep(0., 1., c.x));

    // pencil pattern
    col = clamp((0.8 + 0.3 * pencil(col,p * 12., 0.53141 * c.y + c.z)) * col, 0., 1.);

	fragColor = vec4(col,1.0);
}

#define PI 3.14159265
#define TWO_PI 6.28318530718

float sdCircle(in vec2 _st, in float _radius)
{
    return length(_st) - _radius;
}


float boxDist(vec2 p, vec2 size, float radius)
{
  	vec2 d = abs(p) - size - radius;
  	return min(max(d.x, d.y), 0.0) + length(max(d, 0.0)) - radius;
}

// taken from Chapter 7 of "The Book of Shaders"
//  https://thebookofshaders.com/07/
float makeAPolygon(in float _numberOfSides, in vec2 _st)
{
    float a = atan(_st.x,_st.y);
    float r = TWO_PI/float(_numberOfSides);   
	return cos(floor(.5+a/r) * r - a) * length(_st);
}

float rect(vec2 coordSpace, vec2 dim)
{          
    vec2 tmp = smoothstep( dim, dim, coordSpace );
    float pct = tmp.x * tmp.y;

    tmp = smoothstep( dim, dim, 1. - coordSpace );
    pct *= tmp.x * tmp.y;

    return pct;
}

// concept from "Rainbow Showoff"
// by akufishi
// https://www.shadertoy.com/view/lscBRf
float rainDrop( vec2 coordSpace, float scalar, float timeOffset, float modVal)
{      
    float tempTime = iTime * 10.;
    float initPos = fract( sin(coordSpace.x) * scalar ) / iTime + timeOffset;
    return rect( vec2(mod(coordSpace.y + tempTime * initPos, modVal)) , vec2(0.485, .44) );
}

vec2 rotateOnZ(in vec2 _st, in float _angle)
{
    return mat2( cos(_angle * PI), -sin(_angle * PI),
                 sin(_angle * PI),  cos(_angle * PI) ) * _st;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = vec3(0.0, 0.749, 1.0);

    col = mix(vec3(0.173, 0.149, 0.337), col, uv.y);    
    float scalar = 20.0;

    for(float i = 3.0; i < 7.0; i++)
    {
        vec2 st = uv * scalar;
        st -= scalar / i;
        st += vec2(0.0, 2.0);
        st += vec2(iTime * i, sin(iTime) / i / 2.0);  

        //sin(x)+ (0.3 * cos(0.5 * x))
        float yVal = abs(sin(st.x)+ (0.3 * cos(i * iTime))) * -1.0;
        float invertedI = 9.0 - i;

        // boat stuff
        // credit to @neondactyl
        if(i == 5.0)
        {      
            vec2 xy = uv - 0.5;
            xy *= vec2(scalar / 5.0);          
            xy += vec2(0.0, 1.25 + sin(iTime * 10.0) / 10.0);           

            float rotationVal = (abs(sin(iTime * 3.2)) - 0.5) / 10.0;
            xy = rotateOnZ(xy, rotationVal);


            float flagPoleDF = step(boxDist(xy * vec2(4.0, 1.0) - vec2(0.5, 0.0), vec2(0.00015, 1.5), 0.0), 0.15);
            col = mix( col, vec3(0.33), flagPoleDF );

            vec2 sailScale = vec2(2.5+(0.6*cos(xy.y-iTime)/0.90), -1.0);                      
            vec2 sailOffset = vec2(-0.35, 0.85);

            float tmpScale = mod(abs(iTime*5.0+1.5), 1.0) * sqrt(25.0-xy.y*xy.y)-1.8;

            //mod(iTime, 1.5) + 3.5

            sailScale = vec2(2.5+(0.6*cos(-xy.y-tmpScale /*xxx*/)/0.90), -1.0);

            vec2 wz = xy * sailScale + sailOffset;

            float sailDF = smoothstep(makeAPolygon(4.0, wz)-0.015, makeAPolygon(4.0, wz)+0.015,0.65);
            col = mix( col, vec3(1.0), sailDF);


            if(xy.y < 0.05)
            {          
                float boatDF = smoothstep(sdCircle(xy*0.98, 0.5) + 0.015, sdCircle(xy*0.98, 0.5) - 0.015, 0.5);    
            	col = mix(vec3(0.369, 0.184, 0.051), col, boatDF);                 
            }  

            if(xy.y < 0.0)
            {          
                float boatDF = smoothstep(sdCircle(xy, 0.5) + 0.015, sdCircle(xy, 0.5) - 0.015, 0.5);    
            	col = mix(vec3(0.545, 0.271, 0.075), col, boatDF);                 
            }                         
        }

        // water
        if(st.y - 0.25 < yVal )
        {
            col = vec3(0.282 / (invertedI / 2.0), 0.239 / (invertedI / 2.0), 0.545 / (invertedI / 2.0));
        }   
        if(st.y < yVal )
        {
            col = vec3(0.0, 0.0, 0.545 / (invertedI / 2.0));
        }

    }

    for(float i = 6.0; i > 2.0; i--)
    {
        vec2 st = uv * scalar * 1.75;
        st -= scalar / i;

        float invertedI = (8.0 - i);        
        st -= vec2(-iTime * (invertedI / 2.0), 28.0);


        float yVal = 0.0;      
        yVal = sin(pow(sin(st.x), 3.0) - pow(cos(st.y), 1.0) + abs(cos(iTime + i) ) );

        // clouds + borders
        if(st.y + 0.25 > yVal)
        {
            col = vec3(1.0 / (i * i  / 1.5));
        }       
        if(st.y > yVal )
        {
            col = vec3(1.0 / i);
        }   

        // rain
        float rainDF = rainDrop(uv + vec2(0.2, 0.0), 5000.0, 0.2, 5.5 );
    	col = mix(col, vec3(0.851, 0.851, 1.0), rainDF );          

    }
    fragColor = vec4(col,1.0);
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float PI = 3.14159263;
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float freq;
    float radius;
    float t;
    float rot;
    int numEl;
    float scale = 1.; //how bright the image is. set very high for pure B/W, set lower for grayscale
    int choice = 0; //choice between settings. mainly for quickly changing 
    //between constant and varying frequencies

    if(choice == 0){
    freq = 120.; //busyness of screen
    radius = 2.; //radius of circle of points
    rot = 0.0; //how fast the screen rotates
    t = iTime*0.01*60.; //a timing constant
    numEl = 6; //number of radiators on ring
    }

    else if(choice == 1){
    t = iTime*0.01*60.;
    freq = 300.*tanh(0.018*(t-30.))+150.; //busyness of screen
    //freq = 500.;
    radius = 1.; //radius of circle of points
    rot = 0.03; //how fast the screen rotates
    numEl = 6; //number of radiators on ring
    }

    float col = 0.;
    if(numEl%2==0){ //if the number of radiators is even, it'll be lined up well
    for(int i = 0; i<numEl; i++){
    uv = fragCoord/iResolution.xy; //reset coordinate plane
    uv.x *= iResolution.x/iResolution.y; //scale coordinates
    uv.x -= 0.5*iResolution.x/iResolution.y; //move origin
    uv.y-=0.5; //move origin
    uv.x-=radius*cos(2.*3.14159*float(i)/float(numEl)-rot*t); //move to each point on ring
    uv.y-=radius*sin(2.*3.14159*float(i)/float(numEl)-rot*t);

    col+=(scale/float(numEl))*cos(freq*length(uv)-t); //sum up the field magnitude at each point due to each radiator
    //0.77 is a scaling factor. Set higher for wider color range.
    }
    }

    else{ //if number of radiators is odd, the ring needs to be rotated to be vertically symmetric

    for(int i = 0; i<numEl; i++){
    uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;
    uv.x -= 0.5*iResolution.x/iResolution.y;
    uv.y-=0.5;
    uv.x-=radius*cos(2.*3.14159*float(i)/float(numEl)-rot*t+PI/2.);
    uv.y-=radius*sin(2.*3.14159*float(i)/float(numEl)-rot*t+PI/2.);

    col+=(scale/float(numEl))*cos(freq*length(uv)-t);
    }  
    }
    //col = normalize(col);
    vec3 c = vec3(col+.64, 0.7, col+1.); //mess with this to get different color mappings


    //vec3 c = vec3(col*sin(t), col*cos(t), col*cos(t*0.5+0.5));//all these constants are to get a nice color
    //colors are hard, so I put oscillations in so eventually the colors will look good. Thats when you screenshot or record
    c = hsv2rgb(c); //I find HSV to be easier to work in, but there are cool color schemes in RGB too.
    fragColor = vec4(vec3(c), 1.0);
}

// Raymarching the Sierpinski Tetrahedron: Final
// Created by Anthony Hall for Writ 107T

// Raymarching constants
const float maxDistance = 30.0;
const float epsilon = 0.001;

const float maxShadowDistance = 10.0;
const float shadowEpsilon = 0.005;

// Lighting
const float kAmbient = 0.35;
const float kDiffuse = 1.0 - kAmbient;

const float shadowK = 30.0;

const float aoIncrement = 0.02;
const float aoK = 4.0;

// Camera
vec3 cameraPos = vec3(0.0, 0.5, 4.0);
const float fov = radians(50.0);

// Other scene globals
const vec3 skyColor = vec3(0.5, 0.75, 1.0);
const vec3 toSun = normalize(vec3(-1.0, 3.75, 2.0));

// Number of iterations for the Sierpinski IFS
const int sierpinskiLevel = 5;

// Vertices of the tetrahedron defined by the SDF
const vec3[] vertices = vec3[](
    vec3(1.0, 1.0, 1.0),
    vec3(-1.0, 1.0, -1.0),
    vec3(-1.0, -1.0, 1.0),
    vec3(1.0, -1.0, -1.0));

// Get a 2D rotation matrix
mat2 getRotationMatrix(float angle)
{
	return mat2(cos(angle), sin(angle),
                -sin(angle), cos(angle));
}

// Get a surface color based on a point's location
// Uses a procedural palette from iq
// https://www.shadertoy.com/view/ll2GD3
vec3 getColor(vec3 point)
{
    // Set t such that the floor color will change slowly
    // and the tetrahedron color will change more rapidly
    float t;
    if (point.y < -1.9) {
        t = point.x / 60.0 + 0.2;
    }
    else {
        point /= 2.0;
     	t = point.x * point.x - point.y + point.z * point.z;
    }

    vec3 a = vec3(0.5);
    vec3 b = vec3(0.5);
    vec3 c = vec3(1.0);
    vec3 d = vec3(0.00, 0.10, 0.20);
	vec3 color = a + b*cos( radians(360.0)*(c*t+d) );

    // Lighten the color a bit
    return mix(color, vec3(1.0), 0.3);
}

// Signed distance to a floor plane
float sdFloor(vec3 point, float floorY)
{
	return point.y - floorY;
}

// Signed distance to a tetrahedron within canonical cube
// https://www.shadertoy.com/view/Ws23zt
float sdTetrahedron(vec3 point)
{
    return (max(
	    abs(point.x + point.y) - point.z,
	    abs(point.x - point.y) + point.z
	) - 1.0) / sqrt(3.);
}

// Fold a point across a plane defined by a point and a normal
// The normal should face the side to be reflected
vec3 fold(vec3 point, vec3 pointOnPlane, vec3 planeNormal)
{
    // Center plane on origin for distance calculation
    float distToPlane = dot(point - pointOnPlane, planeNormal);

    // We only want to reflect if the dist is negative
    distToPlane = min(distToPlane, 0.0);
    return point - 2.0 * distToPlane * planeNormal;
}

// Signed distance to Sierpinski tetrahedron at specified level
// Rotates over time or with mouse press
float sdSierpinski(vec3 point, int level)
{
    // If the mouse is not pressed, rotate the tetrahedron over time
    // Otherwise, rotate it based on the mouse position
    if (iMouse.z <= 0.0) {
        float time = iTime / 4.0;
        point.xz = getRotationMatrix(time) * point.xz;
    }
    else {
     	vec2 mouse = (2.0 * iMouse.xy - iResolution.xy) / iResolution.y;

        point.yz = getRotationMatrix(mouse.y) * point.yz;
        point.xz = getRotationMatrix(mouse.x) * point.xz;
    }

    float scale = 1.0;
    for (int i = 0; i < level; i++)
    {
    	// Scale point toward corner vertex, update scale accumulator
        point -= vertices[0];
        point *= 2.0;
        point += vertices[0];

        scale *= 2.0;

        // Fold point across each plane
        for (int i = 1; i <= 3; i++)
        {
            // The plane is defined by:
            // Point on plane: The vertex that we are reflecting across
            // Plane normal: The direction from said vertex to the corner vertex
         	vec3 normal = normalize(vertices[0] - vertices[i]); 
            point = fold(point, vertices[i], normal);
        }
    }
    // Now that the space has been distorted by the IFS,
    // just return the distance to a tetrahedron
    // Divide by scale accumulator to correct the distance field
    return sdTetrahedron(point) / scale;
}

// Returns signed distance to the scene
float scene(vec3 point)
{
    // Create a sierpinski tetrahedron and a floor
 	float sierpinskiDist = sdSierpinski(point, sierpinskiLevel);
    float floorDist = sdFloor(point, -2.0);

    return min(sierpinskiDist, floorDist);
}

// Approximates the normal at an intersection by calculating the gradient of the distance function
vec3 estimateNormal(vec3 point) {
	return normalize(vec3(
        scene(vec3(point.x + epsilon, point.y, point.z)) - scene(vec3(point.x - epsilon, point.y, point.z)),
        scene(vec3(point.x, point.y + epsilon, point.z)) - scene(vec3(point.x, point.y - epsilon, point.z)),
        scene(vec3(point.x, point.y, point.z  + epsilon)) - scene(vec3(point.x, point.y, point.z - epsilon))));
}

// Distance field AO
// https://www.iquilezles.org/www/material/nvscene2008/rwwtt.pdf slide 53
float calcAO(vec3 surfacePoint, vec3 normal)
{
    float t = aoIncrement;
    float distSum = 0.0; // Sum of distance differences

    // Take four distance samples, compare to orthogonal distance
    for (int i = 0; i < 4; i++)
    {
        vec3 point = surfacePoint + t * normal;
     	float dist = scene(point);

        distSum += exp2(-float(i)) * (t - dist);

        t += aoIncrement;
    }
    return 1.0 - aoK * distSum;
}

// Calculates the percentage that a point is illuminated
// https://www.iquilezles.org/www/articles/rmshadows/rmshadows.htm
float calcShadow(vec3 surfacePoint)
{
    // Initialize our marching variables
 	vec3 point = surfacePoint;
    float t;
    float illumination = 1.0;

    // Initialize the ray a little bit away from the point
    // We don't want to start close enough to be considered a hit
    for (t = 2.0 * shadowEpsilon; t < maxShadowDistance;)
    {
     	point = surfacePoint + t * toSun;
        float dist = scene(point);

        // The path to the sun is blocked
        if (dist < shadowEpsilon) {
            return 0.0;
        }

        // Get darker if we get closer to the scene than we have been before
        illumination = min(illumination, shadowK * dist/t);
        t += dist;
    }
    return illumination;
}

// Shades a surface at the given point
vec3 shadeSurface(vec3 point) {
    // Calculate the surface normal and color of our point
    vec3 normal = estimateNormal(point);
    vec3 surfaceColor = getColor(point);

    // Ambient
    vec3 color = kAmbient * surfaceColor;

    // Diffuse
	float diffuseIntensity = max(dot(normal, toSun), 0.0);

    // Shadow
    float illumination = calcShadow(point);
    diffuseIntensity *= illumination;
    color += kDiffuse * diffuseIntensity * surfaceColor;

    // AO (only on pyramid)
    if (point.y > -2.0 + epsilon) {
        float occlusion = calcAO(point, normal);
        color *= occlusion;
    }

    return color;
}

// Returns the result color of casting any ray
vec3 castRay(vec3 rayOrigin, vec3 rayDir)
{
    // Initialize our marching variables
    vec3 point = rayOrigin;
    float t;
    vec3 color = skyColor;

    // Repeatedly march the ray forward based on the distance to the scene
    for (t = 0.0; t < maxDistance; point = rayOrigin + t * rayDir)
    {
     	float dist = scene(point);

        // We got a hit
        if (dist <= epsilon) {
            color = shadeSurface(point);
        	break;
        }
        t += dist;
    }
    float totalDist = t / maxDistance;
    return mix(color, skyColor, totalDist * totalDist);
}

// OpenGL's lookAt function
// https://www.geertarien.com/blog/2017/07/30/breakdown-of-the-lookAt-function-in-OpenGL/
mat4 lookAt(vec3 eye, vec3 at, vec3 up)
{
  vec3 zAxis = -normalize(at - eye);    
  vec3 xAxis = -normalize(cross(zAxis, up));
  vec3 yAxis = -cross(xAxis, zAxis);

  return mat4(
    vec4(xAxis, -dot(xAxis, eye)),
    vec4(yAxis, -dot(yAxis, eye)),
    vec4(zAxis, -dot(zAxis, eye)),
    vec4(0.0, 0.0, 0.0, 1.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalize the coordinates to [-1, 1] in the minimum dimension
    // Use this to calculate the ray direction
    float minDimension = min(iResolution.x, iResolution.y);
    vec2 coord = 2.0 * (fragCoord - iResolution.xy/2.0) / minDimension;
    vec3 rayDir = normalize(vec3(coord * tan(fov/2.0), -1.0));

    // Make the camera point toward the origin
    mat4 cameraMat = lookAt(cameraPos, vec3(0.0), vec3(0.0, 1.0, 0.0));
    rayDir = (cameraMat * vec4(rayDir, 1.0)).xyz;

    // Cast the ray!
    vec3 color = castRay(cameraPos, rayDir);
    fragColor = vec4(color, 1.0);
}

float noise2D(vec2 p) {
    float a = fract(sin(dot(p, vec2(127.1, 311.7))) *
                 43758.5453);

    float b = fract(cos(dot(p, vec2(284.1, 132.4))) *
                    43758.5453);
    return fract(a + b);
}

vec2 cubic(vec2 p) {
    return p*p*(3.f-2.f*p);
}

const float twopi  = 6.28318530718f;

float interpNoise2D(vec2 p) {
    p = mod(p, 1000.f);  
    vec2 p_flr = floor(p);
    vec2 p_fra = fract(p);


    float tl = noise2D(p_flr) * twopi;
    float tr = noise2D(p_flr + vec2(1.f, 0.f)) * twopi;
    float bl = noise2D(p_flr + vec2(0.f, 1.f)) * twopi;
    float br = noise2D(p_flr + vec2(1.f, 1.f)) * twopi;

    vec2 tlvec = vec2(-sin(tl), cos(tl));
    vec2 trvec = vec2(-sin(tr), cos(tr));
    vec2 blvec = vec2(-sin(bl), cos(bl));
    vec2 brvec = vec2(-sin(br), cos(br));

    float tldot = dot(tlvec, p_fra);
    float trdot = dot(trvec, p_fra - vec2(1.f, 0.f));
    float bldot = dot(blvec, p_fra - vec2(0.f, 1.f));
    float brdot = dot(brvec, p_fra - vec2(1.f, 1.f));

    vec2 cubic = cubic(p_fra);

    float topmix = mix(tldot, trdot, cubic.x);
    float botmix = mix(bldot, brdot, cubic.x);
    return mix(topmix, botmix, cubic.y) + 0.5;
}

float fbm(vec2 p) {
    float persistence = 0.5;
    int octaves = 6;
    float total = 0.0;

    for(int i = 1; i <= octaves; i++) {
        float freq = pow(2.f, float(i));
        float amp = pow(persistence, float(i));

        total += interpNoise2D(p* freq) * amp;
    }
    return total;
}


// uv in [0, 1]
vec3 myInterp(vec3 tl, vec3 tr, vec3 bl, vec3 br, 
              vec2 uv) {
    uv = uv*uv*(3.f-2.f*uv);
    vec3 topmix = mix(tl, tr, uv.x);
    vec3 botmix = mix(bl, br, uv.x);
    return mix(topmix, botmix, uv.y);
}


