vec4 hsv2rgb(vec3 c) 
{
    vec3 rgb = clamp(abs(mod(c.x*6.+vec3(0.,4.,2.),6.)-3.)-1.,0.,1.);
    rgb = rgb * rgb * (3.0 - 2.0 * rgb);
    vec3 color = c.z * mix(vec3(1.0), rgb, c.y);
    return vec4(color,1);
}

vec4 GetColor(in int i)
{
	float noise0 = 0.5*(1.0 + PerlinNoise(vec3(points[i], iTime*0.01), 5.0));
