    vec3 hsv = vec3(noise0, 0.7+0.3*noise1, 1.0);
    return hsv2rgb(hsv);
}

vec2 GetPoint(in int i) //elliptical orbit
{
    // move the points on orbits
    float x = points[i].x + a[i]*sin(c[i]*iTime*0.1);    
    float y = points[i].y + b[i]*sin(c[i]*iTime*0.1); 
  	return vec2(x,y);
}

vec4 GetVoronoiColor( in vec2 point ) //loops through points, finds nearest color assosiated
{
    float nearDistSq = 1e20;
    vec4 color;
    vec2 offset;
    offset.x = 0.5*FractalNoise(vec3(point, iTime*0.2), 2.0, 0.5, 3);
    offset.y = 0.5*FractalNoise(vec3(point, iTime*0.2 + 100.0), 2.0, 0.5, 3);

    for ( int i = 0; i < 8; i++ )
    {
        vec2 diff = point+offset-GetPoint(i);
        float d2 = dot(diff,diff);
        if ( d2 < nearDistSq )
        {
            nearDistSq = d2;
            color = GetColor(i);
        }
    }
    return color;
}


