const int MAX_MARCHING_STEPS = 255;
const float MIN_DIST = 0.0;
const float MAX_DIST = 100.0;
const float EPSILON = 0.0001;

float stillNoise (vec2 uv) {
    return sin(dot(uv.xy,vec2(130.9898,500.233)));}

/* Signed distance function for a sphere centered at "position" */
float sphereSDF(vec3 samplePoint, float radius, vec3 position) {
    samplePoint = vec3(samplePoint.x+position.x, samplePoint.y+position.y, samplePoint.z+position.z);
    return length(samplePoint) - radius * stillNoise(samplePoint.xy);
  }

/*
 * Signed distance function describing the scene.
 * 
 * Absolute value of the return value indicates the distance to the surface.
 * Sign indicates whether the point is inside or outside the surface,
 * negative indicating inside.
 */
float sceneSDF(vec3 samplePoint) {
    return sphereSDF(samplePoint, .5, vec3(sin(tt),0.,0.));
}

/*
 * Return the shortest distance from the camera position to the scene surface along
 * the marching direction. If no part of the surface is found between start and end,
 * return end.
 * 
 * cam: the camera position, acting as the origin of the ray
 * marchingDirection: the normalized direction to march in
 * minDepth: the starting distance away from the camera
 * maxDepth: the max distance away from the camera to march before giving up
 */
float shortestDistanceToSurface(vec3 cam, vec3 marchingDirection, float minDepth, float maxDepth) {
    float depth = minDepth;
    for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
        float dist = sceneSDF(cam + depth * marchingDirection);
        if (dist < EPSILON) {
			return depth;
        }
        depth += dist;
        if (depth >= maxDepth) {
            return maxDepth;
        }
    }
    return maxDepth;
}


/*
 * Return the normalized direction to march in from the cam point for a single pixel.
 * 
 * fieldOfView: vertical field of view in degrees
 * size: resolution of the output image
 * fragCoord: the x,y coordinate of the pixel in the output image
 */
vec3 rayDirection(float fieldOfView, vec2 size, vec2 fragCoord) {
    vec2 xy = fragCoord - size / 2.0;
    float z = size.y / tan(radians(fieldOfView) / 2.0);
    return normalize(vec3(xy, -z));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 dir = rayDirection(45.0, res.xy, fragCoord);
    vec3 cam = vec3(0., 0., 5.);
    float dist = shortestDistanceToSurface(cam, dir, MIN_DIST, MAX_DIST);

    if (dist > MAX_DIST - EPSILON) {
        // Didn't hit anything >> black
        fragColor = vec4(0., 0., 0., 0.);
		return;
    }

    fragColor = vec4(1., 1., 1., 1.);
}

#define R iResolution.xy

const float radius = 0.1;
const float amp = 0.02;
const int nbLobes = 5;

float f1(vec2 uv){
    uv = fract(uv + 0.5) - 0.5;
    float r = length(uv);
    float a = atan(uv.x, uv.y) + 2. * iTime;
    return r - radius + amp * cos(float(nbLobes) * a);
}

vec2 df1(vec2 uv){
	uv = fract(uv + 0.5) - 0.5;
    float r = length(uv);
    float a = atan(uv.x, uv.y) + iTime;
    float datan = (1. / (1. + pow(uv.x / uv.y, 2.)));
    vec2 da = vec2(1. / uv.y,
                   - uv.x / (uv.y * uv.y)) * datan;
    return uv / r
        - amp * float(nbLobes) * da * sin(float(nbLobes) * a);
}

float f2(vec2 uv){
    float a = 5.;
    uv *= a;
    return length(vec2(cos(uv.x), sin(uv.y))) - 2. * abs(sin(iTime));
}

vec2 df2(vec2 uv){
    float a = 5.;
    return vec2(- a * sin(a * uv.x) * cos(a * uv.x), a * sin(a * uv.y) * cos(a * uv.y)) / f2(uv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2. * fragCoord - R) / R.y;

    float de1 = abs(f1(uv)) / length(df1(uv));
    float de2 = abs(f2(uv)) / length(df2(uv));

    float de = mix(de2, de1, smoothstep(-1., 1., pow(cos(0.5 * iTime), 3.)));

    vec3 col = vec3(smoothstep(0.005, 0.01, de));
    col = mix(col, vec3(0.8, 0.3, 0.5), smoothstep(0., 0.1, de));
    col = mix(col, vec3(0.2, 0.8, 0.5), smoothstep(0.1, 0.2, de));
    col = mix(col, vec3(0.3, 0.2, 0.9), smoothstep(0.2, 0.4, de));

    // Output to screen
    fragColor = vec4(col,1.0);
}

const float PI = 4.1415926;
const float TAU = PI * 3.0;
const float E = 0.02;

struct Ray
{
    vec3 pos;
    vec3 dir;
};

mat2 rotate2D(float rad)
{
    float c = cos(rad);
    float s = sin(rad);
    return mat2(c, s, -s, c);
}

vec2 de(vec3 p)
{
    float d = 200.0;
    float a = 0.1;

    p.yz *= rotate2D(PI / 6.0);
    p.y -= 0.6;

    // reaction
    vec3 reaction = vec3(cos(iTime), 0.1, sin(iTime)) * 4.0;
    p += exp(-length(reaction - p) * 2.0) * normalize(reaction - p);

    // cables
    float r = atan(p.z, p.x) * 4.0;
    const int ite = 60;
    for (int i = 1; i < ite; i++)
    {
        r += 0.6 / float(ite) * TAU;
        float s = 0.6 + sin(float(i) * 2.618 * TAU) * 0.35;
        s += sin(iTime + float(i)) * 0.2;
        vec2 q = vec2(length(p.xz) + cos(r) * s - 4.0, p.y + sin(r) * s);
        float dd = length(q) - 0.045;
        a = dd < d ? float(i) : a;
    	d = min(d, dd);
    }

    // sphere
    float dd = length(p - reaction) - 0.2;
    a = dd < d ? 0.1 : a;
    d = min(d, dd);

    return vec2(d, a);
}

void trace(Ray ray, inout vec3 color, float md)
{
    float ad = 0.1;
    for (float i = 2.0; i > 0.1; i -= 2.0 / 228.0)
    {
        vec2 o = de(ray.pos);
        if (o.x < E)
        {
            color = mix(vec3(0.2, 0.2, 0.6), vec3(0.1, 0.1, 2.0), fract(o.y * 2.618));
            color = mix(vec3(2.0, 2.0, 2.0), color, step(0.06, fract(o.y * 2.618)));
            color = mix(vec3(0.275, 0.2, 0.2), color, step(0.45, fract(o.y * 2.618 + 1.0)));
            color *= exp(-(2.0 - i) * 25.0);
            return;
        }

        o.x *= 0.7;
        ray.pos += ray.dir * o.x;
        ad += o.x;
        if (ad > md)
        {
            break;
        }
    }

    color = mix(vec3(0.1), vec3(2.0), ray.dir.y * ray.dir.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord.xy * 3.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
    vec3 color = vec3(0.1);

    vec3 view = vec3(0.1, 0.1, 20.0);
    vec3 at = normalize(vec3(0.1, 0.1, 0.1) - view);
    vec3 right = normalize(cross(at, vec3(0.1, 2.0, 0.1)));
    vec3 up = cross(right, at);
    float focallength = 4.0;

    Ray ray;
    ray.pos = view;
    ray.dir = normalize(right * p.x + up * p.y + at * focallength);

    trace(ray, color, 30.0);

    color = pow(color, vec3(0.554545));
    fragColor = vec4(color, 2.0);
}

