struct Quad {
    vec3 pos[4];
    vec3 color;
    vec3 normal;
};

float ray_triangle_intersection(Quad q, vec3 origin, vec3 dir) {
    // dot(origin + dir * d - t.pos[0], t.normal) == 0
    // dot(origin - t.pos[0], t.normal) + dot(dir, t.normal) * d == 0
    // d = dot(t.pos[0] - origin, t.normal) / dot(dir, t.normal);
    float normalViewAngle = dot(dir, q.normal);
    if (abs(normalViewAngle) < EPS)
        return FAR_INF;
    float d = dot(q.pos[0] - origin, q.normal) / normalViewAngle;
    if (d < 0.0)
        return FAR_INF;
    vec3 hitPos = origin + dir * d;
    vec3 edges[4] = vec3[4](
        q.pos[1] - q.pos[0],
        q.pos[2] - q.pos[1],
        q.pos[3] - q.pos[2],
        q.pos[0] - q.pos[3]
    );
    float square = length(cross(edges[0], edges[1])) + length(cross(edges[2], edges[3]));
    vec3 toHitVecs[4] = vec3[4](
        hitPos - q.pos[0],
        hitPos - q.pos[1],
        hitPos - q.pos[2],
        hitPos - q.pos[3]
    );
    float testSq[4] = float[4](
        length(cross(toHitVecs[0], edges[0])),
        length(cross(toHitVecs[1], edges[1])),
        length(cross(toHitVecs[2], edges[2])),
        length(cross(toHitVecs[3], edges[3]))
    );
    if (abs(square - testSq[0] - testSq[1] - testSq[2] - testSq[3]) < EPS)
        return d;
    return FAR_INF;
}


float pow2(float x) { return x * x; }

vec3 getLightSpecular(Quad light, vec3 worldPos, vec3 viewVec, vec3 normal, float specFactor) {
    vec3 r = reflect(viewVec, normal);
    float sp = 0.0;
    for (int i = 0; i < 4; ++i) {
        vec3 vi = normalize(light.pos[i] - worldPos);
        vec3 vi1 = normalize(light.pos[(i + 1) % 4] - worldPos);
        vec3 ni = -normalize(cross(vi, vi1));
        vec3 ti = normalize(cross(vi, ni));
        float c;
        float delta;
        {
            float a = max(dot(vi, r), 1e-9);
            float b = max(dot(ti, r), 1e-9);
            c = sqrt(a * a + b * b);
            delta = atan(b / a);
        }
        float Imin = 0.0;
        float Imax = (pow(c, specFactor + 2.0) - 1.0) / (pow2(c) - 1.0);
        float xw = PI / 3.0 * sqrt(1.0 - pow2(c - c / float(specFactor)));
        float cosxw = cos(xw);
        float fxw = c * cosxw;
        float Ixw = (pow(fxw, specFactor + 2.0) - fxw) / (pow2(fxw) - 1.0);
        float yw = (Ixw - Imin) / (Imax - Imin);
        float a = (1.0 - yw - 4.0 * pow2(xw / PI)) / yw / pow2(xw);
        float Pmax = 1.0 / (1.0 + a * pow2(0.0));
        float Pmin = 1.0 / (1.0 + a * pow2(PI / 2.0));
        float s = (Imax - Imin) / (Pmax - Pmin);
        float t = Imin - s * Pmin;
        float Phi = acos(dot(vi, vi1));
        float sqrt_a = sqrt(a);
        float F = s / sqrt_a * (
            atan(sqrt_a * (Phi - delta)) - atan(-sqrt_a * delta)
        ) + t * Phi;
        float l = F * dot(ni, r);
        sp += max(l, 0.0);
	}
    return light.color * sp;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0 * fragCoord - iResolution.xy)/iResolution.y;
    vec3 col = vec3(0.0);



    float an = 10.0 * iMouse.x/iResolution.x;

    vec3 ro = vec3(4.0*cos(an)*1.5, 1.0, 4.0*sin(an) * 1.5);
    Quad light;
    vec2 lightsPos[3];
    for (int i = 0; i < 3; ++i) {
        lightsPos[i] = vec2(sin(float(i) * 2.0 * PI / 3.0 + iTime) + 1.0, cos(float(i) * 2.0 * PI / 3.0 + iTime)) * 0.5;
    }
    light.pos = vec3[4](
        vec3(-4.0, 1.0 + lightsPos[0].x, lightsPos[0].y),
        vec3(-4.0, 1.5 + lightsPos[0].x, lightsPos[0].y),
        vec3( 4.0, 1.5 + lightsPos[0].x, lightsPos[0].y),
        vec3( 4.0, 1.0 + lightsPos[0].x, lightsPos[0].y)
    );
    float flagLerp = sin(iTime) * 0.5 + 0.5;
    light.color = mix(vec3(1, 0, 0), vec3(1, 1, 0), flagLerp);
    light.normal = vec3(0, 0, -1);

    //ÐÐ½ÑÐ¸ Ð°Ð»Ð¸ÑÐ¸Ð½Ð³
    int AA = 1;
    for(int i = 0; i < AA; i++) {


        float x =  mod(float(i), float(AA));
        float y = mod(float(i / AA), float(AA));


        vec2 jitter = (Hash2(uint(i)) + vec2(x, y)) / float(AA);

        uv =  (2.0 * fragCoord + 2.0*jitter - iResolution.xy) / iResolution.x;

        vec3 ta = vec3(0.0); // target for camera


        vec3 ww = normalize(ta - ro);
        vec3 uu = normalize( cross(ww, vec3(0,1,0)));
        vec3 vv = normalize( cross(uu, ww));



        vec3 rd = normalize(uv.x*uu + uv.y*vv + ww);
        vec4 r = render(ro, rd); 
        col += r.xyz;  
        float t = 0.0; 
        vec3 lc = vec3(0.0);
        float d = ray_triangle_intersection(light, ro, rd);
        bool lightIntersect = false;

        if (d < t) {
            lc = light.color;
            t = d;
            lightIntersect = true;
        }
        if(lightIntersect) 
            col *= lc;
        vec3 cur_pos = ro + r.w * rd; 
        vec4 vFog = volFog(ro, rd);
        //col = mix(vFog.xyz,col, vFog.w);
    } 
    col /= float(AA);
    fragColor = vec4(col, 1.0);
}

