float lenny(vec2 p) { return abs(p.x)+abs(p.y); }

vec3 getDir(vec3 fwd, vec2 uv)
{
    vec3 r = normalize(cross(normalize(fwd), vec3(0.,1.,0.)));
    vec3 u = normalize(cross(r, normalize(fwd)));
    float fov = .8;
    return uv.x*r+uv.y*u+fov*fwd;
}

