#define PI atan(1.)*4.
#define TAU atan(1.)*8.

void lookAt(inout vec3 rd,vec3 ro,vec3 ta,vec3 up){
    vec3 w=normalize(ta-ro),u=normalize(cross(w,up));
    rd=rd.x*u+rd.y*cross(u,w)+rd.z*w;
}

void rot(inout vec3 p,vec3 a,float t){
	a=normalize(a);
	vec3 u=cross(a,p),v=cross(a,u);
	p=u*sin(t)+v*cos(t)+a*dot(a,p);   
}

// https://shadertoy.com/view/ltf3W2
#define sabs(p,k)sqrt(p*p+k)

// https://www.shadertoy.com/view/MsKGzw
// smoothing type
void sfold(inout vec3 p, float n, float k) 
{
    float c=cos(PI/n),s=sqrt(.75-c*c);
    vec3 v=vec3(-.5,-c,s);
    for(int i=0;i<5;i++)
    {
        p.xy=sabs(p.xy,k);
        float g=dot(p,v);
        p-=(g-sabs(g,k))*v;
    }
}

// https://www.shadertoy.com/view/WdfcWr
void sfold(inout vec2 p,float n, float k)
{
    float h=floor(log2(n)),a=TAU*exp2(h)/n;
    for(float i=0.;i<h+2.;i++)    {
	 	vec2 v=vec2(-cos(a),sin(a));
		float g=dot(p,v);
 		p-=(g-sabs(g,k))*v;
 		a*=.5;
    }
}

// p-=2.*min(0.,dot(p,v))*v;
// smooth type
void sfold(inout vec2 p, vec2 v, float k)
{
    float g=dot(p,v);
    p-=(g-sabs(g,k))*v;
}

// if(p.x<p.y)p.xy=p.yx;
// smooth type
void sfold90(inout vec2 p, float k)
{
    vec2 v=normalize(vec2(1,-1));
    float g=dot(p,v);
    p-=(g-sabs(g,k))*v;
}

float de0(vec3 p)
{
    float k=1e-3;
    p.z = sabs(p.z,k);
	sfold(p.xy,5.,k);
    vec3 v = normalize(vec3(2,1,3));
    return dot(p,v)-.6;
}

float de1(vec3 p)
{
    float k=5e-3;
    p=sabs(p,k);
    sfold90(p.xz,k);
	sfold90(p.yz,k);
    vec3 v = normalize(vec3(1,1,-1));
    return dot(p,v)-.7;
}

float de2(vec3 p)
{
    float k=5e-4;
	float n = 4.;
    sfold(p,n,k);
    vec3 v = normalize(vec3(1));
    return dot(p,v)-1.;
}

float de3(vec3 p)
{
    float k=5e-4;
	float n = 5.;
    sfold(p,n,k);
    vec3 v = normalize(vec3(0,1,1));
    return dot(p,v)-1.;
}

float de4(vec3 p)
{
    float k=5e-4;
	float n = 5.;
    sfold(p,n,k);
    vec3 v = normalize(vec3(1));
    return dot(p,v)-1.;
}

float de5(vec3 p)
{
    float k=3e-3;
    p=sabs(p,k);
    sfold90(p.xz,k);
	sfold90(p.yz,k);
    sfold90(p.xy,k);
    vec3 v = normalize(vec3(1,0,1));
    return dot(p,v)-1.;
}

float de6(vec3 p)
{
    float k=1e-3;
    p=sabs(p,k);
    sfold90(p.xz,k);
	sfold90(p.yz,k);
    sfold90(p.xy,k);
    vec3 v = normalize(vec3(2,3,1));
