}

float de7(vec3 p)
{
    float k=3e-3;
    p=sabs(p,k);
    sfold90(p.xz,k);
	sfold90(p.yz,k);
    sfold90(p.xy,k);
    vec3 v = normalize(vec3(1,2,-1));
