float map(in vec3 p)
{
	rot(p,vec3(cos(iTime*.3),sin(iTime*.3),1),iTime*.5);
	switch(int(mod(iTime,8.))) {
    case 0: return de0(p); break;
    case 1: return de1(p); break;
    case 2: return de2(p); break;
    case 3: return de3(p); break;
    case 4: return de4(p); break;
    case 5: return de5(p); break;
    case 6: return de6(p); break;
    case 7: return de7(p); break;
    }
    return 1.;
}

vec3 calcNormal(vec3 p)
{
  vec3 n=vec3(0);
  for(int i=0; i<4; i++){
    vec3 e=.001*(vec3(9>>i&1, i>>1&1, i&1)*2.-1.);
    n+=e*map(p+e);
  }
  return normalize(n);
}

float march(vec3 ro, vec3 rd, float near, float far)
{
    float t=near,d;
    for(int i=0;i<70;i++)
    {
        t+=d=map(ro+rd*t);
        if (d<.001) return t;
        if (t>=far) return far;
    }
    return far;
}

vec3 doColor(vec3 p)
{
    return vec3(.3,.5,.8)+cos(p)*.5+.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy*2.-iResolution.xy)/iResolution.y;
    vec3 ro = vec3(0,0,5);
    vec3 rd = normalize(vec3(uv,2));
    vec3 ta =vec3(0);
    lookAt(rd,ro,ta,vec3(0,1,0));    
    vec3 col= vec3(0);
	const float maxd=50.;
    float t=march(ro,rd,0.,maxd);
    if(t<maxd)
    {
        vec3 p=ro+rd*t;
        col=doColor(p); 
        vec3 n=calcNormal(p);      
		vec3 lightPos=ro+vec3(2,5,2);
    	vec3 li=lightPos-p;
		float len=length(li);
		li/=len;
		float dif=clamp(dot(n,li),0.,1.);
        col*=max(dif,.2);
        float rimd=pow(clamp(1.-dot(reflect(-li,n),-rd),0.,1.),2.5);
		float frn=rimd+2.2*(1.-rimd);
    	col*=frn*.6;
        col*=max(.5+.5*n.y,.3);
        col*=exp2(-2.*pow(max(0.,1.-map(p+n*.8)/.8),2.));
        col+=vec3(.8,.6,.2)*pow(clamp(dot(reflect(rd,n),li),0.,1.),10.);
    }
    fragColor.xyz=col;
}

#define time iTime
#define mod01 floor(mod(time * 2.0, 4.0))
#define mod02 floor(mod(time * 1.0, 4.0))
float gg = 0.0;

mat2 rot(float a)
{
 	float ca = cos(a);
    float sa = sin(a);

    return mat2(ca,sa,-sa, ca);
}

struct matter
{
 	float m;
    int type;
    bool reflected;
};

float box(vec3 p, vec3 s)
{
 	p = abs(p) -s;
    return max(p.x, max(p.y, p.z));
}

float rnd(vec2 a)
{
 	return fract(dot( sin(a * 452.1365 + a.yx * 452.1236), vec2(423.6548)));   
}


float rnd(float a)
{
 	return fract(sin(a * 452.1365) * 423.6548);   
}

float opSmoot(float a, float b, float k)
{
 	float h = clamp(0.5 - 0.5 * (b-a) /k, 0.0, 1.0);

    return mix(b, a, h) + k *h *(1.0-h);
}

void map(inout matter mat, vec3 p)
{
 	float mat01, mat02, mat03;
    vec3 p01 = p, p02 = p, p03 = p, p04 = p, p05 = p;

    p01.xz *= rot(iTime * 0.25);
    p01.yz *= rot(iTime * 0.15);

    mat02 = -box(p01, vec3(2.0 + 0.35 * mod01));

    float rep01 = 0.60;
    float rep02 = 0.20;

    float id = rnd((floor(abs(p.xz) / rep01 - 0.5) - 0.5) * rep01 * 16.2) * 2.0;

    //p03.xz = (fract(abs(p.xz) / rep01 - 0.5) - 0.5) * rep01;
    //p05.yz *= rot(iTime * 0.5);
    p04 = (fract(abs(p05) / rep02 - 0.5) - 0.5) * rep02;
    p04.yz *= rot(iTime * 0.5);
    p04.xz *= rot(iTime * 0.25);

   vec2 scale = vec2(0.15, 0.5) * rot(iTime * 0.2);

    p03.xz = (fract(abs(p.xz) / rep01 - 0.5) - 0.5) * rep01;

    p03.yz *= rot(iTime * 0.35);
    p03.xz *= rot(iTime * 0.05);
    p03.xy *= rot(iTime * 0.25);


    mat03 = box(p03 + vec3(0.02 * mod02 * id, 0.0, -0.02 * mod01 * id), vec3(0.2, 0.2 + 0.10 * mod01 * id, 0.2));
    mat03 = opSmoot(box(p04, vec3(0.05, 0.01 + abs(sin(iTime * 0.15)) * 0.5, 0.05)) , mat03, 0.15);

    mat02 = min(mat02, mat03);

    if(mat02 < 0.01)
    {
     	mat.type= 1;   
    }

    if(mat.reflected)
    {
        p02.xz *= rot(-iTime * 0.25);
        p02.yz *= rot(iTime * 0.55);

        mat01 = box(p02, vec3(0.05, 125.0, 0.05));
     	gg += 0.15/(0.05+abs(mat01));   
    }

    mat.m = mat02;
}

vec3 normals(vec3 p)
{
 	vec2 uv = vec2(0.01, 0.0);

    matter m01, m02, m03, m04;

    map(m01, p);
    map(m02, p - uv.xyy);
    map(m03, p - uv.yxy);
    map(m04, p - uv.yyx);

    return normalize(m01.m - vec3(m02.m, m03.m, m04.m));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv /= vec2(iResolution.y/iResolution.x, 1.0);

    vec3 o = vec3(2.0 * cos(iTime * 0.2), 0.75, 2.0 * sin(iTime * 0.2)), t = vec3(0.0);
    vec3 fr = normalize(t-o);
    vec3 ri = normalize(cross(vec3(0.0, 1.0, 0.0), fr));
    vec3 up = normalize(cross(fr, ri));
    vec3 dir = normalize(fr + uv.x * ri + uv.y * up);
    vec3 p = o + dir * 0.75;

    matter mat;
    float d;



    // Time varying pixel color
    vec3 col = vec3(0.0);

    for(int i = 0; i < 150; ++i)
    {
     	map(mat, p);

        col += gg * 0.00025 * vec3(1.0, 0.65, 0.75);

        if(mat.m < 0.01)
        {
         	if(mat.type == 1)
            {
             vec3 n = normals(p);
                dir = reflect(dir, -n);
                mat.m = 0.25;
                mat.type = 0;
                mat.reflected = true;

            }
        }



        p += mat.m * dir * 0.55;
        d += mat.m * 0.55;
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

