/////////////////////////////////////////////////////////////////////////////

/////////////////////////////////////////////////////////////////////////////
// shader specific controls

// When true, the filter is picked based on mouse.x and the orientation by mouse.y
bool DEMO_MODE = true; // toggled with space key

bool CAMERA_MOUSE = false;

/////////////////////////////////////////////////////////////////////////////
// ray marching values

#define RAY_MARCH_STEPS    99
#define RAY_MARCH_TOO_FAR  float( RAY_MARCH_STEPS )
#define RAY_MARCH_CLOSE    0.004
#define RAY_EPSILON        vec2( RAY_MARCH_CLOSE, .0 )

/////////////////////////////////////////////////////////////////////////////
// anti-aliasing

#define ANTI_ALIAS_STEPS    3
#define ANTI_ALIAS_DISTANCE 2.

/////////////////////////////////////////////////////////////////////////////
// useful constants and macros

#define PI2             6.283185307179586
#define TRIG(d, a)      ( d * vec2( cos( a ), sin( a ) ) )
#define RGB(r,g,b)      vec3( float(r)/255., float(g)/255., float(b)/255. )
#define MAX3(v)         max( v.x, max( v.y, v.z ) ) 
#define EQUALS(a,b)     step( a-1e-11, b ) * step( b-1e-11, a )

/////////////////////////////////////////////////////////////////////////////
// fun with masking

#define SCALE 1.

float mesh( vec2 p ) {
    p = fract( SCALE * p );
    return smoothstep( .0, .1,  p.x * p.y );
}

float tooth( vec2 p ) {
    p = fract( SCALE * p );
    return smoothstep( .9, .4, min(p.x,p.y) / max(p.x,p.y) );
}

float check( in vec2 p ) {
    vec2 s = sign(fract(p*.5)-.5);
    return .5 - .5*s.x*s.y;
}

float triangle( in vec2 p ) {
    p = fract( SCALE * p );
    return p.x + p.y;
}

float seed( in vec2 p ) {
    p = fract( SCALE * p );
    return abs( p.y - p.x );
}

float dark( in vec2 p ) {
    p = fract( SCALE * p );
    return p.y - p.x;
}

float emboss( in vec2 p ) {
    p = fract( SCALE * p );
    return ( cos( p.x ) + sin( p.y ) +2. ) / 4.;
}

float sechs( in vec2 p ) {
    p = fract( SCALE * p );
    float f = abs( cos( p.x - p.y ) );
    return smoothstep( .88, .89, f );
}

float linx( in vec2 p ) {
    p = fract( SCALE * p );
    float f = ( cos( p.x - p.y ) ) * .91;
    return smoothstep( .79, .80, f ) - smoothstep( .82, .83, f );
}

float dashes( in vec2 p ) {
    p = fract( SCALE * p );
    float f = ( cos( p.x - p.y ) );
    return smoothstep( .95, .96, f ) - smoothstep( .97, .98,  f );
}

float ells( in vec2 p ) {
    p = fract( SCALE * p );
    float f = ( cos( max(p.x,p.y) ) );
    return smoothstep( .5, .61, f ) - smoothstep( .7, .71, f );
}

float grid( in vec2 p ) {
    p = fract( SCALE * p );
    float f = abs( max(p.x,p.y ) );
    return smoothstep( .92, .93, f );
}

float idk( in vec2 p ) {
    p = fract( SCALE * p );
    float f = ( cos( max(p.x,p.y) ) );
    return smoothstep( .5, .71, f ) - smoothstep( .96, .951, f );
    return smoothstep( .5, .66, f );
}
//

float mask( in vec3 point, in vec2 control ) {
    //return idk( point.xz );

    float orientation = floor( control.y * 8. );

    vec2 oz = point.xz;
    oz += point.xx * EQUALS( 0., orientation );
    oz += point.xy * EQUALS( 1., orientation );
    oz += point.xz * EQUALS( 2., orientation );

    oz += point.yy * EQUALS( 3., orientation );
    oz += point.yx * EQUALS( 4., orientation );
    oz += point.yz * EQUALS( 5., orientation );   

    oz += point.zz * EQUALS( 6., orientation );
    oz += point.zx * EQUALS( 7., orientation );
    oz += point.zy * EQUALS( 8., orientation );   

    float which = floor( control.x * 11. );

    float masked = .0;

#if 1
    masked += mesh( oz )     * EQUALS( 0., which );
    masked += tooth( oz )    * EQUALS( 1., which );
    masked += check( oz )    * EQUALS( 2., which );
    masked += triangle( oz ) * EQUALS( 3., which );
    masked += seed( oz )     * EQUALS( 4., which );
    masked += dark( oz )     * EQUALS( 5., which );
    masked += emboss( oz )   * EQUALS( 6., which );
    masked += linx( oz )     * EQUALS( 7., which );
    masked += dashes( oz )   * EQUALS( 8., which );
    masked += ells( oz )     * EQUALS( 9., which );
    masked += sechs( oz )    * EQUALS( 10., which );
    masked += grid( oz )     * EQUALS( 11., which );
#else
    switch( int( which ) ) {
        case 0:    masked = mesh( oz )     ; break;
        case 1:    masked = tooth( oz )    ; break;
        case 2:    masked = check( oz )    ; break;
        case 3:    masked = triangle( oz ) ; break;
        case 4:    masked = seed( oz )     ; break;
        case 5:    masked = dark( oz )     ; break;
        case 6:    masked = emboss( oz )   ; break;
        case 7:    masked = linx( oz )     ; break;
        case 8:    masked = dashes( oz )   ; break;
        case 9:    masked = ells( oz )     ; break;
        case 10:   masked = sechs( oz )    ; break;
        case 11:   masked = grid( oz )     ; break;
     }
#endif

    return clamp( masked, .0, 1. ) + .1; // avoid total darkness
}

float mask( in vec3 point ) {
    return DEMO_MODE 
    ? mask( point, .5 + TRIG( .5, iTime * .05 ) )
    : mask( point, iMouse.xy / iResolution.xy );
}

/////////////////////////////////////////////////////////////////////////////
// signed distance functions for types

float sdSphere( in vec3 point, in float radius ) {
    return length( point ) - radius;
}

float sdBox( in vec3 point, in vec3 size ) {
    return MAX3( ( abs( point ) - size ) );
}

/////////////////////////////////////////////////////////////////////////////
// create the scene

#define BALL   1.
#define BOX    2.
#define BACK   3.
#define FRONT  4.

#define TOP    5.
#define FLOOR  6.
#define LEFT   7.
#define RIGHT  8.

vec2 which( in vec2 current, in float test, in float what ) {
    current.y = mix( current.y, what, step( test, current.x ) );
    current.x = min( current.x, test );
    return current;
}

float box( in vec3 point ) {
    return sdBox( point - vec3( 3., .1, 3.) , vec3( 1. ) );
}

float boxm( in vec3 point, float m ) {
    return sdBox( point - vec3( 3., .1, -3. ), vec3( 1. ) ) - m;
}

float ball( in vec3 point ) {
    return sdSphere( point + vec3( 3., -.5, 3. ), 1.33 );
}

float ballm( in vec3 point, float m ) {
    return sdSphere( point + vec3( 3., -.5, -3. ), 1.33 ) - m;
}

vec2 sceneDistance( in vec3 point ) {
    float w = 8.;
    float h = w * 2. / 3.;

    float m = mask( point ) * .33; 

    vec2 closest = vec2( RAY_MARCH_TOO_FAR );

    closest = which( closest, box( point ),  BOX );
    closest = which( closest, ball( point ), BALL );

    closest = which( closest, boxm( point, m ),  BOX );
    closest = which( closest, ballm( point, m ), BALL );

    closest = which( closest, point.z + w,   BACK );
    closest = which( closest, w - point.z,   FRONT );

    closest = which( closest, h - point.y,   TOP );
    closest = which( closest, point.y +1.,  FLOOR );

    closest = which( closest, point.x + w,   LEFT );
    closest = which( closest, w - point.x,   RIGHT );

    return closest;
}


vec3 colorAt( in vec3 point, vec2 d ) {
    vec3 color = vec3( .0 );

    color += RGB( 255,  33,  33 ) * EQUALS( d.y, BALL   );
    color += RGB(  33, 255,  33 ) * EQUALS( d.y, BOX    );
    color += RGB(  33,  33, 255 ) * EQUALS( d.y, BACK   );

    color += RGB( 255, 255,  33 ) * EQUALS( d.y, FRONT  );
    color += RGB(  33, 255, 255 ) * EQUALS( d.y, TOP    );
    color += RGB( 255,  33, 255 ) * EQUALS( d.y, FLOOR  );

    color += RGB( 255, 210, 120 ) * EQUALS( d.y, LEFT   );
    color += RGB( 120, 210, 255 ) * EQUALS( d.y, RIGHT  );

    color *= mask( point );

    return color;
}

/////////////////////////////////////////////////////////////////////////////
// the ray marching bits

#define THANKS_SHANE_FOR_THE_RAY_SHORTENING_SUGGESTION
vec2 rayMarch( in vec3 origin, in vec3 direction ) {
    vec2 total = vec2(.0);
    for ( int i = 0 ; i < RAY_MARCH_STEPS ; i++ ) {
        vec3 point = origin + direction * total.x;        

        vec2 current = sceneDistance( point );

#ifdef THANKS_SHANE_FOR_THE_RAY_SHORTENING_SUGGESTION
        if ( total.x > RAY_MARCH_TOO_FAR || current.x < RAY_MARCH_CLOSE ) {
            break;
        }
        // Note: Ray advancement occurs after checking for a surface hit.
        //
        // Ray shortening: Shorter for the first few iterations.
        total.x += i<32? current.x*.35 : current.x*.85; 
        total.y = current.y;
#else
        total.x += current.x;
        total.y = current.y;

        if ( total.x > RAY_MARCH_TOO_FAR || current.x < RAY_MARCH_CLOSE ) {
            break;
        }     
#endif
    }
    return total;
}

vec3 sceneNormal( in vec3 point ) {
    return normalize( sceneDistance( point ).x - vec3(
        sceneDistance( point - RAY_EPSILON.xyy ).x,
        sceneDistance( point - RAY_EPSILON.yxy ).x,
        sceneDistance( point - RAY_EPSILON.yyx ).x
    ));
}

/////////////////////////////////////////////////////////////////////////////
// lighting

float pointLight( vec3 point, vec4 light ) {
    vec3 normal = sceneNormal( point );

    vec3 towardLight = light.xyz - point;
    float toLight = length( towardLight );
    towardLight = normalize( light.xyz - point );

    float diffuse = clamp( dot( normal, towardLight ), 0., 1. );

    vec3 lightStart = point + normal * RAY_MARCH_CLOSE * 2.;
    float d = rayMarch( lightStart, towardLight ).x;
    diffuse *= 1. - 0.5 * smoothstep( d * 0.9, d, toLight );

    float lightStrength = .7 + .3 * light.w / dot( toLight, toLight );  
    return diffuse * lightStrength;
}

vec3 colorPoint( vec3 point, vec2 d ) {
    vec2 T = TRIG(2.,iTime);
    vec4 light    = vec4( T.x, 3., T.y, 3. );
    float ambient = 0.307;
    float gamma   = 1.33;

    float lighting = pointLight( point, light );
    lighting = ( 1. -  ambient ) * gamma * lighting;

    vec3 color = colorAt( point, d );
	return vec3( color * ambient + color * lighting );
}

/////////////////////////////////////////////////////////////////////////////
// simple camera

struct Ray {
    vec3 eye;
    vec3 direction;
};

// from https://github.com/glslify/glsl-look-at
mat3 makeCamera( vec3 origin, vec3 target, float roll ) {
	vec3 up = vec3( sin( roll ), cos( roll ), .0 );
	vec3 zz = normalize( target - origin );
	vec3 xx = normalize( cross( zz, up ) );
	vec3 yy = normalize( cross( xx, zz ) );
	return mat3( xx, yy, zz );
}

Ray cameraRay( in vec2 uv ) {
    vec3 eye, look;
    float zoom, roll;
    if ( CAMERA_MOUSE ) {
        vec2 mx = ( iMouse.xy / iResolution.xy ) -.5;
        vec2 T = TRIG( 4., mx.x * PI2 );

        eye  = vec3( T.x, mx.y * 6. + 1. , T.y );
        look = vec3( 0. );

        zoom = 1.;
        roll = .0;
    } else {
        vec2 T = TRIG( 1., iTime * .1 );

        eye  = vec3( 6. * T.x, 2., 6. * T.y );
        look = vec3( .0, 0, .0 );

        roll = 0.2 * T.x;
        zoom = 1. + .25 * abs( sin( iTime * .66 ) );
    }

	mat3 camera = makeCamera( eye, look, roll );
    vec3 direction = normalize( camera * vec3( uv.xy, zoom ) );

    return Ray( eye, direction );
}

// from https://www.shadertoy.com/view/4dsGRl
// key is javascript keycode: http://www.webonweboff.com/tips/js/event_key_codes.aspx
bool ReadKey( int key, bool toggle )
{
	float keyVal = textureLod( iChannel0, vec2( (float(key)+.5)/256.0, toggle?.75:.25), 0.0 ).x;
	return (keyVal>.5)?true:false;
}

/////////////////////////////////////////////////////////////////////////////
// main function

vec3 mainly( in vec2 fragCoord ) {
	//vec2 uv = ( 2. * gl_FragCoord.xy - iResolution.xy ) / iResolution.y;
    vec2 uv = ( 2. * fragCoord - iResolution.xy ) / iResolution.y;


    Ray ray = cameraRay( uv );
    vec2 d = rayMarch( ray.eye, ray.direction );
    float missed = step( RAY_MARCH_TOO_FAR, d.x );

    vec3 point = ray.eye + ray.direction * d.x;

    vec3 background = vec3( 3.6 );
    vec3 color = colorPoint( point, d );

    return mix( color, background, missed );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	DEMO_MODE = ReadKey( 32, true );
    CAMERA_MOUSE = ReadKey( 13, true );


#if ANTI_ALIAS_STEPS == 1
    fragColor = vec4( mainly( fragCoord ) , 1. );
#else
    vec3 color = vec3( .0 );
    float a = ANTI_ALIAS_DISTANCE / float( ANTI_ALIAS_STEPS );

    for ( int y = 0 ; y < ANTI_ALIAS_STEPS ; y++ ) {
        float ya = float( y ) * a; 
        for ( int x = 0 ; x < ANTI_ALIAS_STEPS ; x++ ) {
            vec2 aa = vec2( float( x ) * a, ya );          
            color += mainly( fragCoord + aa );
        }
    }
    fragColor = vec4( color / float( ANTI_ALIAS_STEPS * ANTI_ALIAS_STEPS ), 1. );

#endif
}

