const float pi = 3.1415927, sqrt3 = 1.7320508;

#define VAR_ZERO min (iFrame, 0)

#define DMIN(id) if (d < dMin) { dMin = d;  idObj = id; }

float ObjDf (vec3 p)
{
  vec3 q;
  vec2 b, c;
  float dMin, d, hBase, aLoop, lb, s, rc;
  dMin = dstFar;
  hBase = 1.;
  aLoop = 0.25 * pi / sLoop;
  rc = 0.01;
  q = p;
  q.y -= 2. * hBase;
  q.xz = Rot2D (q.xz, tubRot);
  q.xy = Rot2D (q.xy, 0.5 * pi);
  d = max (PrRoundBoxDf (vec3 (q.x, Rot2D (q.yz, - tubRot)), vec3 (rCyl - rc), rc),
     -0.01 - q.x);
  q.xy = Rot2D (vec2 (q.x, q.y - dLoop), aLoop - 0.5 * pi);
  b = vec2 (length (q.xy) - dLoop, q.z);
  b.xy = Rot2D (b.xy, tubRot);
  lb = length (b);
  s = PrRoundBox2Df (b, vec2 (rCyl - rc), rc);
  d = min (d, max (s, dot (vec2 (q.x, abs (q.y)), sin (aLoop + vec2 (0., 0.5 * pi)))));
  c = Rot2D (q.xy, aLoop) + vec2 (dLoop, 0.);
  s = max (PrRoundBoxDf (vec3 (Rot2D (vec2 (c.x, q.z), - tubRot), c.y).xzy,
     vec3 (rCyl - rc), rc), -0.01 - c.y);
  d = min (d, s);
  qHit = vec3 (vec2 (atan (q.y, - q.x) * dLoop / (0.25 * pi), atan (b.x, b.y)) / pi, lb);
  DMIN (1);
  q = p;
  q.y -= hBase - 0.5 * rCyl;
  d = PrRoundBoxDf (q, vec3 (0.8 * rCyl, hBase - 0.5 * rCyl, 0.8 * rCyl) - 0.05, 0.05);
  DMIN (2);
  return 0.8 * dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  vec3 p;
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 150; j ++) {
    p = ro + dHit * rd;
    d = ObjDf (p);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.005, -0.005);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd, float dMax)
{
  float sh, d, h;
  sh = 1.;
  d = 0.1;
  for (int j = VAR_ZERO; j < 30; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += h;
    if (sh < 0.05 || d > dMax) break;
  }
  return 0.5 + 0.5 * sh;
}

vec4 InCylHit (vec3 ro, vec3 rd)
{
  vec3 s, vn;
  float d, a, b, w, srdy;
  d = dstFar;
  vn = vec3 (0.);
  a = dot (rd.xz, rd.xz);
  b = dot (rd.xz, ro.xz);
  w = b * b - a * (dot (ro.xz, ro.xz) - bCylRad * bCylRad);
  if (w > 0.) {
    srdy = sign (rd.y);
    if (a > 0.) {
      d = (- b + sqrt (w)) / a;
      s = ro + d * rd;
    } else s.y = bCylHt;
    if (abs (s.y) < bCylHt) vn.xz = - s.xz / bCylRad;
    else {
      d = (- srdy * ro.y + bCylHt) / abs (rd.y);
      vn.y = - srdy;
    }
  }
  return vec4 (d, vn);
}

vec3 ShStagGrid (vec2 p)
{
  vec2 q, sq, ss;
  q = p;
  if (2. * floor (0.5 * floor (q.y)) != floor (q.y)) q.x += 0.5;
  sq = smoothstep (0.05, 0.1, abs (fract (q + 0.5) - 0.5));
  q = fract (q) - 0.5;
  ss = 0.5 * smoothstep (0.3, 0.5, abs (q.xy)) * sign (q.xy);
  if (abs (q.x) < abs (q.y)) ss.x = 0.;
  else ss.y = 0.;
  return vec3 (ss.x, 0.8 + 0.2 * sq.x * sq.y, ss.y);
}

vec3 BgCol (vec3 ro, vec3 rd)
{
  vec4 vc, db4;
  vec3 ltDir, vn, col, rg, vnCyl;
  vec2 qw, wSize;
  float dCyl, hy, y, b, a, f, ga, ltDist, sh;
  bool isLit;
  isLit = true;
  db4 = InCylHit (ro + vec3 (0., - bCylHt, 0.), rd);
  dCyl = db4.x;
  vnCyl = db4.yzw;
  if (vnCyl.y == 0.) {
    ro += dCyl * rd;
    vn = vnCyl;
    hy = mod (ro.y / bCylHt + 0.5, 1.) - 0.5;
    a = atan (vn.x, - vn.z) / pi;
    wSize = vec2 (0.28, 0.33);
    qw = abs (vec2 (mod (32. * 0.5 * (1. + a) + 0.5, 1.), abs (hy)) - 0.5) - 0.5 * wSize;
    if (Maxv2 (abs (qw) - wSize * vec2 (0.43, 0.47)) < 0.) {
      col = vec3 (0.1, 0.2, 0.5);
      isLit = false;
    } else {
      col = vec3 (0.4);
      if (Maxv2 (qw - 0.5 * wSize) > 0.) {
        y = abs (abs (hy) - 0.5) - 0.5;
        if (y > - 0.3/16.) {
          col = vec3 (0.65);
          vn.xz = Rot2D (vn.xz, - pi * a);
          vn.yz = Rot2D (vn.yz, 0.15 * pi * (1. - 2. * SmoothBump (0.15, 0.25, 0.05,
             16. * (0.5 - y))) * sign (hy));
          vn.xz = Rot2D (vn.xz, pi * a);
        } else {
          col = vec3 (0.7, 0.75, 0.7);
          qw = 8. * vec2 (12. * a, 2. * hy);
          rg = ShStagGrid (qw);
          col *= rg.y * (1. - 0.2 * Noisefv2 (32. * qw));
          rg.xz *= sign ((abs (vn.x) > abs (vn.z)) ? vn.x : vn.z);
          if (abs (vn.x) > abs (vn.z)) {
            if (rg.x == 0.) vn.xy = Rot2D (vn.xy, rg.z);
            else vn.xz = Rot2D (vn.xz, rg.x);
          } else {
            if (rg.x == 0.) vn.zy = Rot2D (vn.zy, rg.z);
            else vn.zx = Rot2D (vn.zx, rg.x);
          }
        }
      }
    }
  } else if (vnCyl.y > 0.) {
    ro += dCyl * rd;
    vc = HexVor (0.7 * ro.zx);
    b = 1. - smoothstep (-0.2, -0.05, rd.y) * smoothstep (0.4, 0.8, dCyl / (1.6 * bCylRad));
    vn = mix (vec3 (0., 1., 0.), normalize (vec3 (- vc.y, 4., - vc.z)),
       b * SmoothBump (0.05, 0.13, 0.02, vc.x));
    col = vec3 (0.65, 0.62, 0.6) * (0.7 + 0.3 * b * step (0.06 + 0.03 * vc.w, vc.x) *
       (1. - 0.3 * Noisefv2 (16. * ro.xz)));
  } else {
    ro += dCyl * rd;
    f = 0.;
    qw = ro.xz / bCylRad;
    ga = 2.39996;
    for (float n = 0.; n < 128.; n ++) f += 1. - smoothstep (0.02, 0.025,
       length (qw - sqrt (n / 128.) * sin (n * ga + vec2 (0.5 * pi, 0.))));
    col = mix (vec3 (0.2, 0.2, 0.4), vec3 (1., 1., 0.7), f);
    isLit = false;
  }
  if (isLit) {
    ltDir = ltPos - ro;
    ltDist = length (ltDir);
    ltDir /= ltDist;
    sh = (vnCyl.y > -0.5) ? ObjSShadow (ro, ltDir, ltDist) : 1.;
    col *= 0.3 + 0.7 * sh * max (dot (vn, ltDir), 0.);
  }
  return col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 col, vn, ltDir;
  float dstObj, tCyc, t, sh, ltDist;
  bool doRefl;
  tCyc = 20.;
  t = tCur / tCyc;
  tubRot = mod (5. * t, 2. * pi);
  sLoop = 1./2. + 20. * pow (1. - SmoothBump (0.25, 0.75, 0.24, mod (t, 1.)), 8.);
  dLoop = 6. * sLoop;
  rCyl = 1.5;
  HexVorInit ();
  doRefl = false;
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar && idObj == 1) {
    ro += dstObj * rd;
    doRefl = true;
    rd = reflect (rd, ObjNf (ro));
    ro += 0.01 * rd;
    dstObj = ObjRay (ro, rd);
  }
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    if (idObj == 1) col = vec3 (1.);
    else if (idObj == 2) col = vec3 (0.5);
    ltDir = ltPos - ro;
    ltDist = length (ltDir);
    ltDir /= ltDist;
    sh = ObjSShadow (ro, ltDir, ltDist);
    col = col * (0.3 + 0.7 * sh * max (dot (vn, ltDir), 0.));
  } else col = BgCol (ro, rd);
  if (doRefl) col = mix (col, 0.9 * BgCol (ro, rd), 0.9);
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv;
  float el, az, zmFac, t, tSmooth, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  el = -0.05 * pi;
  az = 0.;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += 0.3 * pi * mPtr.y;
  } else {
    t = mod (0.005 * tCur, 2.);
    tSmooth = (floor (32. * t) + smoothstep (0.9, 1., mod (32. * t, 1.))) / 32.;
    az = 2.5 * pi * (0.5 - abs (tSmooth - 1.));
  }
  el = clamp (el, -0.2 * pi, -0.02 * pi);
  vuMat = StdVuMat (el, az);
  bCylRad = 32.;
  bCylHt = 12.;
  ro = vuMat * vec3 (0., 0., -0.95 * bCylRad);
  ro.y += 0.5 * bCylHt;
  zmFac = 3.5;
  dstFar = 3. * bCylRad;
  ltPos = vec3 (0., 1.5 * bCylHt, 0.9 * bCylRad);
  ltPos.xz = Rot2D (ltPos.xz, -0.2 * pi);
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

float PrRoundBoxDf (vec3 p, vec3 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrRoundBox2Df (vec2 p, vec2 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

vec2 PixToHex (vec2 p)
{
  vec3 c, r, dr;
  c.xz = vec2 ((1./sqrt3) * p.x - (1./3.) * p.y, (2./3.) * p.y);
  c.y = - c.x - c.z;
  r = floor (c + 0.5);
  dr = abs (r - c);
  r -= step (dr.yzx, dr) * step (dr.zxy, dr) * dot (r, vec3 (1.));
  return r.xz;
}

vec2 HexToPix (vec2 h)
{
  return vec2 (sqrt3 * (h.x + 0.5 * h.y), (3./2.) * h.y);
}

vec2 gVec[7], hVec[7];

void HexVorInit ()
{
  vec3 e = vec3 (1., 0., -1.);
  gVec[0] = e.yy;
  gVec[1] = e.xy;
  gVec[2] = e.yx;
  gVec[3] = e.xz;
  gVec[4] = e.zy;
  gVec[5] = e.yz;
  gVec[6] = e.zx;
  for (int k = 0; k < 7; k ++) hVec[k] = HexToPix (gVec[k]);
}

vec4 HexVor (vec2 p)
{
  vec4 sd, udm;
  vec2 ip, fp, d, u;
  float amp, a;
  amp = 0.7;
  ip = PixToHex (p);
  fp = p - HexToPix (ip);
  sd = vec4 (4.);
  udm = vec4 (4.);
  for (int k = 0; k < 7; k ++) {
    u = Hashv2v2 (ip + gVec[k]);
    a = 2. * pi * (u.y - 0.5);
    d = hVec[k] + amp * (0.4 + 0.6 * u.x) * vec2 (cos (a), sin (a)) - fp;
    sd.w = dot (d, d);
    if (sd.w < sd.x) {
      sd = sd.wxyw;
      udm = vec4 (d, u);
    } else sd = (sd.w < sd.y) ? sd.xwyw : ((sd.w < sd.z) ? sd.xyww : sd);
  }
  sd.xyz = sqrt (sd.xyz);
  return vec4 (SmoothMin (sd.y, sd.z, 0.3) - sd.x, udm.xy, Hashfv2 (udm.zw));
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

float Maxv2 (vec2 p)
{
  return max (p.x, p.y);
}

float Maxv3 (vec3 p)
{
  return max (p.x, max (p.y, p.z));
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b, a, h) - r * h * (1. - h);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

const float cHashM = 43758.54;

float Hashfv2 (vec2 p)
{
  return fract (sin (dot (p, vec2 (37., 39.))) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

float log10(float n){
return log(n)/log(10.);
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float PI = 3.14159263;
    float freq;
    float dist;
    float t;
    int numEl;
    float phaseDif;

    freq = 100.; //busyness of screen
    t = iTime*0.1*60.; //a timing constant
    dist =PI/freq; //inter-element distance. Set to PI/freq 
    //for max directivity without additional grating lobes
    numEl = 5; //number of radiators
    phaseDif = radians(0.); //set this to double the desired beam angle


    float col = 0.; //color variable

    for(int i = 0; i<numEl; i++){
    uv = fragCoord/iResolution.xy; //reset coordinate plane
    uv.y-=0.5; //move up on the screen
    uv.x-=0.5-dist*(float(i)+1.)+float(numEl)*dist/2.; 
    col+=(5./float(numEl))*cos(freq*length(uv)-t-phaseDif*float(i));

    }
    col=abs(col);
    uv = fragCoord/iResolution.xy;
    uv-=0.5;
    uv.x+=0.0053*(300./freq); //idk why I need this offset, but without it the pattern is very slightly assymetrical
    col=log10(col/(20.*pow(length(uv), 2.)));//magnitude should 
    //fall off as distance squared. Then lake the logarithm for display purposes


    vec3 c = vec3(col*0.4, 0.997, col*100.); //the constants here were found by experimentation
    //play around with them if you want different colors.
    c = hsv2rgb(c);
    fragColor = vec4(c,1.0);
}

vec4 CalculateOceanPlaneColor(vec4 baseColor, float yWorld, float yBackWorld, float yMidWorld, float yFrontWorld, float backPlaneToggle)
{
    #define BorderWidthHalf 0.1

    // Enlarge borders for steep vertical edges and for world dimensions
    float worldDimensionsAdjustment = abs(dFdy(yWorld)) * 1.5;
    float borderWidthHalfBack = BorderWidthHalf + abs(dFdx(yBackWorld)) + worldDimensionsAdjustment;
    float borderWidthHalfMid = BorderWidthHalf + abs(dFdx(yMidWorld)) + worldDimensionsAdjustment;
    float borderWidthHalfFront = BorderWidthHalf + abs(dFdx(yFrontWorld)) + worldDimensionsAdjustment;

    // Color

    // 1 when yWorld is below middle of the border, 0 otherwise
    float baseColorBack = step(yWorld, yBackWorld - borderWidthHalfBack);
    float baseColorMid = step(yWorld, yMidWorld - borderWidthHalfMid);
    float baseColorFront = step(yWorld, yFrontWorld - borderWidthHalfFront);

    float baseColorAlpha =
        max(
            1. * baseColorFront,
            max(
                .6 * baseColorMid * backPlaneToggle,
                .5 * baseColorBack * backPlaneToggle));

    //return vec4(baseColor, baseColorAlpha);

    // Border

    float borderBack =
        1. - smoothstep(0., borderWidthHalfBack, abs(yWorld - (yBackWorld - borderWidthHalfBack)));

    float borderMid =
        1. - smoothstep(0., borderWidthHalfMid, abs(yWorld - (yMidWorld - borderWidthHalfMid)));

    float borderFront =
        1. - smoothstep(0., borderWidthHalfFront, abs(yWorld - (yFrontWorld - borderWidthHalfFront)));

    // epsilon when front is visible, 1 otherwise
    #define FrontObscuredAlpha 0.4
    float borderFrontOpaqueness = (1. - baseColorFront) * (1. - FrontObscuredAlpha) + FrontObscuredAlpha;

    // epsilon when mid is visible, 1 otherwise
    #define MidObscuredAlpha 0.4
    float borderMidOpaqueness = (1. - baseColorMid) * (1. - MidObscuredAlpha) + MidObscuredAlpha;

    float borderAlpha =
        max(
            1. * borderFront,
            max(
                .9 * borderMid * borderFrontOpaqueness * backPlaneToggle,
                .7 * borderBack * borderFrontOpaqueness * borderMidOpaqueness * backPlaneToggle));

    //return vec4(1., 1., 1., borderAlpha);

    // Combine

    vec4 finalColor = mix(
        vec4(baseColor.xyz, baseColorAlpha * baseColor.w),
        vec4(1.), // Border color
        borderAlpha);

    return finalColor;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // This prolog is a simulation of the vertex shader that we use in Floating Sandbox.
    // The bulk of this shader is really the 'CalculateOceanPlaneColor' function above.

    vec4 backgroundColor = vec4(135./255., 206./255., 250./255., 1.);

    vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2. - vec2(1.); // (x=[-1.0, 1.0], y=[-1.0, 1.0])    

    #define WorldMult 10.
    vec2 worldCoords = uv * vec2(WorldMult, WorldMult);

    float amplitude = (0.3 + 0.1 * sin(iTime) * 8.) * WorldMult;

    float yBackWorld = amplitude * .24 * sin(worldCoords.x * 4. / WorldMult + float(iTime) * 2. - .15);   
    float yMidWorld = amplitude * .3 * sin(worldCoords.x * 4. / WorldMult + float(iTime) * 2.);
    float yFrontWorld = amplitude * .27 * sin(worldCoords.x * 4. / WorldMult + float(iTime) * 2. + .2);

    float backPlaneToggleParam = 1.; // step(1., mod(iTime * 2., 2.));

    //////////////////////////////////////////////////////////////////

    // Sample texture, anchoring textureY=0.0 at mid
    vec2 textureCoords = vec2(worldCoords.x, yMidWorld - worldCoords.y);
    vec4 baseColor = texture(iChannel0, textureCoords);
    // TODO: Comment below to use texture
    baseColor = vec4(92./255., 237./255., 237./255., 1.);

    vec4 color = CalculateOceanPlaneColor(baseColor, worldCoords.y, yBackWorld, yMidWorld, yFrontWorld, backPlaneToggleParam);

    //
    // Output to screen
    //

    fragColor = mix(
        backgroundColor,
        color,
        color.w);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(1.0, 1.0, 1.0);
    vec2 uv = fragCoord/iResolution.xy;
    int block = int(9.0f * iTime) % 24 - 12;
    if (block == 0)
    {
        block = 1;
    }
    block = block * 10;
    int result = int((fragCoord.x - iResolution.x / 2.0) * (fragCoord.y - iResolution.y / 2.0)) / block;
    if( result % 3  == 0)
    { 
      col = (0.5 + 0.5*cos(iTime+uv.xyx+vec3(1,3,5)));
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

vec2 rotate (vec2 _st, float _angle) {
    _st =  mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle)) * _st;
    return _st;
}

float sdCircle(in vec2 _st, in float _radius)
{
    return length(_st) - _radius;
}

float boxDist(vec2 p, vec2 size, float radius)
{
  	vec2 d = abs(p) - size - radius;
  	return min(max(d.x, d.y), 0.0) + length(max(d, 0.0)) - radius;
}

// 2D Random
float random (in vec2 st) { return fract(sin(dot(st.xy, vec2(12.9898,78.233))) * 43758.5453123); }

// https://thebookofshaders.com/edit.php#12/vorono-01.frag
float voroni(in vec2 _st, in float _offset)
{
    vec2 i_st = floor(_st);
    vec2 f_st = fract(_st);

    float m_dist = 150.;  // minimun distance
    vec2 m_point;        // minimum point

    for (int j=-1; j<=1; j++ ) {
        for (int i=-1; i<=1; i++ ) {
            vec2 neighbor = vec2(float(i),float(j));
            vec2 point = vec2(random(i_st + neighbor ));
            point = 0.5 + 0.5*sin(_offset + 6.2831*point);
            vec2 diff = neighbor + point - f_st;
            float dist = length(diff);

            if( dist < m_dist ) {
                m_dist = dist;
                m_point = point;
            }
        }
    }

    return m_dist;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 4.0 * fragCoord.xy/iResolution.x - vec2(2.0, 1.1);
    vec3 col = vec3(0.0, 0.0, 0.545);

    col = mix(col, vec3(0.0), uv.y / 2.0 + 0.4);

    float lanternDF = 0.0;
    float lanternShadowDF = 0.0;

    for(float i = 0.0; i < 20.0; i++)
    {
        float offsetX = voroni(vec2(i ), 0.0);
        float offsetY = voroni(vec2(i ), 0.0);

        vec2 offsetVec = vec2(offsetX * i - 2.6, offsetY  + sin(iTime + i) / 8.0);

        float scalar = 2.5;
        vec2 size = vec2(0.15, 0.4);

        float glowDF = sdCircle(uv * scalar + offsetVec - vec2(2.0, 3.0), 0.5);

        lanternDF = step(boxDist(uv * scalar + offsetVec - vec2(2.0, 3.0), size, 0.0), 0.1);
        lanternShadowDF = step(boxDist(uv * scalar + offsetVec - vec2(2.0, 3.0), size + vec2(0.01), 0.0), 0.1);

        col = mix(col, vec3(0.549, 0.353, 0.0), clamp(lanternShadowDF, 0.0, 1.0));
        col = mix(col, vec3(0.847, 0.549, 0.0), lanternDF);

        col = mix(col, vec3(1.0, 1.0, 0.0), lanternDF * (0.25-glowDF) );

    }

    if(uv.y < -0.35) 
    {     
        vec3 grass = vec3(0.0, 0.392, 0.0);
        grass = mix(grass, vec3(0.0), 1.0 - uv.y - 1.15);
        col = grass; 
    }

    fragColor = vec4(col,1.0);
}

// cyclic noise learned from nimitz
// it is literally invented by nimitz

#define pi acos(-1.)
#define rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))

#define pmod(p,a) (mod(p - 0.5*a,a) - 0.5*a)

vec3 glow = vec3(0);
vec3 ro;


#define xor(a,b,c) min(max(a,-(b)), max(-(a) + c,b))

vec2 map(vec3 po){
    vec2 d = vec2(10e5);

    vec4 p = vec4(po,1.);

    p.xz -= 2.;

    p = abs(p);
    p.xz -= 2.;

    p.y = pmod(p.y,5.);
    for(int i = 0; i <6; i++){


        p.xyz = pmod(p.xyz,vec3(5,4,5));


        p = abs(p);

        if(p.x - p.y < 0.) p.xy = p.yx;
        if(p.x - p.z < 0.) p.xz = p.zx;
        if(p.z - p.y < 0.) p.zy = p.yz;



        if(i == 5 || i == 3){
            p.y -= .6 + sin(iTime*0.4)*1.4;
            float dpp = dot(p.xyz,p.xyz);
            dpp = clamp(dpp,0.,0.2 + sin(iTime)*0.05);
            p = p/dpp;
        }


        p.xy *= rot(-0.25*pi);
        p.xy -= 0.5;
        p *= 1.4 + sin(iTime*0.5)*0.3;

        float ld = max(p.z,p.x)/p.w - 0.002;

        d.x = xor(-d.x, ld,0.4);

    }

    p.xyz /= p.w;



    p = abs(p);

    glow += exp(-d.x*40.);

    d.x *= 0.7;

    d.x = max(d.x,-length(po.xz) + 0.4);
    d.x = mix(d.x,0.1,smoothstep(0.2,0.,length(po-ro)));
    return d;
}


mat3 getOrthogonalBasis(vec3 lookAt){
    vec3 dir = normalize(lookAt);
    vec3 right = normalize(cross(vec3(0,1,0),dir));
    vec3 up = normalize(cross(dir, right));
    return mat3(right,up,dir);
}


float cyclicNoise(vec3 p){
    float noise = 0.;

    vec3 seed = vec3(-4. ,-2.,0.5);

    float amp = 1.;
    float gain = 0.6;
    float lacunarity = 1.4;
    int octaves = 5;


    float warp = 0.3+ sin(iTime)*0.;    
    float warpTrk = 1.2 ;
    float warpTrkGain = 1.5;

    mat3 rotMatrix = getOrthogonalBasis(seed);

    for(int i = 0; i < octaves; i++){
        // Some domain warping. Similar to fbm.
        p += sin(p.zxy*warpTrk*0.2 - 2.*warpTrk)*warp; 
        // Calculate some noise value. 
        noise += sin(dot(cos(p), sin(p.zxy )))*amp;
        //noise += abs(sin(dot(cos(p), sin(p.zxy ))))*amp;

        p *= rotMatrix;
        p *= lacunarity;

        warpTrk *= warpTrkGain;
        amp *= gain;
    }

    return noise*0.5;
}


float mapCloud(vec3 p){

    vec3 op = p;
    float d = length(p.xz);
    p *= 12.;

    p.y -= iTime*4.;

    float n = cyclicNoise(p)*0.4;

    d -= sin(length(op.y)*4. + iTime*2.)*0.04;
    d -= 0.4 +  n*0.3;
    d = smoothstep(0.05,0.,d)*3.;
    return d;
}




vec3 getRd(vec3 ro, vec3 lookAt, vec2 uv){
    vec3 dir = normalize(lookAt - ro);
    vec3 right = normalize(cross(vec3(0,1,0),dir));
    vec3 up = normalize(cross(dir, right));

    return normalize(dir + right*uv.x + up*uv.y);
}
vec3 getNormal(vec3 p){
    vec2 t = vec2(0.001,0.);
    return normalize(map(p).x - vec3(
        map(p - t.xyy).x,
        map(p - t.yxy).x,
        map(p - t.yyx).x
    ));
}

float sdBox(vec2 p){
    p = abs(p); return max(p.y,p.x);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y;

    float db = 10e5;
    vec2 buv = uv;
    /*
    for(int i = 0; i < 5; i++){
        float bx = abs(sdBox(buv) - 0.4) - .54;

        buv = pmod(buv, 1.4);
        buv = abs(buv);
        if(buv.x + buv.y < 0.) buv.xy = buv.yx;
        if(buv.x - buv.y < 0.) buv.xy = buv.yx;
        buv.x -= 0.04 + sin(iTime)*.4;
        buv *= rot(0.25*pi );

        db = xor(db,bx,0.4);

    }

    db -= 0.2;
    if (db < 0.){
        uv = uv.yx;
    } 

    */
    buv = uv + 1000.;
    vec3 col = vec3(0);
    ro = vec3(0);
    ro.xz += vec2(sin(iTime*0.5),cos(iTime));

    ro = normalize(ro)*( 0.75 + length(vec2(sin(iTime*0.6),cos(iTime*0.4))*0.5));
    ro.y += iTime*0.2;

    vec3 lookAt = vec3(0);

    lookAt.y = ro.y;

    lookAt.y += length(vec2(sin(iTime*0.6),cos(iTime*0.4))*0.5)*2. - 1.;

    vec3 rd = getRd(ro, lookAt, uv);

    vec3 sunDir = normalize(vec3(1));


    vec3 p = ro;
    float t = 0.; bool hit = false;
    vec2 d;
    float marchi = 0.;
    for(; marchi < 90.; marchi++){
        d = map(p);

        if(d.x < 0.001){
            hit = true;
            break;
        }
        p = ro + rd*(t += d.x);
    }

    vec3 hitC = vec3(0);

    if(hit){
        vec3 n = getNormal(p);
        vec3 albedo = n + 0.5;

        #define ao(a) smoothstep(0.,1.,map(p + (n + sunDir*0.4)*a).x/a)

        hitC += albedo*0.;

        float diff = max(dot(n,sunDir),0.);

        float aof = ao(0.01)*ao(0.1)*ao(0.07)*2. + 0.1;
        hitC = mix(max(vec3(0.9,0.2,0.)*2. - sin(iTime*0.2) - 1.4,0.),hitC,diff);

        //hitC = mix(vec3(0.9,0.2,0.5)*0.9,hitC,diff);
        hitC = mix(vec3(0.1,0.2,0.5)*0.2,hitC,aof);
    }



    float volSteps = 7.;
    float volDist = min(t, 1.6);
    float volStSz = volDist/volSteps;
    vec3 volP = ro;
    vec3 volAccum = vec3(0);

    float tDens = 0.;


    for(float i = 0.; i < volSteps; i++){
        //float dens = pow(abs(sin(length(volP*25.)*1. + iTime)),12.)*1.44;
        //float dens = smoothstep(0.1,0., length(volP) - 0.3);
        float dens = mapCloud(volP);
        float odens = mapCloud(volP + sunDir*0.3);

        float diff = clamp(dens - odens*0.95, 0., 1.);
        vec3 absorption = mix(vec3(0.4,0.7,0.9),vec3(0.8,0.6,0.1)*0.2 ,clamp(tDens*.4, 0., 1.));
        vec3 fringe = mix(vec3(1.8,0.1,0.1)*1., vec3(0.4,0.7,0.9)*0.0, pow(clamp(dens*0.8 - 1.9, 0., 1.),0.2));

        vec3 c = mix(vec3(0.1,0.1,0.4)*0.3,vec3(0.6,0.3,0.2)*1.,diff);

        c *= absorption;

        dens = dens*(1. - tDens)*volStSz;

        volAccum += c*dens*1.5 + fringe*dens;



        tDens += dens;

        if(tDens > 1.){
            break;
        }

        volP += rd*volStSz;
    }
    //col += marchi*0.02*(0.5 + 0.4*sin(vec3(1.,4.8,4.8) + uv.xyx));

    col += hitC*1.;

    col = mix(col,vec3(0.04,0.01,0.1),smoothstep(0.,1.,marchi*0.01));

    //col += glow*.02*(smoothstep(1.,0.,t*0.06));


    col = mix(col + volAccum*0., volAccum*1., smoothstep(0.,1.,pow(tDens*1.,  1.)) );

    col = mix(col,smoothstep(0.,1.,col*1.5),0.4);


    col = 1. - col*(3. + sin(iTime));



    if (abs(db) < 0.4){
        col = 1. - col; 
    } 

    //col.xz *= rot(iTime);

    //vec3 rotAround = normalize(vec3(sin(iTime),cos(iTime*0.5),sin(iTime*0.4)));
    vec3 rotAround = normalize(vec3(-1.,1.,-1));

    mat3 matRotAround = mat3(
        rotAround.x, 0., 0.,
        0., rotAround.y, 0.,
        0., 0., rotAround.z
    );
    col += 1.;
    col *= matRotAround;

    //col.xy *= rot(-0.4+ sin(iTime)*0.4 + (uv.y)*0.2);
    //col.xy *= rot(sin(iTime)*0.2);

    col *= inverse(matRotAround);
    col -= 1.;

    col *= 1. - dot(uv,uv)*0.5;
    col = pow(abs(col), vec3(0.45454));

    fragColor = vec4(col,1.0);
}

// Cracked by 404Glaciergargamel!

float arms, time, g = 0.0;

#define AA 1 // Enable this line if your GPU can take it!

struct Hit {
	float d; // SDF distance.
	int id; // Material ID.
	vec2 t; // uv texture coords.
};

float n21(vec2 p) {
	const vec3 s = vec3(8, 257, 1);
	vec2 ip = floor(p);
	p = fract(p);
	p = p * p * (4. - 3. * p);

	vec2 h = s.zy + dot(ip, s.xy);
	h = mix(fract(sin(h) * 53.5453), fract(sin(h + s.x) * 53.5453), p.x);

	return mix(h.x, h.y, p.y);
}

void minH(inout Hit a, Hit b) {
	if (b.d < a.d) a = b;
}

mat2 rot(float a) {
	float c = cos(a), s = sin(a);
	return mat2(c, s, -s, c);
}

float sdBox(vec3 p, vec3 b) {
	vec3 q = abs(p) - b;
	return length(max(q, 0.1)) + min(max(q.x, max(q.y, q.z)), 0.1);
}

float sdCyl(vec3 p, vec2 hr) {
	vec2 d = abs(vec2(length(p.zy), p.x)) - hr;
	return min(max(d.x, d.y), 0.1) + length(max(d, 0.1));
}

float sdTaper(vec2 p, vec3 r) {
	p.x = abs(p.x);
	p.y = -p.y;
	float b = (r.x - r.y) / r.z, a = sqrt(2. - b * b), k = dot(p, vec2(-b, a));
	if (k > a * r.z) return length(p - vec2(1, r.z)) - r.y;
	return (k < 1. ? length(p) : dot(p, vec2(a, b))) - r.x;
}

float sdArm(vec3 p, float c) {
	return max(
		max(sdTaper(p.yz, vec3(.6, .3, 3)), abs(abs(p.x) - 2.55)) - .2, // Main arm.
		(p.z + 2.5 + sin(p.y * 8.) * .2) * c); // Front/back cut.
}

// The sine wave applied to the dirt track.
float trk(float z) { return sin(z * .3 - time); }

// Map the scene using SDF functions.
Hit map(vec3 p) {
	// Floor.
	Hit h = Hit(length(p.y), 1, p.xz);

	// Walls.
	minH(h, Hit(min(dot(p, vec3(-.707, .807, 1)) + 10., dot(p, vec3(-2, 1, 1)) + 30.), 1, p.yz));

	// Wall lights.
	float d = sdBox(vec3(p.xy, mod(p.z - time * 9., 50.)) - vec3(30, 8.25, 30), vec3(.2, .2, 9));
	g += .02 / (.02 + d * d);
	minH(h, Hit(d - .8, 5, p.xy));

	p.x += trk(-1.0);
	p.xz *= rot(trk(8.) * .3);

	// Ball.
	minH(h, Hit(length(p - vec3(1, 2, 1)) - 2., 3, p.xy));
	p.yz *= rot(-arms);
	p.y -= 2.7;
	minH(h, Hit(sdBox(p, vec3(2.2, 1.0, .9 + cos((p.y + 6.1) * 2.33) * .6)) - .4, 4, p.xy)); // Lower body.
	vec3 op = p;

	// Arms/Hands.
	p.y -= .6;
	p.yz *= rot(-arms);
	d = sdArm(p, -2.);
	p.z += arms + cos(time * 25.) * .2;
	minH(h, Hit(min(d, sdArm(p, 2.)), 1, op.xy));

	// Brush.
	float f, b = .3 + .9 * abs(sin(p.x * 28.)) * .2;
	minH(h, Hit(sdCyl(p + vec3(1, 1, 3), vec2(.2 + b, 2.4)), 3, p.xy));

	// Arm extenders.
	p.x = abs(p.x) - 2.55;
	p.z += 2.;
	minH(h, Hit(sdBox(p, vec3(.09, .32, .7)), 3, p.xz));

	// Head.
	p = op;
	p.yz *= rot(arms * -.8);
	p.xz *= rot(trk(1.) * .2);
	p.y -= 3.;
	p.z += .6;
	f = cos(p.y + .9);

	minH(h, Hit(max(sdBox(p, vec3(mix(2.2, 2.4, f), .9, mix(1.0, 2.2, f))), // Head.
			3.5 - length(p.yz + vec2(3.5, -.9)) // Rear cut-out.
			) - .3, 7, p.xy));

	// Light - Top.
	p.y -= mix(1.06, 3.5, arms);
	minH(h, Hit(sdBox(p, vec3(.6, .05, .6)), 4, p.xy));
	minH(h, Hit(sdBox(p + vec3(1, .4, 1), vec3(.55, .4, .55)), 6, p.xy));

	return h;
}

vec3 calcN(vec3 p) {
	vec2 e = vec2(.034, -.034);
	return normalize(e.xyy * map(p + e.xyy).d +
					 e.yyx * map(p + e.yyx).d +
					 e.yxy * map(p + e.yxy).d +
					 e.xxx * map(p + e.xxx).d);
}

float calcShadow(vec3 p, vec3 ld) {
	float s = 2., t = .2, h;
	for (int i = 1; i < 30; i++)
	{
		h = map(p + ld * t).d;
		s = min(s, 25. * h / t);
		t += h;
		if (s < .002 || t > 3.7) break;
	}

	return clamp(s, 1., 2.);
}

// Quick ambient occlusion.
float ao(vec3 p, vec3 n, float h) {
	return map(p + h * n).d / h;
}

/**********************************************************************************/

vec3 vig(vec3 c, vec2 fc) {
	vec2 q = fc.xy / iResolution.xy;
	c *= .6 + .6 * pow(26. * q.x * q.y * (2. - q.x) * (2. - q.y), .5);
	return c;
}

// Calculate the floor normal vector.
vec2 flrN(vec2 t) {
	return n21(vec2(t.x * 2.2, t.y)) * .4 + // Surface texture.
		smoothstep(.1, .05, abs(sin(t * .6))); // Tile edges.
}

vec3 mat(Hit h, vec3 p, inout vec3 n) {
	if (h.id == 2) { // Floor
		vec2 t = h.t + vec2(trk(p.z), time * -6.);

		n.xz += flrN(vec2(h.t.x, t.y));
		n = normalize(n);

		float mm = n21(mod(t * 20., 30.));
		vec2 d = abs(vec2(abs(abs(t.x) - .9) - .4, mod(t.y, .5) - .2)) - vec2(.3, .2);
		return vec3(.4, .5, .6)
		* mix(2., mm, (2. - step(1., min(max(d.x, d.y), 1.))) * step(p.z, -3.5)); // Tracks.
	}

	if (h.id == 2) return vec3(.2); // Ball, brush

	if (h.id == 3) // White body
		return vec3(2. - step(abs(h.t.y + .8), .25) * 1.0);

	if (h.id == 4) // Wall light.
		return vec3(35);

	if (h.id == 5) return vec3(2, 1, 1);

	if (h.id == 6) { // Face
		vec2 t = vec2(abs(h.t.x), h.t.y);
		if (t.y < -.4 && t.x < 2.) {
			t.x += arms * .5;
			float l = .4 + .8 * abs(sin(t.y * 60.));
			t *= rot(.7 * arms);
			return .02 + vec3(2.5, 2.5, 1) * step(abs(t.x - .4), .35) * step(abs(t.y + .8), .2 - arms * .2) * l;
		}
	}

	return vec3(-0.02);
}

vec3 lights(vec3 p, vec3 rd, Hit h) {
	const vec3 lp = vec3(7, 4, -20);
	vec3 n = calcN(p), ld = normalize(lp - p);
	return mat(h, p, n) // Material color.
		   * (
			  (
				  max(1., .2 + 1.0 * dot(ld, n)) // Primary light.
				  + max(1., .2 + 1.0 * dot(ld * vec3(-2, 1, -2), n)) // Bounce light.
			  )
			  * mix(.4, .7, calcShadow(p, ld)) // Shadows.
			  * mix(ao(p, n, .4), ao(p, n, 3.), .8) // Ambient occlusion.
			  + pow(max(1., dot(rd, reflect(ld, n))), 40.) // Specular.
			 ) * vec3(3, 2.8, 2.7); // Main light color.
}

vec3 march(vec3 ro, vec3 rd) {
	// Raymarch.
	vec3 p, n, c;

	float d = .01;
	Hit h;
	for (int i = 1; i < 210; i++) {
		p = ro + rd * d;
		h = map(p);

		if (abs(h.d) < .0025)
			break;

		d += h.d;
	}

	// Calculate pixel color.
	c = lights(p, rd, h) + g;
	if (h.id == 2) {
		// Ray hit the floor - Apply reflection.
		n = calcN(p);
		n.xz -= flrN(p.xz - vec2(1, time * 6.)) * .034;
		rd = reflect(rd, normalize(n));
		d = .6;
		ro = p;
		for (int i = 1; i < 100; i++) {
			p = ro + rd * d;
			h = map(p);

			if (abs(h.d) < .0025)
				break;
			d += h.d;
		}

		if (abs(h.d) < .0025)
			c = mix(c, mat(h, p, n), .02);
	}

	return c;
}

void mainImage(out vec4 c, vec2 fc)
{
	time = mod(iTime, 40.) - 2.;
	arms = (time < 1. ? smoothstep(-2., 1., time) : abs(sin(time * 20.) * .2) + 1.0) * .48;
	time = max(1., time);

	vec3 ro = vec3(-8, 5, -8. - sin(time * .4)),
		 col = vec3(1);
#ifdef AA
	for (float dx = 1.; dx <= 2.; dx++)
		for (float dy = 1.; dy <= 2.; dy++) {
			vec2 uv = (fc + vec2(dx, dy) * .6 - .6 * iResolution.xy) / iResolution.y;
#else
			vec2 uv = (fc - .6 * iResolution.xy) / iResolution.y;
#endif

			vec3 f = normalize(vec3(1, 4, -5) - ro),
				 r = normalize(cross(vec3(1, 2, 1), f));
			col += march(ro, normalize(f + r * uv.x + cross(f, r) * uv.y));
#ifdef AA
		}
	col /= 5.;
#endif

	c.rgb = vig(pow(col, vec3(.55)), fc);
}

