#define BLUR 4.0 / iResolution.y

float Hash21(vec2 uv)
{                   
    return fract(sin(dot(uv, vec2(825.123, 9475.832))) * 244.19);
}

vec2 Rotate(vec2 uv, float deg)
{
    float angle = radians(deg);
    vec2 rot = uv * mat2(cos(angle), sin(angle), -sin(angle), cos(angle));
    return rot;
}

float Circle(vec2 uv, vec2 centre, float radius)
{
    float d = length(uv - centre);
    float c = smoothstep(radius, radius - BLUR, d);
    return c;
}

float MouseInPresent(){
    vec2 mouse=(2.0 * iMouse.xy-iResolution.xy) / iResolution.y;
    float dist=distance(mouse,vec2(0.3, -0.64));
    return dist>.18?0.:1.;
}
float Rect(vec2 uv, vec2 leftBot, vec2 rightTop)
{
    vec2 lb = smoothstep(leftBot, leftBot - BLUR, uv);
    vec2 rt = smoothstep(rightTop, rightTop - BLUR, uv);
    vec2 dim = rt - lb;

    return (min(dim.x, dim.y));
}

float RectP(vec2 uv, vec2 leftBot, vec2 rightTop)
{
    float blur=BLUR;
    float mi=MouseInPresent();
    if(mi>0.) blur=abs(sin(iTime*2.))*(30./iResolution.y);
    vec2 lb = smoothstep(leftBot, leftBot - blur, uv);
    vec2 rt = smoothstep(rightTop, rightTop - blur, uv);
    vec2 dim = rt - lb;

    return (min(dim.x, dim.y));
}

float TranslateRect(vec2 uv,vec2 center,vec2 wh,float angle,vec4 color){
    float rad=radians(angle);
    vec2 rot=mat2(cos(rad), sin(rad), -sin(rad), cos(rad))*(uv-center);
    vec2 leftBot=vec2(-wh.x/2.,-wh.y/2.);
    vec2 rightTop=vec2(wh.x/2.,wh.y/2.);
    vec2 lb = smoothstep(leftBot, leftBot - BLUR, rot);
    vec2 rt = smoothstep(rightTop, rightTop - BLUR, rot);
    vec2 dim = rt - lb;
    return min(dim.x, dim.y);
}

float TriangleIsosceles(vec2 uv, vec2 tip, vec2 size)
{
    tip = uv - tip;
    tip.x = abs(tip.x);

    vec2 a = tip - (size * clamp(dot(tip, size) / dot(size, size), 0.0, 1.0));
    vec2 b = tip - (size * vec2(clamp(tip.x / size.x, 0.0, 1.0), 1.0));
    float s = -sign(size.y);
    vec2 d = min(vec2(dot(a, a), s * ((tip.x * size.y) - (tip.y * size.x))), vec2(dot(b, b), s * (tip.y - size.y)));
    float t = -sqrt(d.x) * sign(d.y);

    return smoothstep(0.0, 0.0 - BLUR, t);
}

float Quad(vec2 uv, vec2 p1, vec2 p2, vec2 p3, vec2 p4)
{  
    float top = uv.y + (uv.x * (p1.y - p2.y));
    float bot = uv.y + (uv.x * (p3.y - p4.y));    
    top = smoothstep(p1.y, p1.y - BLUR, top);
    bot = smoothstep(p3.y, p3.y + BLUR, bot);

    float right = uv.x + (uv.y * (p4.x - p2.x));
    float left = uv.x + (uv.y * (p3.x - p1.x));    
    right = smoothstep(p2.x, p2.x - BLUR, right);
    left = smoothstep(p1.x, p1.x + BLUR, left);

    float h = min(top, bot);
    float w = min(left, right);

    float r = min(w, h);
    return r;
}

vec4 DecorateBase(vec2 uv, vec2 centre, float radius, float thickness, vec4 color, float base)
{
    float deco1 = Circle(uv, centre, radius);
    float deco2 = Circle(uv, centre - vec2(0.03, 0.02), radius - thickness);

    vec4 col = vec4(0.0);
    col = mix(col, TREE_COLOR, base);
    col = mix(col, color, min(base, deco1 - deco2));

    return col;
}

vec4 Snowfall(vec2 uv, float time, float speed, float radius)
{
    float ar = iResolution.x / iResolution.y;
    uv.x += ar - mod(time * speed, ar * 2.0);
    uv.y -= 1.0 - mod(time * speed, 2.0);

    uv += sin(time) * 0.1;

    float l = length(uv);

    return vec4(smoothstep(radius, radius - 3.0 / iResolution.y, l));   
}

