float layer2(vec2 p, float d, float scale, float scale2, float numline){
    vec2 prevP = p;
    float r = BORDER;
    float d2 = 1.0;

    float t = iTime *0.1;

    for(float i = 0.0; i<numline; i++){
        float targetScale1 = 0.3-(i*scale2);
        float targetScale2 = (0.2-(i*scale2));
        float targetScale3 = (0.17-(i*scale2));

        if(i>=4.0) {
            targetScale1 = (0.3-(i*0.01))*abs(sin(t*i))+0.1*(i*0.1);
            targetScale2 = (0.2-(i*0.01))*abs(sin(t*i))+0.05*(i*0.1);
            targetScale3 = (0.17-(i*0.01))*abs(sin(t*i))+0.03*(i*0.1);
        }        

        p = prevP;
        p.x = abs(p.x);
        p.x -= 0.5*scale;
        d2 = SimpleVesicaDistance(p*Rot(radians(90.0)),targetScale1*scale,0.2*scale);

        d = min(d,abs(d2)-r*1.0);

        p = prevP;
        p = abs(p);
        p -= vec2(0.3)*scale;
        d2 = SimpleVesicaDistance(p*Rot(radians(45.0)),targetScale1*scale,0.2*scale);

        d = min(d,abs(d2)-r*1.0);    

        p = prevP;
        p.y = abs(p.y);
        p.y -= 0.4*scale;
        d2 = length(p)-targetScale2*scale;

        d = min(d,abs(d2)-r*1.0);

        p = prevP;
        p = abs(p);
        p -= vec2(0.35,0.15)*scale;
        d2 = length(p)-targetScale3*scale;

        d = min(d,abs(d2)-r*1.0); 
    }

    return d;
}

float VesicaShapeDistance(vec2 p) {
    float t = iTime*0.2;
    float d = layer2(p,1.0,1.0,0.02,6.0);
    d = max(-( length(p)-0.45),d);    

    float d2 = min(d,layer0(p*Rot(radians(-30.0*t))));
    float d3 = layer1(p*Rot(radians(30.0*t*0.5)),d2);
    return d3;
}

float VesicaShapeBackground(vec2 p) {
    vec2 prevP = p;
    float d = 1.0;
    p.x = abs(p.x);
    p.x -= 0.5;
    float d2 = SimpleVesicaDistance(p*Rot(radians(90.0)),0.3,0.2);

    d = min(d,d2);

    p = prevP;
    p = abs(p);
    p -= vec2(0.3);
    d2 = SimpleVesicaDistance(p*Rot(radians(45.0)),0.3,0.2);

    d = min(d,d2);    

    p = prevP;
    p.y = abs(p.y);
    p.y -= 0.4;
    d2 = length(p)-0.2;

    d = min(d,d2);

    p = prevP;
    p = abs(p);
    p -= vec2(0.35,0.15);
    d2 = length(p)-0.17;

    d = min(d,d2); 

    p = prevP;

    d2 = length(p)-0.45;
    d = min(d,d2); 

    return d;
}

float Background(vec2 p) {
    float t = iTime *0.3;
    p.x = mod(p.x,0.43)-0.215;
    p.y = mod(p.y,0.36)-0.18;

    vec2 prevP = p;
    vec2 prevP2 = p;

    float d = layer2(p,1.0,0.3,0.04,3.0);

    p*=Rot(radians(-30.0*t));
    prevP*=Rot(radians(-30.0*t));
    p*=2.5;
    prevP*=2.5;

    p = abs(p);
    p-=vec2(0.105,0.043);
    float h = mod(t*0.3,0.6);
    if(h>=0.2){
        h = 0.2-(h-0.2);
    }
    float d2 = sdSegment(p*Rot(radians(-52.0)),h,0.004);
    d = min(d,d2);

    p = prevP;
    p.x = abs(p.x);
    p.x -= 0.105;
    p.y += 0.042;
    d2 = sdSegment(p,(h/0.2)*0.085,0.004);
    d = min(d,d2);

    p = prevP2;
    p = abs(p);
    p-=vec2(0.105,0.043);
    d2 = sdSegment(p*Rot(radians(-52.0)),0.15,0.001);
    d = min(d,d2);

    p = prevP2;
    p.x = abs(p.x);
    p.x -= 0.105;
    p.y += 0.042;
    d2 = sdSegment(p,0.085,0.001);
    d = min(d,d2);

    return d;
}

float Background2(vec2 p) {
    float t = iTime *0.3;
    float thickness = BORDER;
    float r = 0.05;
    vec2 prevP = p;

    p.x+= 0.215;
    p.y+= 0.18;
    p.x = mod(p.x,0.43)-0.215;
    p.y = mod(p.y,0.36)-0.18;

    p*=Rot(radians(30.0*t*3.0));

    float d = length(p-vec2(0.0,r*0.5))-r;
    float d2 =  length(p-vec2(0.0,-r*0.5))-r;

    d = min(abs(d)-thickness,abs(d2)-thickness);

    p*=Rot(radians(90.0));
    d2 = length(p-vec2(0.0,r*0.5))-r;
    d = min(d,abs(d2)-thickness);
    d2 =  length(p-vec2(0.0,-r*0.5))-r;
    d = min(d,abs(d2)-thickness);

    p = prevP;

    p.x+= 0.215;
    p.y+= 0.18;
    p.x = mod(p.x,0.43)-0.215;
    p.y = mod(p.y,0.36)-0.18;
    p*=Rot(radians(45.0));
    p = abs(p);
    p -= vec2(0.083);
    d2 = SimpleVesicaDistance(p*Rot(radians(45.0)),0.045,0.03);    
    d = min(d,abs(d2)-thickness*1.2);

    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 prevUV = uv;
    vec3 col = vec3(0.99,0.98,0.95);

    vec3 lineCol = vec3(132.0/255.0,153.0/255.0,141.0/255.0);

    uv.y+=iTime*0.1;
    uv*=1.3;
    float d = Background(uv);
    col = mix(col,lineCol,S(d,0.0));//0.9,0.6,0.5

    d = Background2(uv);
    col = mix(col,lineCol,S(d,0.0));

    uv = prevUV;
    uv*=1.17;

    d = VesicaShapeBackground(uv);
    col = mix(col,vec3(245.0/255.0,253.0/255.0,163.0/255.0)*1.45,S(d,-0.06));

    uv = prevUV;
    uv*=1.25;
    d = VesicaShapeDistance(uv);
    col = mix(col,vec3(140.0/255.0,152.0/255.0,171.0/255.0),S(d,0.0));

    fragColor = vec4(col,1.0);
}

float f(float x)
{
    return dot(cos(x * vec3(1.0, 2.0, 4.0) + iTime), vec3(1.0));
}

vec2 viewportMin = vec2(-7.0, -4.0);
vec2 viewportMax = vec2(7.0, 4.0);

vec3 backgroundColor = vec3(1.0);
vec3 gridColor = vec3(0.95);
vec3 axisColor = vec3(0.5);
vec3 graphColor = vec3(0.0);

float sq(vec2 x)
{
    return dot(x, x);
}

vec2 getClosestPointOnGraph(vec2 p, float x0, float x1)
{
    // Bin search (not NewtonâRaphson because requires proper gradient)
    for (int n = 0; n < 4; n++)
    {
        float d0 = sq(p - vec2(x0, f(x0)));
        float d1 = sq(p - vec2(x1, f(x1)));
        float mid = (x0 + x1) * 0.5;
        if (d0 < d1)
            x1 = mid;
        else
            x0 = mid;
    }
    // Get closest point on line for result
    vec2 p0 = vec2(x0, f(x0));
    vec2 p1 = vec2(x1, f(x1));
    vec2 v = normalize(p1 - p0);
    return p0 + v * dot(p - p0, v);
}

bool isClosestToZero(float x, float d)
{
    return abs(x) < abs(x - d) && abs(x) < abs(x + d);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 pixelSize = (viewportMax - viewportMin) / iResolution.xy;
    vec2 position = (fragCoord - 0.5) * pixelSize + viewportMin;

    // Background
    vec3 color = backgroundColor;

    // Grid
    vec2 gd = 1.0 - abs(0.5 - fract(position)) * 2.0;
    if (isClosestToZero(gd.x, pixelSize.x * 2.0) ||
        isClosestToZero(gd.y, pixelSize.y * 2.0))
        color = gridColor;

    // Axis notches
    if (abs(position.y) < pixelSize.y * 2.5 && isClosestToZero(gd.x, pixelSize.x * 2.0) ||
        abs(position.x) < pixelSize.x * 2.5 && isClosestToZero(gd.y, pixelSize.y * 2.0))
        color = axisColor;

    // Axis
    if (isClosestToZero(position.x, pixelSize.x) || 
        isClosestToZero(position.y, pixelSize.y))
        color = axisColor;

    // Graph
    vec2 p = getClosestPointOnGraph(position, position.x - pixelSize.x, position.x + pixelSize.x);
    float d = length((p - position) / pixelSize);
    color = mix(color, graphColor, max(0.0, 1.0 - d));

    fragColor = vec4(color, 1.0);
}

#define PI 3.14159265

vec2 b(float t, vec2 v){
   return abs(fract(t*v)-.5)*2.;
}

float ff(float n, float n2, float n3, float amp){
 return ((sin(n)+1.)*(sin(n2)*(sin(n3))+1.)+log(((sin(PI+n)+1.)*(sin(PI+n2)+1.))+1.))*amp; 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    float a = 20.;
    float amp = 0.5;
    float d = 1.;

    float time = iTime*0.05;

    float n = a*(time+distance(uv, b(time,vec2(3.1,1.7))));
    float n2 = a*(time+distance(uv, b(time,vec2(2.4,3.15))));
    float n3 = a*(time+distance(uv, b(time,vec2(1.45,2.65))));

    float f = ff(n, n2, n3, amp);
    float f2 = ff(n+d, n2+d, n3+d, amp);

    n = a*(time+distance(uv, b(time,vec2(1.5,3.7))));
    n2 = a*(time+distance(uv, b(time,vec2(3.4,1.15))));
    n3 = a*(time+distance(uv, b(time,vec2(2.45,1.65))));

    f += ff(n, n2, n3, amp);
    f2 += ff(n+d, n2+d, n3+d, amp);

    float v = (f2-f)/d;

    fragColor = vec4(vec3(v, 0., v),1.0);
}
