/*Ethan Alexander Shulman 2020 - xaloez.com
4k 60fps video https://www.youtube.com/watch?v=b5S1k7Ma1C8
4k wallpaper xaloez.com/art/2020/ShadowPool.jpg*/

#define time iTime

mat2 r2(float a) {
	float s = sin(a), c = cos(a);
	return mat2(c,-s,s,c);
}

vec4 hash(vec4 a) {
	return fract(abs(sin(a.ywxz*766.345)+cos(normalize(a)*4972.92855))*2048.97435+abs(a.wxyz)*.2735);
}

float caustics(vec2 p, vec2 uv) {
	float s = 0.;
	for (float l = 1.; l < 5.; l++) {
		vec4 lh = hash(l*vec4(.01,.1,1.,10.));
		vec2 ruv = mod(abs((p.xy-(lh.xy*2.-1.))*r2(time*.1+l*.01+log(1.+length(uv.xy)*(1.+l*l)))),.4)-.2,
			auv = abs(ruv)-.2;
		s += pow(max(-auv.x,-auv.y)*5.,8.);
	}
	return s;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord*2.-iResolution.xy)/iResolution.y;

	vec3 rd = normalize(vec3(uv.xy,.1));
	float s = 0.;
	for (float t = .1; t < 3.; t += .2) {
		s += caustics((rd*t).xy,uv)/t;
	}
	fragColor = vec4(1.-pow(s*.03,1.));
}

/*Ethan Alexander Shulman 2020 - xaloez.com
4k 60fps video https://www.youtube.com/watch?v=7M4K1v5c43s
4k wallpaper xaloez.com/art/2020/Brew.jpg*/

#define time iTime

#define PI 3.14159265358
#define EPS 1e-3

mat2 r2(float a) {
	float sn = sin(a), cs = cos(a);
	return mat2(cs,-sn,sn,cs);
}

float geo(vec3 p) {
	return min(-(length(p.xz)-5.),
			   length(mod(abs(p+vec3(100.+sin(time+p.y*2.)*.1,time*-.1,100.+cos(p.y*2.)*.1)),.4)-.2)-.05);
}

vec3 bg(vec3 rd) {
	return vec3(0)+pow(max(0.,1.-length(rd-normalize(vec3(.2,.2,.4)))*.5),2.);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord*2.-iResolution.xy)/iResolution.y;


	vec3 rp = vec3(0,0,-4),
		rd = normalize(vec3(uv.xy,.5)),
		c = vec3(1);

	for (int i = 0; i < 100; i++) {
		float dst = geo(rp);
		if (dst <= 0.) {
			if (length(rp.xz) >= 5.) break;
			vec3 gp = mod(abs(rp-rd*EPS+vec3(100.+sin(time+rp.y*2.)*.1,time*-.1,100.+cos(rp.y*2.)*.1)),.4)-.2;
			rd = refract(rd,normalize(gp),.97);
			c *= 1.05;
			dst = .2;
		}
		float dt = dst+EPS;
		rp += rd*dt;
		c /= 1.+dt*vec3(.3,.36,1.)*.5;
	}

	fragColor = vec4(c*bg(rd)*3.,1);
}

const float PI= 3.14159265359;
#define TYPE 4
vec3 saturate(vec3 a) { return clamp(a, 0.0, 1.0); }
vec2 saturate(vec2 a) { return clamp(a, 0.0, 1.0); }
float saturate(float a) { return clamp(a, 0.0, 1.0); }


mat3 rotate_x(float a){float sa = sin(a); float ca = cos(a); return mat3(vec3(1.,.0,.0),    vec3(.0,ca,sa),   vec3(.0,-sa,ca));}
mat3 rotate_y(float a){float sa = sin(a); float ca = cos(a); return mat3(vec3(ca,.0,sa),    vec3(.0,1.,.0),   vec3(-sa,.0,ca));}
mat3 rotate_z(float a){float sa = sin(a); float ca = cos(a); return mat3(vec3(ca,sa,.0),    vec3(-sa,ca,.0),  vec3(.0,.0,1.));}


const float TAU = 2.0 * PI;

float glow = 0.0;



float sdPlane(in vec3 p) {
    return p.y;
}

float sdSphere(in vec3 p, float s) {
    return length(p) - s;
}

float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

vec2 opUnion(vec2 d1, vec2 d2) {
    return d1.x < d2.x ? d1 : d2;
}
float sdApple(vec3 p, float r) {
	p.y *= 0.95;

	p.xz *= 1.2;

	float k = 0.84 + 0.16 * smoothstep(-r, r, p.y);
	p.xz /= k;
	return sdTorus(p, vec2((0.9 / 1.25) * r, r))*0.3;
}



float localTime = 0.0;







float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

vec2 matMin(vec2 a, vec2 b)
{
	if (a.x < b.x) return a;
	else return b;
}


float sdBox(vec3 p, vec3 radius)
{
  vec3 dist = abs(p) - radius;
  return min(max(dist.x, max(dist.y, dist.z)), 0.0) + length(max(dist, 0.0));
}






mat2 mm2(in float a){float c = cos(a), s = sin(a);return mat2(c,-s,s,c);}

vec3 fold(in vec3 p)
{
    const vec3 nc = vec3(-0.5,-0.809017,0.309017);
    for(int i=0;i<5;i++)
    {
		p.xy = abs(p.xy);
		float t = 2.*min(0.,dot(p,nc));
		p -= t*nc;
	}
    return p;
}

float smax(float a, float b)
{
    const float k = 2.;
    float h = 1.-clamp(.5 + .5*(b-a)/k, 0., 1.);
    return mix(b, a, h) - k*h*(1.0-h);
}

float tri(in float x){return abs(fract(x)-0.5)*2.;}


vec2 toroidal (vec2 p, float r) { return vec2(length(p.xy)-r, atan(p.y,p.x)); }
mat2 rot (float a) { float c=cos(a),s=sin(a); return mat2(c,-s,s,c); }
float amod (inout vec2 p, float count) { float an = TAU/count; float a = atan(p.y,p.x)+an/2.; float c = floor(a/an); c = mix(c,abs(c),step(count*.5,abs(c))); a = mod(a,an)-an/2.; p.xy = vec2(cos(a),sin(a))*length(p); return c; }
float sdCylinder (vec2 p, float r) { return length(p)-r; }

float sdIso(vec3 p, float r) { return max(0.,dot(p,normalize(sign(p))))-r; }
float sdStar (vec3 pos) {
    float dist;

    float radius = 3.;
    float size = .2;
    vec3 p = pos;
    p.y -= radius;
    p.xy *= rot(-iTime*.5);
    float index = amod(p.xy, 16.);
    p.x -= radius-1.5;
    p.xy *= rot(iTime+index);
    amod(p.xy, 5.);
    dist = sdIso(p, size);

    return dist;
}

float sdShere (vec3 pos) {
    float dist;

    float radius = 3.;
    float size = .2;
    vec3 p = pos;
    p.y -= radius;
    p.xy *= rot(-iTime*.5);
    float index = amod(p.xy, 16.);
    p.x -= radius-1.5;
    p.xy *= rot(iTime+index);

    dist = length(p)-0.2;

    return dist;
}

float box (vec3 pos) {
    float dist;

    float radius = 4.;
    float size = .2;
    vec3 p = pos;
    p.y -= radius;
    p.xy *= rot(-iTime*.5);
    float index = amod(p.xy, 16.);
    p.x -= radius-1.5;
    p.xy *= rot(iTime+index);

    dist =sdBox(p,vec3(0.2));

    return dist;
}

vec2 scene(in vec3 position) {


    vec3 p=position;
    p-=vec3(0.0,-0.5,0.0);
    p/=vec3(0.5);
    float star=sdStar(p)*0.3;
    p-=vec3(0.0,0.0,1.0);
    float sphere=sdShere(p)*0.3;
    p-=vec3(0.0,3.0,-4.3);
    p*=rotate_x(1.5);
    float torus=box (p)*0.3;

    star=min(star,sphere);
    star=min(star,torus);




    vec2 scene = opUnion(
          vec2(sdPlane(position), 1.0),
          vec2(star, 12.0)
    );
    return scene;
}

vec4 boxmap( sampler2D sam, in vec3 p, in vec3 n, in float k )
{
    vec3 m = pow( abs(n), vec3(k) );
	vec4 x = texture( sam, p.yz );
	vec4 y = texture( sam, p.zx );
	vec4 z = texture( sam, p.xy );
	return (x*m.x + y*m.y + z*m.z)/(m.x+m.y+m.z);
}
//------------------------------------------------------------------------------
// Ray casting
//------------------------------------------------------------------------------

float shadow(in vec3 origin, in vec3 direction) {
    float hit = 1.0;
    float t = 0.02;

    for (int i = 0; i < 1000; i++) {
        float h = scene(origin + direction * t).x;
        if (h < 0.001) return 0.0;
        t += h;
        hit = min(hit, 10.0 * h / t);
        if (t >= 2.5) break;
    }

    return clamp(hit, 0.0, 1.0);
}

vec2 traceRay(in vec3 origin, in vec3 direction) {
    float material = -1.0;

    float t = 0.02;

    for (int i = 0; i < 1000; i++) {
        vec2 hit = scene(origin + direction * t);
        if (hit.x < 0.002 || t > 20.0) break;
        t += hit.x;
        material = hit.y;
    }

    if (t > 20.0) {
        material = -1.0;
    }

    return vec2(t, material);
}

vec3 normal(in vec3 position) {
    vec3 epsilon = vec3(0.001, 0.0, 0.0);
    vec3 n = vec3(
          scene(position + epsilon.xyy).x - scene(position - epsilon.xyy).x,
          scene(position + epsilon.yxy).x - scene(position - epsilon.yxy).x,
          scene(position + epsilon.yyx).x - scene(position - epsilon.yyx).x);
    return normalize(n);
}

//------------------------------------------------------------------------------
// BRDF
//------------------------------------------------------------------------------

float pow5(float x) {
    float x2 = x * x;
    return x2 * x2 * x;
}

float D_GGX(float linearRoughness, float NoH, const vec3 h) {
    // Walter et al. 2007, "Microfacet Models for Refraction through Rough Surfaces"
    float oneMinusNoHSquared = 1.0 - NoH * NoH;
    float a = NoH * linearRoughness;
    float k = linearRoughness / (oneMinusNoHSquared + a * a);
    float d = k * k * (1.0 / PI);
    return d;
}

float V_SmithGGXCorrelated(float linearRoughness, float NoV, float NoL) {
    // Heitz 2014, "Understanding the Masking-Shadowing Function in Microfacet-Based BRDFs"
    float a2 = linearRoughness * linearRoughness;
    float GGXV = NoL * sqrt((NoV - a2 * NoV) * NoV + a2);
    float GGXL = NoV * sqrt((NoL - a2 * NoL) * NoL + a2);
    return 0.5 / (GGXV + GGXL);
}

vec3 F_Schlick(const vec3 f0, float VoH) {
    // Schlick 1994, "An Inexpensive BRDF Model for Physically-Based Rendering"
    return f0 + (vec3(1.0) - f0) * pow5(1.0 - VoH);
}

float F_Schlick(float f0, float f90, float VoH) {
    return f0 + (f90 - f0) * pow5(1.0 - VoH);
}

float Fd_Burley(float linearRoughness, float NoV, float NoL, float LoH) {
    // Burley 2012, "Physically-Based Shading at Disney"
    float f90 = 0.5 + 2.0 * linearRoughness * LoH * LoH;
    float lightScatter = F_Schlick(1.0, f90, NoL);
    float viewScatter  = F_Schlick(1.0, f90, NoV);
    return lightScatter * viewScatter * (1.0 / PI);
}

float Fd_Lambert() {
    return 1.0 / PI;
}

//------------------------------------------------------------------------------
// Indirect lighting
//------------------------------------------------------------------------------

vec3 Irradiance_SphericalHarmonics(const vec3 n) {
    // Irradiance from "Ditch River" IBL (http://www.hdrlabs.com/sibl/archive.html)
    return max(
          vec3( 0.754554516862612,  0.748542953903366,  0.790921515418539)
        + vec3(-0.083856548007422,  0.092533500963210,  0.322764661032516) * (n.y)
        + vec3( 0.308152705331738,  0.366796330467391,  0.466698181299906) * (n.z)
        + vec3(-0.188884931542396, -0.277402551592231, -0.377844212327557) * (n.x)
        , 0.0);
}

vec2 PrefilteredDFG_Karis(float roughness, float NoV) {
    // Karis 2014, "Physically Based Material on Mobile"
    const vec4 c0 = vec4(-1.0, -0.0275, -0.572,  0.022);
    const vec4 c1 = vec4( 1.0,  0.0425,  1.040, -0.040);

    vec4 r = roughness * c0 + c1;
    float a004 = min(r.x * r.x, exp2(-9.28 * NoV)) * r.x + r.y;

    return vec2(-1.04, 1.04) * a004 + r.zw;
}

//------------------------------------------------------------------------------
// Tone mapping and transfer functions
//------------------------------------------------------------------------------

vec3 Tonemap_ACES(const vec3 x) {
    // Narkowicz 2015, "ACES Filmic Tone Mapping Curve"
    const float a = 2.51;
    const float b = 0.03;
    const float c = 2.43;
    const float d = 0.59;
    const float e = 0.14;
    return (x * (a * x + b)) / (x * (c * x + d) + e);
}

vec3 OECF_sRGBFast(const vec3 linear) {
    return pow(linear, vec3(1.0 / 2.2));
}

//------------------------------------------------------------------------------
// Rendering
//------------------------------------------------------------------------------

vec3 hash3(vec2 p)
{
    vec3 q = vec3(
		dot(p,vec2(127.1,311.7)), 
		dot(p,vec2(269.5,183.3)), 
		dot(p,vec2(419.2,371.9))
	);
	return fract(sin(q) * 43758.5453);
}

float noise(vec2 x)
{
	vec2 p = floor(x);
	vec2 f = fract(x);
	return mix(
		mix(
			hash3(p + vec2(0.0, 0.0)).x,
			hash3(p + vec2(1.0, 0.0)).x,
			smoothstep(0.0, 1.0, f.x)
		),
		mix(
			hash3(p + vec2(0.0, 1.0)).x,
			hash3(p + vec2(1.0, 1.0)).x,
			smoothstep(0.0, 1.0, f.x)
		),
		smoothstep(0.0, 1.0, f.y)
	);
}


vec2 getUV(vec3 pos)
{
    vec3 nor = normal(pos);
    float lon = atan(nor.x,nor.z)/3.14;
    float lat = acos(nor.y)/3.14;
    vec2 r = vec2(lat, lon);

    return r;
}
vec3 render(in vec3 origin, in vec3 direction, out float distance,vec2 uv) {
    // Sky gradient
    vec3 color = vec3(0.65, 0.85, 1.0) + direction.y * 0.72;

    // (distance, material)
    vec2 hit = traceRay(origin, direction);
    distance = hit.x;
    float material = hit.y;

    // We've hit something in the scene
    if (material > 0.0) {
        vec3 position = origin + distance * direction;

        vec3 v = normalize(-direction);
        vec3 n = normal(position);
        vec3 l = normalize(vec3(0.6, 0.7, -0.7));
        vec3 h = normalize(v + l);
        vec3 r = normalize(reflect(direction, n));

        float NoV = abs(dot(n, v)) + 1e-5;
        float NoL = saturate(dot(n, l));
        float NoH = saturate(dot(n, h));
        float LoH = saturate(dot(l, h));

        vec3 baseColor = vec3(0.0);
        float roughness = 0.0;
        float metallic = 0.0;

        float intensity = 2.0;
        float indirectIntensity = 0.64;

        if (material < 4.0)  {
            // Checkerboard floor
            float f = mod(floor(6.0 * position.z) + floor(6.0 * position.x), 2.0);
            baseColor = 0.4 + f * vec3(0.6);
            roughness = 0.1;
        } else if (material < 16.0) {
            // Metallic objects
            baseColor = vec3(255.0,255.0,0.0)/255.0218,165,32;
            roughness = 0.5;
            metallic=0.5;
        }

        float linearRoughness = roughness * roughness;
        vec3 diffuseColor = (1.0 - metallic) * baseColor.rgb;
        vec3 f0 = 0.04 * (1.0 - metallic) + baseColor.rgb * metallic;

        float attenuation = shadow(position, l);

        // specular BRDF
        float D = D_GGX(linearRoughness, NoH, h);
        float V = V_SmithGGXCorrelated(linearRoughness, NoV, NoL);
        vec3  F = F_Schlick(f0, LoH);
        vec3 Fr = (D * V) * F;

        // diffuse BRDF
        vec3 Fd = diffuseColor * Fd_Burley(linearRoughness, NoV, NoL, LoH);

        color = Fd + Fr;
        color *= (intensity * attenuation * NoL) * vec3(0.98, 0.92, 0.89);

        // diffuse indirect
        vec3 indirectDiffuse = Irradiance_SphericalHarmonics(n) * Fd_Lambert();

        vec2 indirectHit = traceRay(position, r);
        vec3 indirectSpecular = vec3(0.65, 0.85, 1.0) + r.y * 0.72;
        if (indirectHit.y > 0.0) {
            if (indirectHit.y < 4.0)  {
                vec3 indirectPosition = position + indirectHit.x * r;
                // Checkerboard floor
                float f = mod(floor(6.0 * indirectPosition.z) + floor(6.0 * indirectPosition.x), 2.0);
                indirectSpecular = 0.4 + f * vec3(0.6);
            } else if (indirectHit.y < 16.0) {
                // Metallic objects
                indirectSpecular = vec3(0.3, 0.0, 0.0);
            }
        }

        // indirect contribution
        vec2 dfg = PrefilteredDFG_Karis(roughness, NoV);
        vec3 specularColor = f0 * dfg.x + dfg.y;
        vec3 ibl = diffuseColor * indirectDiffuse + indirectSpecular * specularColor;

        color += ibl * indirectIntensity;
    }

    return color;
}

//------------------------------------------------------------------------------
// Setup and execution
//------------------------------------------------------------------------------

mat3 setCamera(in vec3 origin, in vec3 target, float rotation) {
    vec3 forward = normalize(target - origin);
    vec3 orientation = vec3(sin(rotation), cos(rotation), 0.0);
    vec3 left = normalize(cross(forward, orientation));
    vec3 up = normalize(cross(left, forward));
    return mat3(left, up, forward);
}
// Camera
vec2 glFragCoord;
vec3 Ray( float zoom )
{
	return vec3( glFragCoord.xy-iResolution.xy*.5, iResolution.x*zoom );
}

vec3 Rotate( inout vec3 v, vec2 a )
{
	vec4 cs = vec4( cos(a.x), sin(a.x), cos(a.y), sin(a.y) );

	v.yz = v.yz*cs.x+v.zy*cs.y*vec2(-1,1);
	v.xz = v.xz*cs.z+v.zx*cs.w*vec2(1,-1);

	vec3 p;
	p.xz = vec2( -cs.w, -cs.z )*cs.x;
	p.y = cs.y;

	return p;
}


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Normalized coordinates
    vec2 p = -1.0 + 2.0 * fragCoord.xy / iResolution.xy;
    vec2 uv= fragCoord.xy / iResolution.xy;
    // Aspect ratio
    p.x *= iResolution.x / iResolution.y;

    vec3 ray = Ray(1.8);



	ray = normalize(ray);
    vec3 localRay = ray;

	vec2 mouse = vec2(-.1,iTime*.01);

	mouse = vec2(.5)-iMouse.yx/iResolution.yx;

	float T = iTime*.0;
	vec3 pos = 3.0*Rotate( ray, vec2(.2,1.5-T)+vec2(-1.0,-7.0)*mouse );
	//pos += vec3(0,.3,0) + T*vec3(0,0,-1);
    pos.y += .06-pos.z*.02; // tail is higher
    pos.z += pos.z*.2; // centre on the end of the car we're looking at
    pos.x += .3;//sign(pos.x)*.2*smoothstep(.0,.5,abs(pos.x)); // off-centre framingvec3 ray = Ray(1.8);





    // Camera position and "look at"
    vec3 origin = vec3(0.0, 2.0, 0.0);
    vec3 target = vec3(0.0);

    origin+=pos;

    mat3 toWorld = setCamera(origin, target, 0.0);
    vec3 direction = toWorld * normalize(vec3(p.xy, 2.0));

    // Render scene
    float distance;
    vec3 color = render(origin, direction, distance,getUV(origin + direction*traceRay(origin, direction).x));

    // Tone mapping
    color = Tonemap_ACES(color);

    // Exponential distance fog
    color = mix(color, 0.8 * vec3(0.7, 0.8, 1.0), 1.0 - exp2(-0.011 * distance * distance));

    // Gamma compression
    color = OECF_sRGBFast(color);

    fragColor = vec4(color, 1.0);
}

#define MaximumRaySteps 100
#define MinimumDistance .01


float DE(vec3 p)
{
  p = mod(p,1.0)-vec3(0.5);
  return length(p)-0.25;
}

float trace(vec3 from, vec3 direction) {
	float totalDistance = 0.0;
	int steps;
	for (steps=0; steps < MaximumRaySteps; steps++) {
		vec3 p = from + totalDistance * direction;
		float distance = DE(p);
		totalDistance += distance;
		if (distance < MinimumDistance) break;
	}
	return 1.0-float(steps)/float(MaximumRaySteps);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 col = vec3(0);

    vec3 ro = vec3(-6.*sin(iTime/10.), -6.*sin(iTime/10.), 0);
    vec3 rd = normalize(vec3(uv.x, uv.y, 1));

    float d = trace(ro, rd);
    col = vec3(d);

    col = pow(col, vec3(.4545));	// gamma correction

    fragColor = vec4(col,1.0);
}

//Move camera with mouse

const float ni = 3.14159;
vec2 rot(vec2 a, float c){
float l = length(a);
a = normalize(a);
float ang = atan(a.y, a.x)+(c*ni/180.0);    
return vec2(l*cos(ang), l*sin(ang));    
}


float capsule(vec3 a, vec3 b, vec3 p, float s){
float h = dot(b-a, p-a)/pow(length(b-a),2.0);
h = clamp(h,0.0,1.0);
vec3 q = a + (b-a)*h;
return length(q-p)-s;    
}

vec3 r(vec3 a, float c){
return ((fract(a/c))*c)-0.5*c;
}
float r(float a, float c){
return ((fract(a/c))*c)-0.5*c;
}

float box(vec3 p, vec3 c){
vec3 a = abs(p)-c;
return max(max(a.x,a.y), a.z);
}

float sphere(vec3 p, float s){
return length(p)-s;
}


float pyramid(vec3 p){// fix function with position+scale
//p=r(p, 4.);
    vec3 pos = p;
    pos.yz = rot(pos.yz, 45.0);
    pos.xy = rot(pos.xy, 45.0);
    pos = vec3(-pos.y, pos.x, pos.z);
    return max(box(pos, vec3(1.3)), -box(p-vec3(0.0, 0.0, -0.9), vec3(3.0, 3.0, 1.5)));
}

float prism(vec3 p){ //fix function with position + scale
vec3 ppos = vec3(0.0, 4.0, 2.0);
    vec3 pos = p-ppos;
    pos.xy = rot(pos.xy, 90.0);
    pos.yz = rot(pos.yz, 45.0);
    return max(box(pos, vec3(1.0)), -box(ppos-p-vec3(0.0,0.0,1.0), vec3(2.0, 2.0, 1.0)) );
}


float cone(vec3 pos, float h, float s, vec3 p){ //hallow
    vec3 a = vec3(pos.xy + normalize(p.xy-pos.xy)*s, pos.z);
    vec3 b = vec3(pos.xy, pos.z+h);
    float h1 = dot(b-a, p-a)/pow(length(b-a),2.0);
    h1 = clamp(h1, 0.0, 1.0);
    vec3 q = a + (b-a)*h1;
    return length(q-p)-0.1;
}

float cylinder(vec3 pos, vec3 p, float h, float s){
vec3 q = vec3(pos.xy, clamp(p.z, pos.z, pos.z+h));
return max(length(q-p)-s, box(pos-p+vec3(0.0, 0.0, h*0.5), vec3(s,s,h*0.5)));    
}

float torus(vec3 pos, vec3 p, float r1, float r2){
    vec3 pp = vec3(pos.xy + normalize(p.xy-pos.xy)*r1, pos.z);
    return length(pp-p)-r2;
}

float dis(vec3 p){
    return min(min(min(min(min(min(min(min(torus(vec3(0.0, 3.0, 4.0*sin(iTime)), p, 2.0, 0.5),
              cone(vec3(0.0, 3.0, 2.0*sin(iTime)), 3.0, 2.0, p)
              ),
              box(vec3(0.0, 3.0, -4.*sin(iTime))-p, vec3(2.0))
              ),
              capsule(vec3(-3.0, 3.0, 2.0*sin(iTime)), vec3(3.0, 3.0, 2.0*cos(iTime)),p, 0.6)
              ),
              length(vec3(6.0*sin(iTime), 3.0, 2.0)-p)-1.8
              ),
              pyramid(p-vec3(4.0, 2.0, 3.0*sin(iTime)))
              ),
              prism(p-vec3(-4.0, -1.0, 3.0*cos(iTime)))
              ),cylinder(vec3(2.0*cos(iTime), 3.0, 4.0*sin(iTime)), p, 3.0, 1.0)),
              box(vec3(0.0,3.0,-6.0)-p, vec3(40.0,40.0,2.0))
              );

}

bool trac(inout vec3 p, vec3 d, inout float dd){
    for(int i = 0; i < 60; i++){
        dd = dis(p);
        if(dd < 0.01)return true;
        p+=d*dd;
    }
    return false;
}

vec3 norm(vec3 p, float dd){
return normalize(
vec3(
dd-dis(vec3(p.x-0.1, p.yz)),
dd-dis(vec3(p.x, p.y-0.1, p.z)),
dd-dis(vec3(p.xy,p.z-0.1))
));
}

float shadow(vec3 p, vec3 lig){
vec3 d = normalize(lig-p);
    float dd,lg;
    for(int i = 0; i < 60; i++){
        lg = length(lig-p)-1.3;
        dd = min(dis(p), lg);
        if(dd < 0.01)break;
        p+=d*dd;
    }

    if(dd < 0.01 && dd == lg)return 1.0;
    return 0.2;
}

float rough(float a, float c){
return exp(-pow(12.0*(1.0-a)*(c-1.0)-a,2.0))/(ni*a);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;
    uv *= 2.0;

    vec3 d = normalize(vec3(uv.x, 1.0, uv.y));
    vec2 mouse = iMouse.xy / iResolution.xy;
    mouse = mouse * 2.0 - 1.0;
    d.yz = rot(d.yz, mouse.y*90.);
    d.xy = rot(d.xy, -mouse.x*90.);
    vec3 p = vec3(0.0, -5.0, 2.0);

    vec3 col = vec3(0.3, 0.5, 0.6)*max(pow(dot(d, vec3(0.0,0.0,1.0)),0.5),0.2)*2.0;
    float dd;
    vec3 prevp;
    vec3 lig = vec3(8.0, 4.0, 9.0);
    if(trac(p,d,dd)){
       vec3 n = norm(p,dd);
       vec3 light = normalize(lig - p);
       col = vec3(1.0)*max(dot(n,light),0.0)*shadow(p-d*0.1,lig)+
           (vec3(0.3, 0.5, 0.6)*max(pow(dot(reflect(d,n), vec3(0.0,0.0,1.0)),0.5),0.2))*0.9 + rough(0.4, dot(reflect(d,n),light)); 

        p-=d*0.2;
        d = reflect(d,n);
        if(trac(p,d,dd)){
        n = norm(p,dd);
        light = normalize(lig - p);
        col += vec3(1.0)*max(dot(n,light),0.0)*shadow(p-d*0.1,lig)+
           (vec3(0.3, 0.5, 0.6)*max(pow(dot(reflect(d,n), vec3(0.0,0.0,1.0)),0.5),0.2))*0.9+ rough(0.4, dot(reflect(d,n),light)); 
        }else{
        col+= vec3(0.3, 0.5, 0.6)*max(pow(dot(d, vec3(0.0,0.0,1.0)),0.5),0.2)*2.0;
        }
        col/=2.0;

    }

    fragColor = vec4(col,1.0);
}

