const vec2 HEX_MOD = vec2( 1., sqrt( 3.) );

/////////////////////////////////////////////////////////////////////////////
// helper functions

mat2 rotate2d( in float angle ) {
    angle *= PI2;
    return mat2(
        cos( angle ), -sin( angle ),
        sin( angle ),  cos( angle )
    );
}

vec2 which( in vec2 current, in vec2 test ) {
    return test.y < current.y ? test : current;
}

float can_atan( vec2 v ) {
    return atan( v.x, v.y );
}

/////////////////////////////////////////////////////////////////////////////
// distance function 

float sdHex( vec2 uv ) {    
    uv = abs( uv );
    float angled = dot( uv, normalize( HEX_MOD ) );
    float sides = uv.x;

    float d = max( angled, sides );
    return d;
}

/////////////////////////////////////////////////////////////////////////////
// the tile connections (values are fiddled and twiddled)

vec2 connectLines( vec2 uv ) {
    vec2 d = vec2( 1., 1e33 );

    d = which( d, vec2( 1., abs( uv.y - .00 ) / 1.0 ) );
    d = which( d, vec2( 2., abs( uv.x - .25 ) / 0.8 ) );
    d = which( d, vec2( 3., abs( uv.x + .25 ) / 0.8 ) );

    float a = .0;
    a += EQUALS( 1., d.x ) * ( uv.x + .5 );
    a += EQUALS( 2., d.x ) * ( uv.y + .2 ) / .8;
    a += EQUALS( 3., d.x ) * ( uv.y + .2 ) / .8;
    d.x = a;

    return d;
}

vec2 connectCircles( vec2 uv ) {
    vec2 d = vec2( 1., 1e33 );

    float lil = .29;
    float big = .578;
    float side = .5;

    vec2 center1 = vec2( +side, lil );
    vec2 center2 = vec2( -side, lil );
    vec2 center3 = vec2( +.0, -big );
    float r = .28;
    d = which( d, vec2( 1., length( uv - center1 ) -r) );
    d = which( d, vec2( 2., length( uv - center2 ) -r) );
    d = which( d, vec2( 3., length( uv - center3 ) -r) );

    float a = .0;
    a += EQUALS( 1., d.x ) * can_atan( center1 - uv ) / ( PI2 * .5);
    a += EQUALS( 2., d.x ) * abs( can_atan( uv  - center2 ) -1. ) / 2.6;  
    a += EQUALS( 3., d.x ) * ( 1. - ( can_atan( uv - center3 ) + .8 ) );
    d.x = a;

    return d;
}


/////////////////////////////////////////////////////////////////////////////
// make a hex tile with a random rotation and edge types

vec4 hexTile( vec2 uv ) {
    vec2 hexMod = vec2( 1., sqrt( 3.) );
    vec2 halb = .5 * HEX_MOD;

    vec2 a = mod( uv ,       HEX_MOD ) -halb;
    vec2 b = mod( uv + halb, HEX_MOD ) -halb;

    vec2 nu = dot( a,a ) < dot( b,b ) ? a : b;
    vec2 id = ( uv - nu );

    float angle = atan( nu.x, nu.y );
    float d = .5 - sdHex( nu );

#ifdef FLIP    
    float n = floor( mod( 6. * RAND2( id * 7.89 + 43.34 ), 6. ) );
    nu *= rotate2d( 1. / 6. * n );
#endif

    vec2 da = RAND2( id ) < LINE_THRESHOLD ? connectLines( nu ) : connectCircles( nu );
    angle = da.x;
    d = da.y;

    return vec4( angle, d, id.x, id.y );
}

