#define MAX_STEPS 100
#define MIN_DIST 0.01
#define MAX_DIST 1000.
#define EPSILON 0.00000001


vec2 rotatePlane(in vec2 plane, in float angle){
	return cos(angle) * plane + sin(angle) * vec2(plane.y, -plane.x);
}

vec3 rotate(in vec3 pos, in vec3 rot){
    pos.yz = rotatePlane(pos.yz, rot.x);

    pos.xz = rotatePlane(pos.xz, rot.y);

    pos.xy = rotatePlane(pos.xy, rot.z);

    return pos;
}

float DistanceToPlane(in vec3 pos, in float planeOffset){
    return pos.y + planeOffset;
}

float DistanceToBox(in vec3 pos, in vec3 scale){
    vec3 q = abs(pos) - scale;


    return length(max(q, 0.)) + min(max(q.x, max(q.y, q.z)), 0.);
}

float DistanceToSphere(in vec3 pos, in vec3 spherePos, in float r){
    return length(pos - spherePos) - r;
}

float smin(float a, float b, float k ) {
    float h = clamp( 0.5+0.5*(b-a)/k, 0., 1. );
    return mix( b, a, h ) - k*h*(1.0-h);
}

float DistanceTo(in vec3 pos){
    return DistanceToBox(rotate(pos + vec3(7., -5., -20.), vec3(-0.1, sin(iTime), 0.)), vec3(3., 2., 3.));
    //return smin(o, DistanceToSphere(pos, vec3(-5. + sin(iTime * 2.) * 5., 1., 20.), 3.), 1.);
}

float GetDist(vec3 pos){
    float d = length(pos)-1.5;
    return d*.7;
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.01, 0);

    vec3 n = d - vec3(
        DistanceTo(p-e.xyy),
        DistanceTo(p-e.yxy),
        DistanceTo(p-e.yyx));

    return normalize(n);
}

float GetDirectLight(in vec3 normal, in vec3 lightDir){
    return clamp(dot(lightDir, normal), 0., 1.);
}


float GetPointLightSpecular(in vec3 normal, in vec3 pos, in vec3 lightPos){
    vec3 attenGrad = vec3(0.006, 0.003, 0.001);

    vec3 lightDir = lightPos - pos;
    float dist = length(lightDir);
    lightDir /= dist;
    vec3 halfVec = normalize(lightDir - vec3(0., 0., 0.));

    float distAtten = clamp(1. / (attenGrad.x + attenGrad.y * dist + attenGrad.z * dist * dist), 0., 1.);

    return pow(clamp(dot(normal, halfVec), 0., 1.), 32.) * distAtten;
}

float GetPointLight(in vec3 normal, in vec3 pos, in vec3 lightPos){
    vec3 attenGrad = vec3(0.006, 0.003, 0.001);

    vec3 lightDir = lightPos - pos;
    float dist = length(lightDir);
    lightDir /= dist;

    float distAtten = clamp(1. / (attenGrad.x + attenGrad.y * dist + attenGrad.z * dist * dist), 0., 1.);
    return clamp(dot(normal, lightDir), 0., 1.) * distAtten;
}




void RayMarch(in vec3 rayPos, in vec3 rayDir, out bool back, out float dist){

    dist = 0.;
    back = false;

    for (int i = 0; i < MAX_STEPS; i++){
        if (dist > MAX_DIST){
            back = true;
            break;
        }
        else if (DistanceTo(rayPos + rayDir * dist) < MIN_DIST)
            break;
        dist += DistanceTo(rayPos + rayDir * dist);
    }
}

