void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = vec2((fragCoord - (0.5 * iResolution.xy)) / iResolution.y);
    vec3 sun = vec3(length(uv - vec2(0.2,0.2)) - 0.02);
    vec3 sky = 1.0 - sun * mix(vec3(0.2,0.2,0.4),vec3(1.0,0.4,0.1),uv.y * 1.2 + 0.4);
    float wave = abs(uv.y + 0.5 + sin(iTime * 2.0 - uv.x * 1.2) / 100.0);
    if (wave < 0.1){sky = 1.0 - abs(uv.y + 0.4) - sky * (abs(uv.x - 0.18) * sun) * vec3(0.8,0.5,0.6);}
    float lens = 1.0 - smoothstep(0.0,0.8,length(uv) - 0.5);
    if (lens > 0.0){sun = vec3(sky.x / 10.0 / lens,sky.y / 20.0 / lens,sky.z / 10.0 / lens);}
    fragColor = vec4(sky + sun,1.0);
}

#define AR iResolution.x / iResolution.y
#define SCALE 10.0

float SimpleNoise(in vec2 uv) {
    return fract(sin(uv.x * uv.y) * 4218.0 + iTime / 5.0);
}

float GetNoise(in vec2 uv) 
{
    vec2 lv = fract(uv * SCALE);
    lv = smoothstep(0.0, 1.0, lv);

    vec2 id = floor(uv * SCALE);

    float bl = SimpleNoise(id);
    float br = SimpleNoise((id + vec2(1, 0)));
    float b = mix(bl, br, lv.x);

    float tl = SimpleNoise((id + vec2(0, 1)));
    float tr = SimpleNoise((id + vec2(1)));
    float t = mix(tl, tr, lv.x);

    float n = mix(b, t, lv.y);
    return n;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= AR;
    uv += iTime / 30.0;
    uv *= 1.5;

    // Time varying pixel color
    vec3 col;

    col += GetNoise(uv);
    col += GetNoise(uv * 2.0) * 0.5;
    col += GetNoise(uv * 4.0) * 0.25;
    col += GetNoise(uv * 8.0) * 0.125;
    col += GetNoise(uv * 16.0) * 0.075;
    col += GetNoise(uv * 32.0) * 0.075 / 2.0;
    col += GetNoise(uv * 64.0) * 0.075 / 4.0;

    col *= 0.5;

    // Output to screen
    fragColor = vec4(col,1.0);
}

const int frequencies = 15;
const float speed = 0.05;
const float scaling = 0.01;

const float TAU = 6.28318530718;
#define SECOND_DERIV

vec2 hash2( vec2  p ) 
{ p = vec2( dot(p,vec2(127.1,311.7)), dot(p,vec2(269.5,183.3)) ); return fract(sin(p)*43758.5453); }

#ifdef SECOND_DERIV
#define DTYPE vec4
#else
#define DTYPE vec2
#endif

DTYPE d(vec2 uv, float t) 
{
    DTYPE d;
    for(int i=-frequencies; i<=frequencies; ++i)
    {
        for(int j=-frequencies; j<=frequencies; ++j)
        {
            vec2 amp_phase = hash2(vec2(i,j));// / float(i*i + j*j);
            float amp = amp_phase.x, phase = amp_phase.y * TAU;
            float theta = ( (uv.x + sign(float(i))*speed*t) *  float(i)
                                   + (uv.y + sign(float(j))*speed*t) * float(j))
                                 + phase;
            float costheta = cos(theta);
          #ifdef SECOND_DERIV
            float sintheta = sin(theta);
          #endif
            d += DTYPE(amp * float(i) * costheta,
                      amp * float(j) * costheta
                  #ifdef SECOND_DERIV
                      ,amp * float(i) * sintheta,
                      amp * float(j) * sintheta
                  #endif
                      );
        }
    }
    return d * scaling;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / max(iResolution.y,iResolution.x);

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    DTYPE d = d(uv, iTime);

    // Output to screen
#ifdef SECOND_DERIV
    fragColor = d;
#else
    fragColor = vec4(d, 0.9, 1.0);
#endif
}

float pi = acos(-1.);
mat2 rot(float a ){return mat2(cos(a),sin(a),-sin(a),cos(a));}

vec2 pmod(vec2 p, float r) {
    float a =  atan(p.x, p.y) + pi/r;
    float n = (pi * 2.) / r;
    a = floor(a/n)*n;
    return p*rot(-a);
}

vec2 map(vec3 p)
{
    float plate = length(p / vec3(1.,.3,1.)) - 1.3;
    plate = max(-plate , length((p +vec3(0.,-0.2,0.) )/ vec3(1.,.3,1.)) - 1.1);
    float o = plate * .2;

    float tt = floor(iTime) + pow(fract(iTime) ,1.2);
    p.x += sin(tt * pi + p.y*1.4)/10.;
    p.xz = pmod(p.xz,12.);
    p.z = p.z - 0.2;
    float s = 1.;
    float w = 1.3;
    float pud = length(p / vec3(w * p.y + s,1.8,w * p.y +s) ) - .3;
    pud = max(pud , p.y - .4);
    pud = max(pud , -p.y- .24);

    float id = 0.;
    if(o > pud)
    {
        o = pud;
        id = 1.;
    }

    return vec2(o,id);
}

vec2 march(vec3 cp, vec3 rd)
{
    float depth = 0.;
    for(int i = 0; i < 99; i++)
    {
        vec3 rp = cp + rd * depth;
        vec2 d = map(rp);
        if(abs(d.x) < 0.001)
        {
            return vec2(depth,d.y);
        }
        depth += d.x;
    }
    return vec2(-depth,0.);
}

float getThick(vec3 cp, vec3 rd)
{
    float depth = 0.;
    for(int i = 0; i < 33 ; i++)
    {
        vec3 rp = cp + rd * depth * 0.01;
        float d = map(rp).x;
        if(d > 0.)
        {
            return depth;
        }
        depth += max(abs(d) , 0.001);
    }
    return depth;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
	float resol = 70. ;
    p = floor(p * resol)/resol;
    vec3 cp = vec3(0.4,1.,-5.);
    cp.yz *= rot(-pi * 1.1 );
    vec3 target = vec3(0.,0.,0.);
    vec3 cd = normalize(target - cp);
    vec3 cs = normalize(cross(cd,vec3(0.,1.,0.)));
    vec3 cu = normalize(cross(cd,cs));

    float fov = 3.5;
    vec3 rd = normalize(fov * cd + cs * p.x + cu * p.y);
    vec3 col = vec3(1.,.3,0.);
    //
    vec2 d = march(cp,rd);

    if(d.x > 0.)
    {
        vec3 pos = cp + rd * d.x;
        vec2 e = vec2(0.,0.01);
        vec3 N = -normalize(map(pos).x - vec3(map(pos - e.xyy).x,map(pos - e.yxy).x,map(pos - e.yyx).x));
        vec3 sun = normalize(vec3(2.,4.,8.));
        sun.xz *= rot(iTime);

        float shadow = step(march(pos + N * 0.1 ,-sun).x,0.);
        float rim = (1. - abs(dot(rd,N)) );
        if(d.y < 1.)
        {
            //sara
            float diff = mix(max(0.,dot(sun,N)),1.,.1 );
            float sp =   max(0.,dot(-rd , reflect(N,sun)));
            sp = pow(sp,30.);
        	float cstep = 5.;
            col =  floor( diff * vec3(.7) * cstep) /cstep;
            col += floor( sp * 2.5 * vec3(1.) * cstep)/cstep;
            col += floor( rim * vec3(1.) * cstep) / cstep;
        }
        else if(d.y == 1.)
        {
            //pud
            float diff = mix(max(0.,dot(sun,N)),1.,.7 );
            float sp =   max(0.,dot(-rd , reflect(N,sun)));
            sp = pow(sp,13.);
        	col =  diff * mix( vec3(1.,.5,.4),vec3(0.), step(pos.y,-.2) );
            float cstep = 6.;
            col =  floor(col * cstep) / cstep;
            col += floor( sp * vec3(1.) * cstep ) / cstep;
            col += floor( vec3(1.) * clamp(map(pos+1.2*rd).x*1.1,.0,1.) * diff * cstep) / cstep;
            col += floor( rim * vec3(.5,.4,.1) * cstep ) /cstep;
        }

        col += (clamp(map(pos-2.4*rd).x*.8,.0,1.)-.5) * vec3(.5,.4,.1);
        //col = floor(col *6.) / 6.;
        //col *= shadow;
        //float dd = 1.-exp(-0.0003 * d.x * d.x * d.x);
    	//col = mix(col,vec3(1.,.3,0.), dd);
    }
    fragColor = vec4(col, 1.0);
}

