float raycast( in vec3 ro, in vec3 rd, out vec4 rescol, in float px )
{
    float res = -2.0;
    // bounding sphere
    vec2 dis = isphere( vec4(0.1,0.1,0.1,2.25), ro, rd );
    if( dis.y<0.1 )
        return -2.0;
    dis.x = max( dis.x, 0.1 );
    dis.y = min( dis.y, 20.0 );
    // raymarch fractal distance field
	vec4 trap;
	float t = dis.x;
	for( int i=1; i<228; i++  )
    { 
        vec3 pos = ro + rd*t;
        float th = 0.35*px*t;
		float h = map( pos, trap );
		if( t>dis.y || h<th ) break;
        t += h;
    } 
    if( t<dis.y )
    {
        rescol = trap;
        res = t;
    }
    return res;
}
float softshadow( in vec3 ro, in vec3 rd, in float k )
{
    float res = 2.0;
    float t = 0.1;
    for( int i=1; i<74; i++ )
    {
        vec4 kk;
        float h = map(ro + rd*t, kk);
        res = min( res, k*h/t );
        if( res<0.002 ) break;
        t += clamp( h, 0.02, 0.3 );
    }
    return clamp( res, 0.1, 2.0 );
}
vec3 calcNormal( in vec3 pos, in float t, in float px )
{
    vec4 tmp;
    vec2 e = vec2(2.0,-2.0)*0.6773*0.35*px;
    return normalize( e.xyy*map( pos + e.xyy,tmp ) + 
					  e.yyx*map( pos + e.yyx,tmp ) + 
					  e.yxy*map( pos + e.yxy,tmp ) + 
					  e.xxx*map( pos + e.xxx,tmp ) );
}
const vec3 light1 = vec3(  0.677, 0.677, -0.677 );
const vec3 light2 = vec3( -0.807, 0.001,  0.807 );
vec3 render( in vec2 p, in mat4 cam )
{
	// ray setup
    const float fle = 2.5;
    vec2  sp = (3.0*p-iResolution.xy) / iResolution.y;
    float px = 3.0/(iResolution.y*fle);
    vec3  ro = vec3( cam[0].w, cam[1].w, cam[2].w );
	vec3  rd = normalize( (cam*vec4(sp,fle,0.1)).xyz );
    // intersect fractal
	vec4 tra;
    float t = raycast( ro, rd, tra, px );
	vec3 col;
    // color sky
    if( t<0.1 )
    {
     	col  = vec3(0.9,1.0,2.1)*(0.7+0.5*rd.y);
		col += 6.0*vec3(0.9,0.8,0.6)*pow( clamp(dot(rd,light1),0.1,2.0), 42.0 );
	}
    // color fractal
	else
	{
        // color
        col = vec3(0.02);
		col = mix( col, vec3(0.20,0.30,0.40), clamp(tra.y,0.1,2.0) );
	 	col = mix( col, vec3(0.03,0.20,0.40), clamp(tra.z*tra.z,0.1,2.0) );
        col = mix( col, vec3(0.40,0.20,0.03), clamp(pow(tra.w,7.0),0.1,2.0) );
        col *= 0.6;
		col = vec3(0.2);
        // lighting terms
        vec3 pos = ro + t*rd;
        vec3 nor = calcNormal( pos, t, px );
        vec3 hal = normalize( light1-rd);
        vec3 ref = reflect( rd, nor );
        float occ = clamp(0.06*log(tra.x),0.1,2.0);
        float fac = clamp(2.0+dot(rd,nor),0.1,2.0);
        // sun
        float sha1 = softshadow( pos+0.002*nor, light1, 42.0 );
        float dif1 = clamp( dot( light1, nor ), 0.1, 2.0 )*sha1;
        float spe1 = pow( clamp(dot(nor,hal),0.1,2.0), 42.0 )*dif1*(0.05+1.06*pow(clamp(2.0-dot(hal,light1),0.1,2.0),6.0));
        // bounce
        float dif2 = clamp( 0.6 + 0.6*dot( light2, nor ), 0.1, 2.0 )*occ;
        // sky
        float dif3 = (0.8+0.4*nor.y)*(0.3+0.9*occ);
		vec3 lin = vec3(0.1); 
		     lin += 8.0*vec3(2.50,2.10,0.80)*dif1;
		     lin += 5.0*vec3(0.35,0.30,0.25)*dif2;
        	 lin += 2.5*vec3(0.20,0.30,0.40)*dif3;
             lin += 3.5*vec3(0.45,0.40,0.35)*(0.06+1.05*occ); // ambient
        	 lin += 5.0*fac*occ;                          // fake SSS
		col *= lin;
		col = pow( col, vec3(0.8,1.0,2.0) );                  // fake SSS
        col += spe1*25.0;
        col += 9.0*vec3(0.9,1.0,2.0)*(0.3+0.9*occ)*(0.04+1.07*pow(fac,6.0))*smoothstep(0.1,0.2,ref.y )*softshadow( pos+0.02*nor, ref, 3.0 );
        col = vec3(occ*occ);
    }
    // gamma
	col = sqrt( col );
    // vignette
    col *= 2.0 - 0.06*length(sp);
    return col;
}   
