// Signed distance and gradient to a Vesica. Probably
// faster than central differences or automatic 
// differentiation/dual numbers.

// List of other 2D distances+gradients:
//
// https://iquilezles.org/www/articles/distgradfunctions2d/distgradfunctions2d.htm
//
// and
//
// https://www.shadertoy.com/playlist/M3dSRf


// .x = f(p)
// .y = f(p)/x
// .z = f(p)/y
// .yz = f(p) with f(p) = 1
vec3 sdgVesica(vec2 p, float r, float d)
{
    vec2 s = sign(p); p = abs(p);

    float b = sqrt(r*r-d*d);  // can delay this sqrt by rewriting the comparison

    vec3 res;
    if( (p.y-b)*d > p.x*b )
    {
        vec2  q = vec2(p.x,p.y-b);
        float l = length(q)*sign(d);
        res = vec3( l, q/l );
    }
    else
    {
        vec2  q = vec2(p.x+d,p.y);
        float l = length(q);
        res = vec3( l-r, q/l );
    }
    return vec3(res.x, res.yz*s );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;

    // animate
    float time = iTime;
    float r1 = 0.5*cos(time+12.0);
    float r2 = 0.2*sin(time*1.4);

    // sdf(p) and gradient(sdf(p))
    vec3  dg = sdgVesica( p, 0.7, r1 );
    float d = dg.x + r2;
    vec2  g = dg.yz;

    // central differenes based gradient, for comparison
    // g = vec2(dFdx(d),dFdy(d))/(2.0/iResolution.y);

	// coloring
    vec3 col = (d>0.0) ? vec3(0.9,0.6,0.3) : vec3(0.4,0.7,0.85);
    col *= 1.0 + vec3(0.5*g,0.0);
  //col = vec3(0.5+0.5*g,1.0);
    col *= 1.0 - 0.7*exp(-8.0*abs(d));
	col *= 0.9 + 0.1*cos(150.0*d);
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.01,abs(d)) );

	fragColor = vec4(col,1.0);
}

float noise21(vec2 p) {
    return fract(sin(dot(p, vec2(12.9898, 4.1414))) * 43758.5453);
}

vec2 noise22(vec2 p) {
    return fract(vec2(noise21(p), noise21(p+232.245)));
}

vec3 noise23(vec2 p) {
    return fract(vec3(noise21(p), noise21(p+232.245), noise21(p+345.768)));
}

vec3 voronoi(vec2 p) {
    vec2 id = floor(p);
    float d = 10.0;
    vec2 closestPoint = id + noise22(id);
    float dToEdge = 10.0;

    // find colsest point
    for (float i=-1.0; i<=1.0; i++) {
        for (float j=-1.0; j<=1.0; j++) {
            vec2 currCellId = id + vec2(i, j);
            vec2 currPoint = (currCellId + noise22(currCellId));
            float currDistance = length(p - currPoint);
            if (currDistance < d) {
                closestPoint = currPoint;
                d = currDistance;
            }
        }
    }

    // find distance to edge
    float distanceToEdge = 10.0;
    for (float i=-1.0; i<=1.0; i++) {
        for (float j=-1.0; j<=1.0; j++) {
            vec2 currCellId = id + vec2(i, j);
            vec2 currPoint = (currCellId + noise22(currCellId));

            if (length(closestPoint - currPoint) < 0.001) continue;

            vec2 currPointDir = currPoint - closestPoint;
            vec2 midPoint = (currPoint + closestPoint)*0.5;

            distanceToEdge = min(dot(midPoint - p, normalize(currPointDir)), distanceToEdge);

            //vec2 midPoint = id
        }
    }
    float edgeWidth = 0.07;
    float edgeSmooth = 0.02;
    return smoothstep(edgeWidth-edgeSmooth, edgeWidth, distanceToEdge) * noise23(closestPoint);
    //return vec3(d);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 p = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    p *= 10.0;
    p += iTime;

    vec3 col = vec3(0.0);
    col += voronoi(p);

    fragColor = vec4(col,1.0);
}

const float PI = 3.1415926535897932384626433832795;

//calculates the power for the mandolbrot fractal equation (which increases over time)
vec2 Power(vec2 z,float power)
{	
	float real = cos(power * atan(z.x / z.y) - power * PI * sign(z.y) * 0.5) * pow(z.x * z.x + z.y * z.y, power / 2.0 );
	float imag = -sin(power * atan(z.x / z.y) - power * PI * sign(z.y) * 0.5) * pow(z.x * z.x + z.y * z.y, power / 2.0 );

	return vec2(real, imag);	
}

// color gradient that was given in the lab.
vec4 hsv2rgb(vec3 c) {
  vec3 rgb = clamp(abs(mod(c.x*6.+vec3(0.,1.,2.),4.)-3.)-1.,0.,1.);
  rgb = rgb * rgb * (3.0 - 2.0 * rgb);
  vec3 color = c.z * mix(vec3(1.0), rgb, c.y);
   return vec4(color,1);
}

// color mapping that was given in the lab.
vec4 colorMap(int i)
{
    if (i==256)
        return vec4(0,0,0,1);
    vec3 hsv = vec3(float(i)/256.0,1,1);
    return hsv2rgb(hsv); 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // we use sin to calculate the new power by doing ( 0 to 2) *4.0 + 2.0 = max:power=10 min:power=2
    float time = (sin(iTime / 6.0*PI)+1.0) * 4.0 + 2.0 ;
	float power = time;

    vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * vec2(2.4,1.4) - vec2 (1.2, 0.7); 

    vec2 z = vec2(0);
    fragColor = vec4(0);

    for (int i=0;i<256;i++)
    {
        if (z.x * z.x + z.y * z.y >= 4.) 
        {
            // Color maps the Mandelbrot fractal
            fragColor = colorMap(i);
            break;
        } 

        // calls the power function to update the power of the fractal with the "time" value
        z = Power(z,power) + uv;

    }
}

