uint k = 0x456789abu;
const uint max32 = 0xffffffffu;
vec2[8] gtable2 = vec2[](
    vec2(0.92387953, 0.38268343),
    vec2(0.38268343, 0.92387953),
    vec2(-0.92387953, 0.38268343),
    vec2(-0.38268343, 0.92387953),
    vec2(0.92387953, -0.38268343),
    vec2(0.38268343, -0.92387953),
    vec2(-0.92387953, -0.38268343),
    vec2(-0.38268343, -0.92387953)
);
vec2[4] diag = vec2[](    // 0.70710678 = 1.0 / sqrt(2.0)
    vec2(0.70710678,0.70710678),
    vec2(-0.70710678,0.70710678),
    vec2(0.70710678,-0.70710678),
    vec2(-0.70710678,-0.70710678)
);
vec2[4] axis = vec2[](
    vec2(1.,0.),
    vec2(-1.,0.),
    vec2(0.,1.),
    vec2(0.,-1.)
);

uint hash11u(uint n) {
    n ^= (n << 24);
    n ^= (n >> 1);
    n ^= (n << 1);
    return n * k;
}
vec2 hash22(vec2 p) {
    uint n = hash11u(hash11u(uint(p.x)) + uint(p.y));
    n = n >>29;
    if(fragCoord_.x < 0.33 * iResolution.x){
        n = n % 8u;
        return gtable2[n];
    } else if (fragCoord_.x < 0.66 * iResolution.x){
        n = n % 4u;
        return diag[n];
    } else {
        n = n % 4u;
        return axis[n];
    }
}

float pnoise21(vec2 p) {
    vec2 f = fract(p);
    vec2 g00 = hash22(p);
    vec2 g01 = hash22(p + vec2(0.0, 1.0));
    vec2 g10 = hash22(p + vec2(1.0, 0.0));
    vec2 g11 = hash22(p + vec2(1.0, 1.0));
    float v00 = dot(g00, f);
    float v01 = dot(g01, f - vec2(0.0, 1.0));
    float v10 = dot(g10, f - vec2(1.0, 0.0));
    float v11 = dot(g11, f - vec2(1.0, 1.0));
    f = f * f * f * (10.0 - 15.0 * f + 6.0 * f * f); 
    float v = mix(mix(v00, v10, f.x), mix(v01, v11, f.x), f.y);
    return 0.5 * v + 0.5;
}
