//https://www.shadertoy.com/view/XsX3zB
/* skew constants for 3d simplex functions */
const float F3 =  0.3333333;
const float G3 =  0.1666667;

/* 3d simplex noise */
float simplex3d(vec3 p) {
    /* 1. find current tetrahedron T and it's four vertices */
    /* s, s+i1, s+i2, s+1.0 - absolute skewed (integer) coordinates of T vertices */
    /* x, x1, x2, x3 - unskewed coordinates of p relative to each of T vertices*/

    /* calculate s and x */
    vec3 s = floor(p + dot(p, vec3(F3,F3,F3)));
    vec3 x = p - s + dot(s, vec3(G3,G3,G3));

    /* calculate i1 and i2 */
    vec3 e = step(vec3(0.,0.,0.), x - x.yzx);
    vec3 i1 = e*(1.0 - e.zxy);
    vec3 i2 = 1.0 - e.zxy*(1.0 - e);

    /* x1, x2, x3 */
    vec3 x1 = x - i1 + G3;
    vec3 x2 = x - i2 + 2.0*G3;
    vec3 x3 = x - 1.0 + 3.0*G3;

    /* 2. find four surflets and store them in d */
    vec4 w, d;

    /* calculate surflet weights */
    w.x = dot(x, x);
    w.y = dot(x1, x1);
    w.z = dot(x2, x2);
    w.w = dot(x3, x3);

    /* w fades from 0.6 at the center of the surflet to 0.0 at the margin */
    w = max(0.6 - w, 0.0);

    /* calculate surflet components */
    d.x = dot(random3(s), x);
    d.y = dot(random3(s + i1), x1);
    d.z = dot(random3(s + i2), x2);
    d.w = dot(random3(s + 1.0), x3);

    /* multiply d by w^4 */
    w *= w;
    w *= w;
    d *= w;

    /* 3. return the sum of the four surflets */
    return dot(d, vec4(52.0,52.0,52.0,52.0));
}

float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float sdCylinder( vec3 p, vec3 c )
{
  return length(p.xz-c.xy)-c.z;
}

float pedestal(vec3 p)
{
    vec3 bp = p;
    //bp.z -= 2.;
    bp.z = abs(bp.z) - 8.;
    bp.y -= 1.5;
	float b = sdBox(bp,vec3(.7,2.,.5)) - .2;
    b = max(b , -sdBox(p - vec3(0.,-.36,0.) , vec3(.1,.5,10.5) )) ;

    float b2 = sdBox(p - vec3(0.,4.,0.),vec3(2.,1.,10.)) - .1;
    b = min(b,b2);
    if(b < 1.)
    {
        b -= simplex3d((p / vec3(1.,7.,1.)) * 7.) / 50.;
    }
    float o = b;
    return o;
}

float station(vec3 p)
{
    p.yz *= rot(p.z/250.);
    vec3 bp = p;
    float b = sdBox(p - vec3(0.,13.,0.) , vec3(5.,8.,12.)) - .6;
    if(b < 1.)
    {
        b -= simplex3d((p / vec3(2.,12.,2.)) * 30.)/10.;
    }
    return b;
}

float road(vec3 p)
{
    float o;
    o = sdBox(p - vec3(0.,30.,0.),vec3(1000.,9.,16.)) - .5;
    return o;
}

vec2 map(vec3 p)
{
    float o = 10.;
    float id = 0.;
    vec3 pedestalP = p;
    vec3 po = p;

    p.yz *= rot(-p.z /90.);
    p.y += 1.2;
    o = sdBox(p,vec3( (max(pow( p.y/10.+.1  ,1.2), 0.01)) ,.5,12.) );

    pp = p;
    float ped = pedestal(pedestalP);
    if(ped < o)
    {
        o = ped;
        id = 1.;
    }
    float station = station(po);
    if(station < o)
    {
        o = station;
        id = 2.;
    }
    float road = road(po);
    if(road < o)
    {
        o = road;
        id = 3.;
    }
    o *= .8;
    return vec2(o,id);
}

vec2 march(vec3 cp , vec3 rd)
{
    float depth = 0.;
    for(int i = 0 ; i < 128 ; i++)
    {
        vec3 rp = cp + rd * depth;
        vec2 d = map(rp);
        if(abs(d.x) < 0.01)
        {
            return vec2(depth,d.y);
        }
        depth += d.x;
        if(depth > 290.){break;}
    }
    return vec2(-depth,0.);
}

float BechmannDistribution(float d, float m) {
    float d2 = d * d;
    float m2 = m * m;
    return exp((d2 - 1.0) / (d2 * m2)) / (m2 * d2 * d2);
}

float Fresnel(float c, float f0) {
    float sf = sqrt(f0);
    float n = (1.0 + sf) / (1.0 - sf);
    float g = sqrt(n * n + c * c - 1.0);
    float ga = (c * (g + c) - 1.0) * (c * (g + c) - 1.0);
    float gb = (c * (g - c) + 1.0) * (c * (g - c) + 1.0);
    return (g - c) * (g - c) / (2.0 * (g + c) + (g + c)) * (1.0 + ga / gb);
}

vec3 CookTorrance(vec3 v,vec3 l , vec3 n,vec3 specular , float microfacet)
{
    vec3 h = normalize(l + v);

    float hn = dot(h, n);
    float ln = dot(l, n);
    float lh = dot(l, h);
    float vn = dot(v, n);

    vec3 f = vec3(Fresnel(lh, specular.x), Fresnel(lh, specular.y), Fresnel(lh, specular.z));
    float d = BechmannDistribution(hn, microfacet);
    float t = 2.0 * hn / dot(v, h);
    float g = min(1.0, min(t * vn, t * ln));
    float m = pi * vn * ln;
    vec3 spe = max(f * d * g / m, 0.0);
    return spe;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
	vec3 col = vec3(0.);
    vec3 cp = vec3(0.,-16.,-30.);

   // cp.z += mod(iTime* 100. , 280.);

    //cp.z += sin(iTime/2.5) * 280.;
    cp.xz *= rot(pi/2.);
    //cp.xz *= rot(1.5);
    //cp.xz *= rot(-iTime/6.);
    vec3 target = vec3(0.,0.,0.);
    vec3 cd = normalize(target - cp);
    vec3 cs = normalize(cross(cd,vec3(0.,1.,0.)));
    vec3 cu = normalize(cross(cd,cs));

    float fov = 3.5;
    vec3 rd = normalize(cd * fov + cs * p.x + cu * p.y);

    vec2 d = march(cp,rd);
    if(d.x > 1.)
    {
        vec3 pos = cp + rd *d.x;
