    }
    fragColor = vec4(col, 1.0);
}

bool isWithinRange(vec2 coord, vec2 from, vec2 to) {
    vec2 fromBased = coord - from;
    bool isFromWithin = fromBased.x >= 0.0 && fromBased.y >= 0.0;
    vec2 toBased = to - coord;
    bool isToWithin = toBased.x >= 0.0 && toBased.y >= 0.0;
    return isFromWithin && isToWithin;
}

bool isWithinRange(float value, float from, float to) {
    return value >= from && value <= to;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float tolerance = 1.0;
    float timeModifier = 2.0;
    float[2] distanceBetweenRange = float[2](tolerance * 8.0, tolerance * 16.0);
    float[2] centerAmplitudeRange = float[2](10.0, 100.0);

    float distanceBetweenRangeDifference = distanceBetweenRange[1] - distanceBetweenRange[0];
    float timeCosNormalized = (cos(iTime * timeModifier * 2.0) + 1.0) / 2.0;
    float stepSize = distanceBetweenRange[0] + timeCosNormalized * distanceBetweenRange[1];

    vec2 center = vec2(0.5, 0.5) * iResolution.xy;
    vec3 col = vec3(0.0, 0.0, 0.0);
    float screenMaxResolution = max(iResolution.x, iResolution.y);
    float timeSinNormalized = (sin(iTime * timeModifier * 2.0) + 1.0) / 2.0;

    for(int i = 0; i < int(screenMaxResolution); i++) {
        float iF = float(i);
        float t = (iF / screenMaxResolution - 1.0);
        float offset = iF * stepSize;
        float targetDistance = centerAmplitudeRange[0] + timeSinNormalized * centerAmplitudeRange[1] + offset;

        float horizontalLowerBound[2] = float[2](
            center.y - targetDistance - tolerance,
            center.y - targetDistance + tolerance
        );
        float horizontalUpperBound[2] = float[2](
            center.y + targetDistance - tolerance,
            center.y + targetDistance + tolerance
        );

        float verticalLeftBound[2] = float[2](
            center.x - targetDistance - tolerance,
            center.x - targetDistance + tolerance
        );
        float verticalRightBound[2] = float[2](
            center.x + targetDistance - tolerance,
            center.x + targetDistance + tolerance
        );


        bool isWithinUpperMiddlePart = isWithinRange(
            fragCoord, 
            vec2(verticalLeftBound[1], horizontalUpperBound[0]), 
            vec2(verticalRightBound[0], horizontalUpperBound[1])
        );
        bool isWithinLowerMiddlePart = isWithinRange(
            fragCoord, 
            vec2(verticalLeftBound[1], horizontalLowerBound[0]), 
            vec2(verticalRightBound[0], horizontalLowerBound[1])
        );

        bool isWithinLeftMiddlePart = isWithinRange(
            fragCoord, 
            vec2(verticalLeftBound[0], horizontalLowerBound[1]), 
            vec2(verticalLeftBound[1], horizontalUpperBound[0])
        );
        bool isWithinRightMiddlePart = isWithinRange(
            fragCoord, 
            vec2(verticalRightBound[0], horizontalLowerBound[1]), 
            vec2(verticalRightBound[1], horizontalUpperBound[0])
        );

        bool someMiddlePartDrawApplicable = isWithinUpperMiddlePart || 
                                            isWithinLowerMiddlePart ||
                                            isWithinLeftMiddlePart ||
                                            isWithinRightMiddlePart;
        if(someMiddlePartDrawApplicable) {
            // Normalized pixel coordinates (from 0 to 1)
            vec2 uv = fragCoord/iResolution.xy;

            // Time varying pixel color
            col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
        }
    }

    fragColor = vec4(col, 1.0);
}

#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))
#define hash(n) fract(sin(n*234.567+123.34))

float map(vec3 p){
	p.xy *= rot(iTime*0.3);
    p.yz *= rot(iTime*0.4);
	float s=3.;
	p=abs(p);
	vec3  p0 = p*.9;
	for (float i=0.; i<5.; i++){
    	p=1.-abs(p-1.);
    	p=1.-abs(abs(p-2.)-1.); 
    	float g=-4.5*clamp(.45*max(1.6/dot(p,p),.7),.0,1.2);
    	p*=g;
    	p+=p0;
        s*=g;
	}
	s=abs(s);
	float a=3.8;
	p-=clamp(p,-a,a);
	return length(p)/s;

	return length(cross(p,normalize(vec3(1))))/s-.005;
}


vec3 calcNormal(vec3 pos){
  vec2 e = vec2(1,-1) * 0.002;
  return normalize(
    e.xyy*map(pos+e.xyy)+e.yyx*map(pos+e.yyx)+ 
    e.yxy*map(pos+e.yxy)+e.xxx*map(pos+e.xxx)
  );
}

float march(vec3 ro, vec3 rd, float near, float far)
{
    float t=near,d;
    for(int i=0;i<80;i++)
    {
        t+=d=map(ro+rd*t);
        if (d<0.001) return t;
        if (t>=far) return far;
    }
    return far;
}

float calcShadow( vec3 light, vec3 ld, float len ) {
	float depth = march( light, ld, 0.0, len );	
	return step( len - depth, 0.01 );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord* 2.0 - iResolution.xy) / iResolution.y;
    vec3 ro = vec3(0,0,5.5);
    vec3 rd = normalize(vec3(uv,-2.0));
    vec3 col = vec3(0);
	const float maxd = 40.0;
    float t = march(ro,rd,0.0,maxd);
    if(t<maxd)
    {
        vec3 p=ro+rd*t;
        col=vec3(0.3,0.3,0.6)+cos(p*0.17)*0.5+0.5;
        vec3 n = calcNormal(p);      
		vec3 lightPos=vec3(20);
    	vec3 li = lightPos - p;
		float len = length( li );
		li /= len;
		float dif = clamp(dot(n, li), 0.5, 1.0);
        float sha = calcShadow( lightPos, -li, len );
        col *= max(sha*dif, 0.4);
        float rimd = pow(clamp(1.0 - dot(reflect(-li, n), -rd), 0.0, 1.0), 2.5);
		float frn = rimd+2.2*(1.0-rimd);
    	col *= frn*.9;
        col *= max(0.5+0.5*n.y, 0.0);
        col *= exp2(-2.*pow(max(0.0, 1.0-map(p+n*0.3)/0.3),2.0));
        col += vec3(0.5,0.9,0.9)*pow(clamp(dot(reflect(rd, n), li), 0.0, 1.0), 8.0);
        col = mix(vec3(0),col,exp(-t*t*.003));
    }
    fragColor.xyz = col;
}

float rand(vec2 p) {
    p = fract(p * vec2(324.456, 863.789));
    p += dot(p, p+89.56);
    return fract(p.x*p.y);
}

float gauss(float x, float x0, float sx){

    float arg = x-x0;
    arg = -1./2.*arg*arg/sx;

    float a = 1./(pow(2.*3.1415*sx, 0.5));

    return a*exp(arg);
}

float sun(vec2 uv, float zoom) {
    // rotate
    float radian = fract(iTime*.001) * 3.1415926 * 2.;
    float s = sin(radian);
    float c = cos(radian);
    mat2 rotate = mat2(c, -s, s, c);
    uv *= rotate;

    float density = 500.;
    uv *= zoom;

    float col;

    float distToCenter = length(uv);

    float cir = smoothstep(.7, .15, distToCenter);
    float glow = smoothstep(2., .0, distToCenter);

    col += cir;
    col += glow*.2;

    vec2 id = floor(uv*density/zoom);

    float prob = gauss(distToCenter*4.5, 1., 1.);

    float dots = 1. - step(prob, rand(id));

    float fade = smoothstep(1.5, .3, length(uv/zoom));
    col += dots * fade;

    col = max(min(1., col), 0.);

    return col;
}

float bridge(vec2 uv) {
    float col;

    float t = smoothstep(-0.05, -0.15, uv.y);

    float l = smoothstep(0.17, 0.12, abs(uv.x/(uv.y-.01)/4.));

    col = l*t;

    col = max(min(1., col), 0.);

    return col;
}

float stairs(vec2 uv) {

    vec2 stair_uv = uv;
    stair_uv.y += 0.8;
    stair_uv.y = fract(stair_uv.y*stair_uv.y*stair_uv.y*20.) - .5;

    float line = smoothstep(0.1, 0., abs(stair_uv.y));
    float l2 = smoothstep(0.2, 0.1, abs(uv.x/uv.y/3.));
    float t = smoothstep(-0.05, -0.15, uv.y);
    float col = l2 * line * t;

    col = max(min(1., col), 0.);

    return col;
}

vec3 soul(vec2 uv) {
    vec2 b_uv = uv;
    vec2 h_uv = uv;
    float col;

    b_uv.x *= 1.3;
    b_uv *= 1.6;
    float body = smoothstep(.1, .09, length(b_uv));
    col += body;

    h_uv += vec2(0.,-.08);
    h_uv *= 1.3;
    //h_uv.x *= 1.5;
    float head = smoothstep(.1, .09, length(h_uv));
    col += head;

    //h_uv += vec2(0.,-.1);
    //h_uv.x *= 1.5;
    //float head = smoothstep(.1, .09, length(h_uv));
    //col += head;

    col = max(min(1., col), 0.);

    vec3 color = vec3(uv.x+.5, uv.y+.5, 1.) * col;

    return color;
}


