uvec3 k = uvec3(0x456789abu, 0x6789ab45u, 0x89ab4567u);
const uint max32 = 0xffffffffu;
uvec2 hash22u(uvec2 n) {
    n ^= (n.yx << 24);
    n ^= (n.yx >> 1);
    n *= k.xy;
    n ^= (n.yx << 1);
    return n * k.xy;
}
vec2 hash22(vec2 p) {
    float period = 8.0;  //periodicity
    uvec2 n = uvec2(mod(p, period));
    vec2 v = vec2(hash22u(n)) / vec2(max32);
    return normalize(2.0 * v - vec2(1.0));
}
uvec3 hash33u(uvec3 n) {
    n ^= (n.yzx << 24);
    n ^= (n.yzx >> 1);
    n *= k;
    n ^= (n.yzx << 1);
    return n * k;
}
vec3 hash33(vec3 p) {
    float period = 8.0;  //periodicity
    uvec3 n = uvec3(mod(p, period));
    vec3 v = vec3(hash33u(n)) / vec3(max32);
    return normalize(2.0 * v - vec3(1.0));
}
float gnoise21(vec2 p) {
    vec2 i = floor(p);
    vec2 g00 = hash22(i);
    vec2 g01 = hash22(i + vec2(0.0, 1.0));
    vec2 g10 = hash22(i + vec2(1.0, 0.0));
    vec2 g11 = hash22(i + vec2(1.0, 1.0));
    vec2 f = fract(p);
    float v00 = dot(g00, f);
    float v01 = dot(g01, f - vec2(0.0, 1.0));
    float v10 = dot(g10, f - vec2(1.0, 0.0));
    float v11 = dot(g11, f - vec2(1.0, 1.0));
    f = f * f * f * (10.0 - 15.0 * f + 6.0 * f * f);
    float v = mix(mix(v00, v10, f.x), mix(v01, v11, f.x), f.y);
    return 0.5 * v + 0.5;
}
float gnoise31(vec3 p) {
    vec3 f = fract(p);
    p= floor(p);
    vec3 g000 = hash33(p);
    vec3 g001 = hash33(p + vec3(0.0, 0.0, 1.0));
    vec3 g010 = hash33(p + vec3(0.0, 1.0, 0.0));
    vec3 g011 = hash33(p + vec3(0.0, 1.0, 1.0));
    vec3 g100 = hash33(p + vec3(1.0, 0.0, 0.0));
    vec3 g101 = hash33(p + vec3(1.0, 0.0, 1.0));
    vec3 g110 = hash33(p + vec3(1.0, 1.0, 0.0));
    vec3 g111 = hash33(p + vec3(1.0, 1.0, 1.0));

    float v000 = dot(g000, f);
    float v001 = dot(g001, f - vec3(0.0, 0.0, 1.0));
    float v010 = dot(g010, f - vec3(0.0, 1.0, 0.0));
    float v011 = dot(g011, f - vec3(0.0, 1.0, 1.0));
    float v100 = dot(g100, f - vec3(1.0, 0.0, 0.0));
    float v101 = dot(g101, f - vec3(1.0, 0.0, 1.0));
    float v110 = dot(g110, f - vec3(1.0, 1.0, 0.0));
    float v111 = dot(g111, f - vec3(1.0, 1.0, 1.0));
    f = f * f * f * (10.0 - 15.0 * f + 6.0 * f * f);
    float xy0 = mix(mix(v000, v100, f.x), mix(v010, v110, f.x), f.y);
    float xy1 = mix(mix(v001, v101, f.x), mix(v011, v111, f.x), f.y);
    return 0.5 * mix(xy0, xy1, f.z) + 0.5;
}
void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 pos = fragCoord.xy/min(iResolution.x, iResolution.y);
    pos *= 20.;
    pos += iTime;
    vec3 col;
    if (fragCoord.x < 0.5 * iResolution.x){
        col = vec3(gnoise21(pos));
    } else{
        col = vec3(gnoise31(vec3(pos, iTime)));
    }
    fragColor = vec4(col, 1.0);
}

#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST 0.01

mat2 Rot(float a) {
    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c);
}

float sdCapsule(vec3 p, vec3 a, vec3 b, float r){
    vec3 ab = b-a;
    vec3 ap = p-a;

    float t = dot(ab, ap) / dot(ab, ab);
    t = clamp(t, 0., 1.);

    vec3 c = a + t*ab;

    return length(p-c)-r;
}

float sdTorus(vec3 p, vec2 r){
    float x = length(p.xz)-r.x;
    return length(vec2(x, p.y))-r.y;
}

float dBox(vec3 p, vec3 s){
    //p.xz *= Rot(iTime);
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

float smin( float a, float b, float k ) {
    float h = clamp( 0.5+0.5*(b-a)/k, 0., 1. );
    return mix( b, a, h ) - k*h*(1.0-h);
}

float GetDist(vec3 p){
    // sphere position
    vec4 s = vec4(0, 1, 6, 1);
    // point to sphere dist
    // point to plane dist
    float planeDist = dot(p, normalize(vec3(0, 1, 0)));

    vec3 sp = p;
    sp.xz *= Rot(iTime);
    sp -= vec3(3., 1, 0);
    sp.xz *= Rot(iTime*2.);
    float sphereDist = length(p-s.xyz)-s.w;

    vec3 bp = p;
    bp.xz *= Rot(iTime);
    bp -= vec3(3., 1, 0);
    bp.xz *= Rot(iTime*2.);
    float box = dBox(bp, vec3(1.));

    vec3 cp = p;
    cp.xz *= Rot(iTime*1.5);
    cp -= vec3(2., 1, 0);
    //cp.xz *= Rot(iTime*10.);

    float cd = sdCapsule(cp, vec3(0, 0, 0), vec3(0, 1, .5), .8);

    vec3 tp = p;
    tp -= vec3(0, 2, 0);
    tp.yz *= Rot(iTime);
    tp.xy *= Rot(iTime);
    float td = sdTorus(tp - vec3(0, .0, 0), vec2(1.5, .2));

    vec3 bdp = p-vec3(0, 1, 0);
    float scale = mix(1., 2., smoothstep(0., 1., bdp.y));
    bdp.xz *= scale;
    bdp.xz *= Rot(smoothstep(0., 1., bdp.y) - iTime);

    float bd = dBox( bdp , vec3(1) )/scale;
    //bd -= sin(p.x*8.+iTime*2.7)*.1;
    //bd = abs(bd)-.1;

    float blend = smin(td, box, .8);
    float blend2 = smin(blend, box, .8);

    // plane or sphere
    float d = 10000.;
    //cd = abs(cd);
    d = min(d, bd);
    d = min(d, planeDist*300.);
    d = min(d, blend*1.4);
    //d = min(d, bd);
    return d;
}

float RayMarch(vec3 ro, vec3 rd){
    // dist from origin
    float dO = 0.;

    // march till Max step
    for(int i=0; i<MAX_STEPS; i++){
        // marched point
        vec3 p = ro + rd*dO;
        // distance from marched point
        float dS = GetDist(p);
        // move marched point
        dO += dS;
        // if march end break 
        if(dO > MAX_DIST || dS<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p){
    // distance from p to sphere
    float d = GetDist(p);
    // very small number for diff
    vec2 e = vec2(.001, 0);

    // getting vector with very small vector
    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx)
    );

    return normalize(n);

}

float GetLight(vec3 p){
    vec3 lightPos = vec3(3, 5, 6);
    lightPos.xz += vec2(sin(iTime), cos(iTime))*2.;
    vec3 l = normalize(lightPos - p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l)*.5+.5, 0., 1.);
    float d = RayMarch(p+n*SURF_DIST*2., l);    
   // float d = RayMarch(p+n*SURF_DIST*2., l);

    if(d<length(lightPos - p)) dif *= .3;

    return dif;

}


vec3 R(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = p+f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i-p);
    return d;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	vec2 m = iMouse.xy/iResolution.xy;

