    vec3 ro = vec3(0, 4, -5);


    vec3 rd = R(uv, ro, vec3(0,0,0), .7);


    float d = RayMarch(ro, rd);
    if(d<MAX_DIST) {
    	vec3 p = ro + rd * d;

    	float dif = GetLight(p);
    	col = vec3(dif);
    }

    vec3 p = ro+rd*d;

    float dif = GetLight(p);
    col = vec3(dif);

    // Output to screen
    fragColor = vec4(col,1.0);
}

// texture based 3D value noise by iq - https://www.shadertoy.com/view/4sfGzS
float noise( in vec3 x )
{
    vec3 i = floor(x);
    vec3 f = fract(x);
	f = f*f*(3.0-2.0*f);
	vec2 uv = (i.xy+vec2(37.0,17.0)*i.z) + f.xy;
	vec2 rg = textureLod( iChannel0, (uv+0.5)/256.0, 0.0).yx;
	return mix( rg.x, rg.y, f.z );
}

// Metaballs and analytic normals from Klems' https://www.shadertoy.com/view/4dj3zV
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 a, q, p, gradient, normal, dir;
    float b, dist, bdist;
    dir = normalize(vec3((2.*fragCoord.xy-iResolution.xy)/min(iResolution.x,iResolution.y), 1.7));
    p = vec3(0, 0, -3);
    for(int i = 0; i < 200; i++) {
        q = p; // save current position
        p += dir * dist; // step
        gradient = vec3(0);
        dist = 1.;
        for(float j = 0.; j < 3.; j++) {
            vec3 ballp = sin(vec3(1,2,5) * j + iTime); // ball position
            b = dot(a = p - ballp, a);
            // gradient += a / (b * b); // actual normals
            gradient += a / sqrt(b); // Shane's fur grooming tip
            dist -= .5 / b;
        }
        normal = normalize(gradient);
        bdist = dist;
        if(i>90 && dist<.01) {
            dist += noise(normal*60.);
            dist *= .02;
        }
     }
    vec3 col = normal.yyy*.4+.5;
    if(dist<.1) col = col * vec3(.9,.3,.5) + .1;
    else col = col * vec3(.7,.8,.5);
    fragColor.rgb = col * (1.+bdist);
}

#define UI0 1597334673U
#define UI1 3812015801U
#define UI2 uvec2(UI0, UI1)
#define UI3 uvec3(UI0, UI1, 2798796415U)
#define UIF (1.0 / float(0xffffffffU))

float random(vec2 p){
	uvec2 q = uvec2(ivec2(p)) * UI2;
	uint n = (q.x ^ q.y) * UI0;
	return float(n) * UIF;
}

float noise(vec2 uv){
	vec2 id = floor(uv*10.);
    vec2 lc = smoothstep(0.,1.,fract(uv*10.));

    float a = random(id);
    float b = random(id + vec2(1.,0.));
    float c = random(id + vec2(0.,1.));
    float d = random(id + vec2(1.,1.));

    float ud = mix(a,b,lc.x);
    float lr = mix(c,d,lc.x);
    float fin = mix(ud,lr,lc.y);
    return fin;
}

float octaves(vec2 uv){
    float amp = 0.5;
    float f = 0.;
    for(int i =1; i<5;i++){
    	f+=noise(uv)*amp;
        uv*=2.;
        amp*=0.5;
    }
    return f;
}

float circle(vec2 uv,float r){
    return length(uv) - r;
}

float star(vec2 uv, int pts, float r){
    uv = uv*4.-2.;
    float tau = 3.141592*2.;
    float a = atan(uv.y,uv.x)/tau;
    float seg = a * float(pts);
    a = ((floor(seg) +.5)/float(pts) + mix(r,-r,step(.5,fract(seg))))*tau;
    return abs(dot(vec2(cos(a),sin(a)),uv));
}

float stroke(float x, float s, float w){
    float o = (step(s,x+w)-step(s,x-w));
    o = clamp(o,0.,1.);
	return o;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    uv+=.5;
    uv.y+=.05;
    uv.y = 1.-uv.y;
    uv.x+=sin(iTime*2.+uv.y*3.)*.02 + cos(iTime*10.+uv.y*20.2)*.01;
    vec3 col = vec3(0.);
    float s = star(uv.yx,5,.099+octaves(uv+vec2(0.,iTime/2.))*.06*(.3-uv.y));
    float s2 = star(uv.yx,5,.888+octaves(uv+vec2(0.,iTime/2.))*.06*(.3-uv.y));
    col+=min(smoothstep(0.49,.5,s),1.-stroke(circle(uv-.5,.01),.25,.001+octaves(uv+vec2(0.,iTime/2.))*.05*(1.-uv.y)));  
    col = 1.-col;
    col*=mix(vec3(1.0,0.,.1),0.6+vec3(0.3,0.5,0.2),uv.y-.1);
    // Output to screen
    fragColor = vec4(col,1.0);
}

#define MIN_DISTANCE   0.001
#define MAX_DISTANCE   20.
#define MAX_ITERATIONS 200


#define MATERIAL_RED   1  
#define MATERIAL_BLUE  2
#define MATERIAL_GREEN 3
#define MATERIAL_YELLOW 4


/**
* m : index du matriau  appliquer
* d : distance  la surface observe
*/
struct Data {
    float d;
    int m;
};


/**
* Calcul la distance signe en entre le point p et la surface de la sphre
* p : point courant calcul par rapport  la sphre
* c : coordonnes du centre de la sphre
* r : rayon de la sphre
* m : matriau  appliquer sur la surface de la sphre
*/
Data sdSphere(vec3 p, vec3 c, float r, int m) {
    return Data(length(p - c) - r, m);
}

/**
* Calcule la distance signe entre le point p et la surface du plan de hauteur h
* p : point courant calcul par rapport  la sphre
* h : hauteur du plan par rapport  l'origine du monde
* m : matriau  appliquer sur la surface du plan
*/
Data sdPlanH(vec3 p, float h, int m) {
    return Data(p.y - h, m);
}

Data sdBox(vec3 p, vec3 b, int m) {
    vec3 q = abs(p) - b;
    float d = min(max(q.x,max(q.y,q.z)),0.0) + length(max(q,0.0));
    return Data(d, m);
}

Data sdTorus(vec3 p, vec2 t, int m) {
    vec2 q = vec2(length(p.xz)-t.x,p.y);
    float d = length(q)-t.y;
    return Data(d, m);
}

Data minData(Data d1, Data d2) {

    if(d1.d < d2.d) {
        return d1;
    }
    return d2; 
}

Data maxData(Data d1, Data d2) {

    if(d1.d > d2.d) {
        return d1;
    }
    return d2; 
}

Data minusData(Data d) {
    return Data(-d.d, d.m);
}

Data scene(vec3 p) {

    Data s1 = sdSphere(p, vec3(0.5*cos(iTime), 0, 1.7), 0.7, MATERIAL_RED);
    Data s2 = sdSphere(p, vec3(cos(iTime), 0, 1.2), 0.2, MATERIAL_GREEN);
    Data s3 = sdSphere(p, vec3(cos(0.9*iTime), 0, 1.2), 0.3, MATERIAL_BLUE);

    Data b = sdBox(p - vec3(0, max(0.0,0.5*sin(0.5*iTime)), 1), vec3(1, 2, 500)*.1, MATERIAL_YELLOW);

    Data pl = sdPlanH(p, -0.2, MATERIAL_BLUE);

    Data s = maxData(minusData(s2), s1);

    Data donut = sdTorus(p - vec3(0,0,1), vec2(0.25, 0.1), MATERIAL_GREEN);

    s = maxData(minusData(s3), s);
    s = maxData(minusData(b), s);
    s = minData(s, pl);
    s = minData(donut, s);

    return s;
}

Data rayMarch(vec3 rO, vec3 rD) {

    float d = 0.0;
    int m = 0;
    for(int i=0; i < MAX_ITERATIONS; i++) {

        vec3 p =  rO + rD * d;
        Data ds =  scene(p);
        d += ds.d;
        m = ds.m;
        if (ds.d < MIN_DISTANCE || d > MAX_DISTANCE) break;
    }

    return Data(d, m);

}


vec3 material(int index) {
    switch(index) {
        case MATERIAL_RED:
            return vec3(1.0, 0, 0);
        case MATERIAL_GREEN:
            return vec3(0, 1.0, 0);
        case MATERIAL_BLUE:
            return vec3(0, 0, 1.0);
        case MATERIAL_YELLOW:
            return vec3(1.0, 1.0, 0);
        default:
            return vec3(0);
    }

}

vec3 normal(vec3 p) {
    float dp = scene(p).d;

    float eps = 0.01;

    float dx = scene(p + vec3(eps, 0, 0)).d - dp;
    float dy = scene(p + vec3(0, eps, 0)).d - dp;
    float dz = scene(p + vec3(0, 0, eps)).d - dp;

    return normalize(vec3(dx, dy, dz));
}


float lighting(vec3 p, vec3 n, vec3 lp) {

    vec3 ld = lp - p;
    vec3 ln = normalize(ld);

    float d = rayMarch(p + n * 0.01, ln).d;

    if (d < length(ld)) return 0.0;

    return max(0.0, dot(ln, n));

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5* iResolution.xy) / iResolution.y;

    vec2 mouse = iMouse.xy / iResolution.xy;


    float k = 1.;
    vec3 lp = vec3(k*cos(iTime), 1, k*sin(iTime));


    vec3 camera = vec3(0, mouse.y, 0);
    vec3 screen = vec3(uv, 1.0);

    vec3 rD = normalize(screen - camera);

    Data data =  rayMarch(camera, rD);

    vec3 col = vec3(0);

    if(data.d < MAX_DISTANCE) {

       vec3 p = camera + rD * data.d;
       vec3 n = normal(p);
       float l = lighting(p, n, lp);

       vec3 m = material(data.m);

       col = m * l;
       col = pow(col, vec3(0.4545));

    }

    fragColor = vec4(col, 1.0);
}

struct Surface {
    float dist;
    vec3 ambient;
    vec3 diffuse;
    vec3 specular;
    float gloss;
};

float sdHexLink(in vec3 p, in float radius, in float thickness) {
    p = abs(p);
    return max(abs(max(dot(p.xz, vec2(0.5, 0.86602540378)), p.x) - radius), p.y) - thickness;
}

Surface mapScene(in vec3 p) {
    p.xy = vec2(atan(p.y, p.x) * 10.0, length(p.xy));
    p.y -= 10.0;

    float tileSize = 8.0;
    float thickness = 0.2;

    vec2 repTile = vec2(1.0, 1.73) * tileSize;
    vec2 hRepTile = 0.5 * repTile;

    vec2 a = mod(p.xz, repTile) - hRepTile;
    vec2 b = mod(p.xz - hRepTile, repTile) - hRepTile;
    p.xz = dot(a, a) < dot(b, b) ? a : b;

    float linkSize = 0.35 * tileSize;
    float basePattern = sdHexLink(p, linkSize, thickness);

    float repAng = 6.28 / 6.0;
    float hRepAng = 0.5 * repAng;
    p.xz = sin(mod(atan(p.z, p.x) - hRepAng, repAng) - hRepAng + vec2(1.57, 0.0)) * length(p.xz);
    float links = sdHexLink(p.xzy - vec3(0.575 * tileSize, 0.0, 0.0), linkSize, thickness);

    return Surface(min(basePattern, links), vec3(0.2, 0.0, 0.0), vec3(1.0, 0.0, 0.0), vec3(1.0), 16.0);
}

vec3 getNormal(in vec3 p) {
    vec3 e = vec3(0.001, 0.0, 0.0);
    return normalize(vec3(mapScene(p + e.xyy).dist - mapScene(p - e.xyy).dist,
                          mapScene(p + e.yxy).dist - mapScene(p - e.yxy).dist,
                          mapScene(p + e.yyx).dist - mapScene(p - e.yyx).dist));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    fragColor = vec4(0.0, 0.0, 0.0, 1.0);

    vec3 ro = vec3(0.0, 0.0, -iTime * 20.0);
    vec3 rd = normalize(vec3(uv, -1.0));

    float c = cos(iTime), s = sin(iTime);
    rd.xy *= mat2(c, -s, s, c);

    vec3 l = vec3(-0.58, 0.58, 0.58);

    float t = 0.0;
    for (int i=0; i < 100; i++) {
        vec3 p = ro + rd * t;
        Surface scene = mapScene(p);
        if (scene.dist < 0.001) {
            vec3 n = getNormal(p);
            fragColor.rgb += scene.ambient;

            float lambertian = max(0.0, dot(n, l));
            fragColor.rgb += scene.diffuse * lambertian;

            if (lambertian > 0.0) {
                vec3 r = reflect(l, n);
                float specAngle = max(0.0, dot(r, rd));
                fragColor.rgb += scene.specular * max(0.0, pow(specAngle, scene.gloss));
            }

            break;
        }

        if (t > 100.0) {
            break;
        }

        t += scene.dist;
    }
}

mat2 rot(float a){return mat2(cos(a),-sin(a),sin(a),cos(a));}

