}

// where the birds go
#define rot(a) mat2(cos(a), sin(a), -sin(a), cos(a))
const float TAU = atan(1.0) * 8.0;
#define ss(f,g,h) smoothstep(f,g,t)*h

vec3 map(vec3 p, float u, float v) {	
	float k;
	p.yz *= rot(v);
	p.xy *= rot(u);
	for (int i = 0; i < 6; i++) {
		k = length(p.xy); p.x = k * (mod(atan(p.y, p.x), TAU/8.) - TAU/16.); p.y = k - 2.752;		
		k = length(p.yz); p.y = k * (mod(atan(p.z, p.y), TAU/12.) - TAU/24.);p.z = k - 10.0; //-9.
	}

	p.x = dot(abs(p), normalize(vec3(6.9,5,2.2))) - 1.6 ;
	return p; 
}

