vec2 pt(float i) {
    return vec2(sin(i*i), cos(i*i));
    //return normalize(vec2(rand(vec2(i   ,0)),rand(vec2(i   ,1))) * 2. - 1.);
}
vec2 interpolate(vec2 a, vec2 b, vec2 c, vec2 d, float t){
    vec2 p0 = b;
    vec2 m0 = (b-a)*2.;
    vec2 p1 = c;
    vec2 m1 = (c-b)*2.;

    float t2 = t*t;
    float omt = 1.-t;
    float omt2 = omt*omt;
    float h00 = (1.+2.*t)*omt2;
    float h10 = t*omt2;
    float h01 = t2*(3.-2.*t);
    float h11 = t2*(t-1.);

    return h00*p0 + h10*m0 + h01*p1 + h11*m1;
}
