    vec2 a = pt(i-1.); 
    vec2 b = pt(i); 
    vec2 c = pt(i+1.);
    vec2 d = pt(i+2.);
    float t = j;
    vec2 p = interpolate(a,b,c,d,t);


    c = interpolate(b,c,d,pt(i+3.), t);
    b = interpolate(a,b,c,d, t);

    float dist = length(uv-p);
    fragColor = vec4(1);

    if(length(uv-b)*2. < dist) {
        dist = length(uv-b)*2.;
        fragColor = vec4(1,0,0,0);
    }

    if(length(uv-c) < dist) {
        dist = length(uv-c);
        fragColor = vec4(0,1,0,0);
    }

    fragColor*= 1.0 - dist*10.;
