#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .001

#define S smoothstep
#define T iTime

mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

float Hash21(vec2 p) {
    p = fract(p*vec2(123.34,233.53));
    p += dot(p, p+23.234);
    return fract(p.x*p.y);
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

float GetDist(vec3 p) {
    float d;

    float torusWidth = .1;

    float torus1 = sdTorus(p, vec2(1.5,torusWidth * 3.));
    vec3 torus2Pos = p;
    torus2Pos.xz *= Rot(iTime * 2.);
    torus2Pos += vec3(0.,0.,1.5);
    torus2Pos.yz *= Rot(1.57);
    torus2Pos.xy *= Rot(1.57);
    float torus2 = sdTorus(torus2Pos, vec2(.8,torusWidth * 2.));

    vec3 torus3Pos = torus2Pos;
    torus3Pos.xz *= Rot(3.+iTime * 2.);
    torus3Pos.xy *= Rot(1.57);
    torus3Pos += vec3(0.,0.,.8);

    float torus3 = sdTorus(torus3Pos, vec2(.4, torusWidth));
    d = min( torus1, min(torus2,torus3));
    return d;
}

float RayMarch(vec3 ro, vec3 rd) {
	float dO=0.;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        dO += dS;
        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	vec2 m = iMouse.xy/iResolution.xy;

    vec3 col = vec3(0);

    vec3 ro = vec3(0, 3, -6);
    ro.yz *= Rot(-m.y*3.14+1.);
    ro.xz *= Rot(-m.x*6.2831);

    vec3 rd = GetRayDir(uv, ro, vec3(0), 1.);

    float d = RayMarch(ro, rd);

    if(d<MAX_DIST) {
    	vec3 p = ro + rd * d;
    	vec3 n = GetNormal(p);

    	float dif = dot(n, normalize(vec3(1,2,3)))*.5+.5;
    	col += dif;  
        col = pow(col, vec3(.4545));	// gamma correction

    }else{
        //Background
        float yCoord = fragCoord.y / iResolution.y; 
        vec3 colTop = vec3(0.678, 0.937, 0.819);
        vec3 colBottom = vec3(0, 0.125, 0.247);
        col = mix( colBottom, colTop, smoothstep(0.1,0.9, yCoord));

    }


    fragColor = vec4(col,1.0);
}

