const vec3 c = vec3(1.,0.,-1.);
const float pi = acos(-1.);

float m(in vec2 x)
{
    //return max(abs(x.x), abs(x.y)); // rotated square spiral
    //return abs(x.x)+abs(x.y); // square spiral
    return length(x); // spiral
    //return .5*((abs(x.x)+abs(x.y)+min(abs(x.x),abs(x.y)))); // 4-star
    //return .5*((abs(x.x)+abs(x.y)+max(abs(x.x),abs(x.y)))); // octagon
}

// Distance to spiral
float spiral(in vec2 x, in float k)
{
    float tau = 2.*pi;
    vec2 dpr = mod(vec2(atan(x.y,x.x),m(x)/k),tau);
    float a = abs(dpr.y-dpr.x);
    return k*min(a,tau-a);
}

float sm(in float d)
{
    return smoothstep(1.5/iResolution.y, -1.5/iResolution.y, d);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Ignore the drawing code, it's messy, I know :)
    vec2 uv = (fragCoord.xy-.5*iResolution.xy)/iResolution.y;
	float d = spiral(uv, mix(.004,.1,.5+.5*sin(iTime)));
    d = abs(d)-.0025;
    float interval = clamp(.2 * (d-mod(d,.025))/.025, 0., 1.);
    vec3 col = mix(vec3(1.00,0.90,0.68), vec3(0.98,0.64,0.67), 2.*interval);
    if(interval > .5) col = mix(col, vec3(0.54,0.80,0.80), 2.*(interval-.5));
    col = mix(col, c.yyy, sm(d));
    col = mix(col, .4*c.xxx, sm(abs(mod(d+.0125,.025)-.0125)-.001));
    fragColor = vec4(col,1.0);
}

