float seed = 2.;  // the seed for the perlin noise

// changes the range of a number
float map(float val, float cMin, float cMax, float nMin, float nMax)
{
    float nVal = val - cMin;
    nVal *= (nMax - nMin) / (cMax - cMin);
    nVal += nMin;
    return nVal;
}

// a persuado random number generator (not my own generator)
float rand(vec2 co)
{
    return fract(sin(dot(round(co.xy) ,vec2(12.,78.))) * 43758.);
}

// a random range number generator
float randRange(vec2 co, float Min, float Max)
{
    return map(rand(co), 0., 1., Min, Max);
}

// a working smoothstep
float Smoothstep(float x)
{
    float k = clamp(x, 0., 1.);
    return k*k*(3. - 2. * k);
}

// a 2D perlin noise
float perlin2D(float scale, vec2 p)
{
    vec2 np = p / scale;
    vec2 offset = fract(np);

    float lu = randRange(vec2(floor(np.x), ceil(np.y)), 0., 1.);
    float ru = randRange(vec2(ceil (np.x), ceil(np.y)), 0., 1.);
    float ld = randRange(vec2(floor(np.x), floor (np.y)), 0., 1.);
    float rd = randRange(vec2(ceil (np.x), floor (np.y)), 0., 1.);

    return mix(mix(ld, lu, Smoothstep(offset.y)), mix(rd, ru, Smoothstep(offset.y)), Smoothstep(offset.x));
}

// main loop
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // non-streched uv coordinates
    vec2 uv = fragCoord/iResolution.y * 2. - 1.;

    // sample position for perlin noise
    vec2 p = fragCoord + seed * 100000. + round(vec2(iTime * 450., -iTime * 150. + sin(iTime) * 400.) + iMouse.xy);

    // the r layer of fractal noise (stacked layers of decreasing amplitude and scale perlin noise)
    float noise1 = 0.;
    noise1 += perlin2D(200., p         )     ;
    noise1 += perlin2D(100., p + 500.  ) / 2.;
    noise1 += perlin2D(50. , p + 1000. ) / 4.;
    noise1 = map(noise1, 0., 1.75, 0., 1.);

    // the g layer of fractal noise
    float noise2 = 0.;
    noise2 += perlin2D(200., p + 1500. )     ;
    noise2 += perlin2D(100., p + 2000. ) / 2.;
    noise2 += perlin2D(50. , p + 2500. ) / 4.;
    noise2 = map(noise2, 0., 1.75, 0., 1.);

    // the b layer of fractal noise
    float noise3 = 0.;
    noise3 += perlin2D(200., p + 3000. )     ;
    noise3 += perlin2D(100., p + 3500. ) / 2.;
    noise3 += perlin2D(50. , p + 4000. ) / 4.;
    noise3 = map(noise3, 0., 1.75, 0., 1.);

    // combining the three (rgb) layers of noise
    vec3 col = vec3(noise1, noise2, noise3);

    // outputing the final color
    fragColor = vec4(col,1.0);
}

const int MAX_ITER = 59; // Try 30 for extra flames

vec2 rotate(in vec2 v, in float a) {
	return vec2(cos(a)*v.x + sin(a)*v.y, -sin(a)*v.x + cos(a)*v.y);
}


float trap(in vec3 p)
{
    return 0.0;
}

float map(in vec3 p)
{
    float time = iTime+60.0;
	float cutout = dot(abs(p.yz),vec2(0.5))-0.035;
	float road = max(abs(p.y-0.025), abs(p.z)-0.035);

	vec3 z = abs(1.0-mod(p,2.0));
	z.yz = rotate(z.yz, time*0.05);

	float d = 999.0;
	float s = 1.0;
	for (float i = 0.0; i < 3.0; i++) {
		z.xz = rotate(z.xz, radians(i*10.0+time));
		z.zy = rotate(z.yz, radians((i+1.0)*20.0+time*1.1234));
		z = abs(1.0-mod(z+i/3.0,2.0));

		z = z*2.0 - 0.3;
		s *= 0.5;
		d = min(d, trap(z) * s);
	}
	return min(max(d, -cutout), road);
}

vec3 hsv(in float h, in float s, in float v) {
	return mix(vec3(1.0), clamp((abs(fract(h + vec3(3, 2, 1) / 3.0) * 6.0 - 3.0) - 1.0), 0.0 , 1.0), s) * v;
}

vec3 intersect(in vec3 rayOrigin, in vec3 rayDir)
{
    float time = iTime+60.0;
	float total_dist = 0.0;
	vec3 p = rayOrigin;
	float d = 1.0;
	float iter = 0.0;
	//
    float mind = 3.14159+sin(time*0.0)*0.2; // Move road from side to side slowly

	for (int i = 0; i < MAX_ITER; i++)
	{		
		if (d < 0.001) continue;

		d = map(p);
		// This rotation causes the occasional distortion - like you would see from heat waves
		p += d*vec3(rayDir.x, rotate(rayDir.yz, sin(mind)));
		mind = min(mind, d);
		total_dist += d;
		iter++;
	}

	vec3 color = vec3(0.0);
	if (d < 0.001) {
		float x = (iter/float(MAX_ITER));
		float y = (d-0.01)/0.01/(float(MAX_ITER));
		float z = (0.01-d)/0.01/float(MAX_ITER);
		if (max(abs(p.y-0.025), abs(p.z)-0.035)<0.002) { // Road
			float w = smoothstep(mod(p.x*50.0, 4.0), 2.0, 2.01);
			w -= 1.0-smoothstep(mod(p.x*50.0+2.0, 4.0), 2.0, 1.99);
			w = fract(w+0.0001);
			float a = fract(smoothstep(abs(p.z), 0.0025, 0.0026));
			color = vec3((1.0-x-y*2.)*mix(vec3(0.8, 0.8, 0), vec3(0.1), 1.0-(1.0-w)*(1.0-a)));
		} else {
			float q = 1.0-x-y*2.+z;
			color = hsv(0.0, 100.0, 0.0);
		}
	} else
		color = hsv(d, 1.0, 1.0)*mind*1.0; // Background
	return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime+60.0;
	vec3 upDirection = vec3(0, -1, 0);
	vec3 cameraDir = vec3(1,0,0);
	vec3 cameraOrigin = vec3(time*0.2, 0, 0);

	vec3 u = normalize(cross(upDirection, cameraOrigin));
	vec3 v = normalize(cross(cameraDir, u));
	vec2 screenPos = -1.0 + 2.0 * fragCoord.xy / iResolution.xy;
	screenPos.x *= iResolution.x / iResolution.y;
	vec3 rayDir = normalize(u * screenPos.x + v * screenPos.y + cameraDir*(1.0-length(screenPos)*0.5));

	fragColor = vec4(intersect(cameraOrigin, rayDir), 1.0);
}

// Using code from

//Morgan McGuire for the noise function
// https://www.shadertoy.com/view/4dS3Wd

#define time iTime
#define depth 70.0
#define fogSize 25.0
float fogCoef=1.0/(depth-fogSize);
float PI=acos(-1.0);

float random (in float x) {
	return fract(sin(x)*1e4);
}

float noise(in vec3 p) {
	const vec3 step = vec3(110.0, 241.0, 171.0);

	vec3 i = floor(p);
	vec3 f = fract(p);

	// For performance, compute the base input to a
	// 1D random from the integer part of the
	// argument and the incremental change to the
	// 1D based on the 3D -> 1D wrapping
	float n = dot(i, step);

	vec3 u = f * f * (3.0 - 2.0 * f);
	return mix( mix(mix(random(n + dot(step, vec3(0,0,0))),
	random(n + dot(step, vec3(1,0,0))),
	u.x),
	mix(random(n + dot(step, vec3(0,1,0))),
	random(n + dot(step, vec3(1,1,0))),
	u.x),
	u.y),
	mix(mix(random(n + dot(step, vec3(0,0,1))),
	random(n + dot(step, vec3(1,0,1))),
	u.x),
	mix(random(n + dot(step, vec3(0,1,1))),
	random(n + dot(step, vec3(1,1,1))),
	u.x),
	u.y),
	u.z);
}

mat2 rot(float a) {
	float ca=cos(a);
	float sa=sin(a);
	return mat2(ca,sa,-sa,ca);
}

float cloud(in vec3 p, vec3 centerPos, float scale,float radius ) {
	float l = length(p*0.1);
	vec3 d = vec3(p.x+sin(l+time)*2.0,p.y+sin(l)*2.0,0.0);
	float coef = max(length(d)-1.5,0.0);
	float c=1.0;
	float n1=1.0;
	for(int i=0; i<8; ++i) {
		n1+=1.0/c*abs(noise((p*c+time*1.0)*scale));
		c*=2.0;
	}
	return n1+(coef);
}

float mapHyper(vec3 p){
	return cloud(p,vec3(0,0,0),0.5,0.1);
}  

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
	uv -= 0.5;
	uv /= vec2(iResolution.y / iResolution.x, 1);
	vec3 s=vec3(0.5,0.5,100);
	float t2=(time*1.5);
	s.xz *= rot(sin(t2)*0.005);
	vec3 t=vec3(0,0,0);
	s.x += cos(t2*0.2)*0.10*sin(time*0.01);
	s.y += sin(t2*0.2)*0.10*sin(time*0.01+10.0);
	vec3 cz=normalize(t-s);
	vec3 cx=normalize(cross(cz,vec3(0,1,0)));
	vec3 cy=normalize(cross(cz,cx));
	vec3 r=normalize(uv.x*cx+uv.y*cy+cz*0.7);
	s.z+=time*-8.0;

	vec3 p=s;
	float d;
	float seuil=5.1;
	float c= 0.0;
	float distMax =100.0;
	float steps = 300.0;
	float color = 0.0;
	float cl;
	float dist = clamp((1.0-dot(vec3(0,0,-1.0),r))*4.0,0.0,1.0);
	int cc =int(mix(300.0,1000.0,dist));
	float uu =mix(1.0,0.25,dist);
	vec3 p3 = vec3(0);
	for(int i=0; i<cc; ++i) {
		float d2 ;
		float d;
		if(color<0.001)d = mapHyper(p);
		c =d;  
		if( c>seuil )
		{vec3 p2 =p;
			if(p3.x==0.0)p3=p;
			for(int j;j<20;j++)
			{
				if(color<0.2)d2= mapHyper(p2);
				else
				d2 = 5.2;
				if(d2>seuil)
				{
					color = color*0.9 + d2*0.02*0.1;
				}
				p2 +=normalize(vec3(-0.0,-0.0,-5.0))*0.42;
			} 
		}
		cl = 1.0-color;
        p+=r*distMax/steps*uu;
		//p+=r*distMax/float(cc)*uu;
	}

	vec2 off=vec2(1.1,0.0);
	vec3 n=normalize(mapHyper(p3)-vec3(mapHyper(p3-off.xyy), mapHyper(p3-off.yxy), mapHyper(p3-off.yyx)));

	//compositing
	vec3 col=vec3(0);
	col = mix(vec3(0.0,0.0,0.2),vec3(0.88,0.88,0.9),cl);
	float fog =  clamp((length(p3-s)-fogSize)*fogCoef,0.0,1.0);
	col = mix(col,vec3(0.88,0.88,0.9),fog);
	fragColor = vec4(col,1.0);
}

