const float M_PI = 3.14159265358979323846264338327950288;

/** Properties **/

const int N = 5; // Number of points

const float radius = 1.0; // Outer Radius: absolute value (in UV)
const float iradius = 0.6; // Inner Radius: relative to outer
const float glow = 0.3; // Glow Size: relative

const vec4 inColor = vec4(1,1,0,1);
const vec4 outColor = vec4(0,1,1,1);
const vec4 glowColor = vec4(1,0,0.5,1);

/** Animation **/

const float rotateSpeed = 0.05;
const float waveSpeed = 0.5;
const vec4 waveColor = vec4(0.2,0.5,1.0,0.4);

const vec4 transp = vec4(0,0,0,0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Starting from center
    uv.xy -= 0.5;
    uv.xy *= -2.0;

    // Adjusted to aspect ratio
    uv.x *= iResolution.x/iResolution.y;

    // Radius
    float r = length(uv);

    // Background color
    vec4 col = vec4(0,0,0,0);

    // Inside outer circle
    if (r < radius) {

        // Linear position in circle
        // This is a simplified projection of the vertex vector on y
        float ly = uv.y/r;

        // Calculate polar angle
        float a = atan(uv.x,uv.y);

        // Rotate Animation
        a -= iTime*rotateSpeed;

        // Break angle into N sections
        // then rearrange from [0 ~ 1] to [1 ~ 0 ~ 1]
        // The section angle can be precalculated if N doesn't change
        float sa = M_PI/float(N); // section angle
        float p = fract(a/(2.0*sa)); // position in section (angular)
        p = abs(p-0.5)*2.0;

        // Get chord midpoint radius
        // A.K.A. height of the equilateral triangle inside the arc
        float cr = cos(sa)*radius;

        // Get chord point radius, given the angle 
        float pa = p*sa; // point angle
        float cpr = cr/cos(sa-pa);

        // rough method: maybe too expensive?
        // scale cr by triangle propotionality rule
        // The inner radius decomposition can be precalculated if N doesn't change
        float xp = cpr*sin(pa); // decompose cpr x
        float yp = cpr*cos(pa); // decompose cpr y
        float xi = sin(sa*0.5)*iradius*radius; // decompose inner radius x
        float yi = cos(sa*0.5)*iradius*radius; // decompose inner radius y

        //float yx = xi/((xp*(1.0-yi)/yp)+xi); // y component of border (if radius == 1)
        float yx = (xi*yp*radius)/(xp*radius-xp*yi+xi*yp);// y component of border (general radius)

        // Final result: the star border radius
        float border = yx/cos(pa);

        // Coloring (Internal + Glow)
        if (r < border) {
            float gl = 1.0-glow;
            float c = r/border;
            // star
            if (c < gl) {
                col = mix(inColor,outColor,c/gl);
            }
            // antialias
            else if (c < gl*1.025) {
                col = mix(outColor,glowColor,(c-gl)*50.0);
            }
            // glow
            else {
                col += glowColor*((1.0-c)/glow);
            }
        }

        // Wave animation
        float t = fract(iTime*waveSpeed);
        float rt = t*radius;

        if (r < rt) rt = 1.0-(rt - r)/radius;
        else rt = (r - rt)/radius;

        rt = fract(rt*2.0);

        // Lighten
        col += rt*waveColor*waveColor.a*col.a;
    }

    // Output to screen
    fragColor = col;
}

