float Worley( vec2 x )
{
    vec2 p = floor( x );
    vec2 w = fract( x );

    float m = 1.0;
    for( int j=-1; j<=1; j++ )
    for( int i=-1; i<=1; i++ )
    {
        vec2  neighbor = vec2( float(i), float(j) );
        vec2  neighbor_w = rand2( p + neighbor );  // 
        // Worley
    	//neighbor_w = 0.5 + 0.5*sin(_Time.x+6.2831*neighbor_w);
        vec2  r = neighbor + neighbor_w - w;
        float d = dot( r, r );
        m = min(m, d);
    }
    return m ;
}


// white noise, from https://www.shadertoy.com/view/4djSRW
vec2 hash23(vec3 p3)
{
	p3 = fract(p3 * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}

// halton low discrepancy sequence, from https://www.shadertoy.com/view/wdXSW8
vec2 halton (int index)
{
    const vec2 coprimes = vec2(2.0f, 3.0f);
    vec2 s = vec2(index, index);
	vec4 a = vec4(1,1,0,0);
    while (s.x > 0. && s.y > 0.)
    {
        a.xy = a.xy/coprimes;
        a.zw += a.xy*mod(s, coprimes);
        s = floor(s/coprimes);
    }
    return a.zw;
}

float blueNoise(vec2 p)
{
    //int m = int(p.y*iResolution.y*iResolution.x + p.x*iResolution.x) + iFrame;
    //m = m%iFrame;
    //vec2 n = halton(m);

    int mx = int(p.x*iResolution.x);// + int(iFrame);
    int my = int(p.y*iResolution.y);// + int(iFrame);

    mx = mx;// % int(512);
    my = my;// % int(1024);

    vec2 nx = halton(int(mx));
    vec2 ny = halton(int(my));

