float scale;


float map(vec3 p) 
{
    vec3 pp=p;
	p.z -= -iTime * 2.5;
	p.xy = abs(p.xy) - 1.0;
	if (p.x < p.y) p.xy = p.yx;
	p.z = mod(p.z, 4.0) - 2.0;

	p.x -= 3.2;
	p = abs(p);
	float s = 2.0;
	vec3 offset =p*1.5;

    float r;

    float aa = 7.6 + 1.1 * sin(iTime);
	for (float i = 0.0; i < 5.0; i++)
    {
		p = 1.0 - abs(p - 1.0);
		 r = -aa * clamp(0.38 * max(1.2 / dot(p, p), 1.), 0.0, 1.0);
		s *= r;
		p *= r;
        p += offset;
	}
	s = abs(s);
	scale = s;
	float a = 100.0;
	p -= clamp(p, -a, a);
	return (length(p)) /( s);
}

vec3 GetNormal(vec3 p)
{
	float d = map(p);
    vec2 e = vec2(.001, 0);
    vec3 n = d - vec3(
        map(p-e.xyy),
        map(p-e.yxy),
        map(p-e.yyx));
    return normalize(n);
}

