//Started from iq " Inverse Morton and Hilbert": https://www.shadertoy.com/view/XdlcWr


//https://www2.cs.arizona.edu/~bkmoon/papers/tkde-hilbert.pdf
//This paper compares morton , hilbert and something called gray coded curve: 
//It claims the gray coded curve is better at clustering than morton  order for 3D, but worse than hilbert
//It didn't really clarify what the gray coded curve was exactly but after fiddling with gray coding this appears to be the same as the curve in the paper

//There is an ugly worm that traces it see the steps

// 0 = Hilbert
// 1 = Morton
#define CURVE 1

const int level = 4;  // Number of points is 2^level in each dimension

int GrayToBinary32(int num){
		num ^= num >> 16;
		num ^= num >> 8;
		num ^= num >> 4;
		num ^= num >> 2;
		num ^= num >> 1;
		return num;
}

ivec2 curve( int i )
{
    ivec2 res = ivec2(0,0);

    i = GrayToBinary32(i);
    for( int k=0; k<level; k++ )
    {
        #if CURVE==0
        // inverse Hilbert, https://en.wikipedia.org/wiki/Hilbert_curve
        ivec2 r = ivec2( i>>1, i^(i>>1) ) & 1;
        if (r.y==0) { if(r.x==1) { res = (1<<k)-1-res; } res = res.yx; }
        #else
		// inverse Morton, http://www.iquilezles.org/www/articles/wavelet/wavelet.htm
        ivec2 r = ivec2( i, i>>1 ) & 1;
        #endif

        res += r<<k;
        i >>= 2;
    }
   // res.x = GrayToBinary32(res.x);
   // res.y = GrayToBinary32(res.y);
    return res;
}


float sdSqLine( in vec2 p, in vec2 a, in vec2 b, out float outH )
{
	vec2 pa = p - a, ba = b - a;
    float h = clamp(dot(pa,ba)/dot(ba,ba), 0.0, 1.0);
    outH = h;
    vec2 d = pa - ba*h;
    return dot(d,d);
}

