//Shader designed based of this tutorial: https://www.youtube.com/watch?v=PGtv-dBi2wE
//Tutorial project: https://www.shadertoy.com/view/XlGBW3

#define MAXDIS 100.
#define MAXSTEP 500
#define SURFDIS 0.001
#define LIGHTINTENSITY 10.
#define LIGHTCOL vec3(1,.0,.5)

float GetDis(vec3 p)
{
	vec4 s = vec4(0, 1.05, 6, 1);

    float sd =  abs(length(p-s.xyz)-s.w)-.1;
    float pd = p.y-1.;

    float d = max(sd, pd);
    pd = p.y;
    d=min(d,pd);

	s = vec4(3, 1.05, 6, 1);
    sd =  abs(length(p-s.xyz)-s.w)-.1;
    d = min(d,sd);

	s = vec4(5, 4, 7, 2);
    sd =  abs(length(p-s.xyz)-s.w)-.1;
    d = min(d,sd);

	s = vec4(-5.-sin(iTime), 4, 7, 1);
    sd =  abs(length(p-s.xyz)-s.w)-.1;
    d = min(d,sd);

    d = min(d,sd);

    return d;
}

float GetEDis(vec3 p)
{
    vec3 po = vec3(0,4.+sin(iTime),6);
    vec4 s = vec4(po, 0.25);


	//vec4 s = vec4(3, 1.05, 6, 3);
    return  abs(length(p-s.xyz)-s.w)-.1;

    //return abs(length(p-s.xyz)-s.w)-.1;
}

float GetRef(vec3 p)
{
	vec4 s = vec4(0, 1.05, 6, 1);

    float sd =  abs(length(p-s.xyz)-s.w)-.1;
    float pd = p.y-1.;

    float d = max(sd, pd);

    pd = p.y;

	s = vec4(3, 1.05, 6, 1);

    sd =  abs(length(p-s.xyz)-s.w)-.1;

    d = min(sd,pd);

    d=sd;
    d=min(sd,pd);
    if(d<SURFDIS)return .8;
    else return .2;
}

float RayMarch(vec3 ro, vec3 rd)
{
    float d=.0;

    for(int i = 0; i<MAXSTEP; i++)
    {
        vec3 p = ro+rd*d;
        float ds = GetDis(p);
        d+=ds;
        if(ds<SURFDIS || d>MAXDIS)
        {
            break;
        }
    }

    return(d);
}

vec3 GetNormal(vec3 p)
{
    float d = GetDis(p);
    vec2 e = vec2(.01,0);

    vec3 n = d-vec3(
    GetDis(p-e.xyy),
    GetDis(p-e.yxy),
    GetDis(p-e.yyx));

    return normalize(n);
}

vec3 TestMarch(vec3 ro, vec3 rd)
{
    float d=.0;

    for(int i = 0; i<MAXSTEP; i++)
    {
        vec3 p = ro+rd*d;
        float ds = GetDis(p);
        d+=ds;
        if(ds<SURFDIS || d>MAXDIS)
        {
            break;
        }
    }
    vec3 p = ro + rd * d;
    p = p;
    return(p);
}

vec3 GetLight (vec3 p)
{
    vec3 light = vec3(0,4.+1.0*sin(iTime),6);
    vec3 l = normalize(light-p);
    vec3 n = GetNormal(p);

    float diff = dot(n,l);

    float d = RayMarch(p+n*SURFDIS*2.,l);
    if(d<length(light-p))
    {
        diff*=.1;
    }
    diff*=LIGHTINTENSITY/(length(p-light)*length(p-light));

    vec3 dir=normalize(vec3(0, 5, -1)-p);

    vec3 lightCol = vec3(sin(iTime*5.)/2.+.5,0,.5);
    lightCol=vec3(1,1,1);

    return max(diff,0.) * lightCol;
}

vec3 Emission(vec3 ro, vec3 rd)
{
    //vec3 p = vec3(0,3.+sin(iTime),6);
    //vec4 s = vec4(p, 0.25);
    float d=.0;

    for(int i = 0; i<MAXSTEP; i++)
    {
        vec3 p = ro+rd*d;
        //float ds = length(p-s.xyz)-s.w;
        float ds = GetEDis(p);
        d+=ds;
        if(ds<SURFDIS)
        {
            //return vec3(1,1,1);
            //return vec3(sin(iTime*5.)/2.+.5,0,.5);


            float de = GetEDis(p);
            vec2 e = vec2(.01,0);

            vec3 n = normalize(de-vec3(
            GetEDis(p-e.xyy),
            GetEDis(p-e.yxy),
            GetEDis(p-e.yyx)));

            //return(normalize(n));

            vec3 pd = reflect(rd,n);
            float ref = 0.9;
            ref=GetRef(p);
            //vec3 diff = 1.-ref;

            vec3 rpos = TestMarch(p+n*SURFDIS*2.,pd);
            vec3 r = GetLight(rpos);
            ref=GetRef(rpos);
            r*=1.-ref;

            return r + vec3(.9,.9,.9);// * 100.;
        }
        if(d>MAXDIS)
        {
            return vec3(0);
        }
    }

    return vec3(0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;


    vec3 ro = vec3(0, 5.+2.0*cos(iTime), -1);
    ro = vec3(0,5., -1.+3.0*cos(iTime));
    vec3 rd = normalize(vec3(uv.x, uv.y-0.3, 0.7));

    vec3 p = TestMarch(ro, rd);

    vec3 diff=GetLight(p);


    //FIRST REFLECTION
    vec3 n = GetNormal(p);
    vec3 pd = reflect(rd,n);
    float ref = 0.9;
    ref=GetRef(p);
    diff *= 1.-ref;

    vec3 rpos = TestMarch(p+n*SURFDIS*2.,pd);
    vec3 r = GetLight(rpos);
    ref=GetRef(rpos);
    r*=1.-ref;

    //SECOND REFLECTION
    n = GetNormal(rpos);
    pd = reflect(pd,n);
    vec3 r2p = TestMarch(rpos+n*SURFDIS*2.,pd);

    r+=ref*GetLight(r2p)*(1.-GetRef(r2p));


    //COLOURING
    diff+=r*GetRef(p);
    //diff=Emission(ro,rd);

    vec3 col = diff;
    col+=Emission(ro, rd);

    fragColor = vec4(col,1.0);
}

//  https://gist.github.com/patriciogonzalezvivo/670c22f3966e662d2f83
//	Simplex 3D Noise 
//	by Ian McEwan, Ashima Arts
//
vec4 permute(vec4 x){return mod(((x*34.0)+1.0)*x, 289.0);}
vec4 taylorInvSqrt(vec4 r){return 1.79284291400159 - 0.85373472095314 * r;}

float snoise(vec3 v){ 
  const vec2  C = vec2(1.0/6.0, 1.0/3.0) ;
  const vec4  D = vec4(0.0, 0.5, 1.0, 2.0);

// First corner
  vec3 i  = floor(v + dot(v, C.yyy) );
  vec3 x0 =   v - i + dot(i, C.xxx) ;

// Other corners
  vec3 g = step(x0.yzx, x0.xyz);
  vec3 l = 1.0 - g;
  vec3 i1 = min( g.xyz, l.zxy );
  vec3 i2 = max( g.xyz, l.zxy );

  //  x0 = x0 - 0. + 0.0 * C 
  vec3 x1 = x0 - i1 + 1.0 * C.xxx;
  vec3 x2 = x0 - i2 + 2.0 * C.xxx;
  vec3 x3 = x0 - 1. + 3.0 * C.xxx;

// Permutations
  i = mod(i, 289.0 ); 
  vec4 p = permute( permute( permute( 
             i.z + vec4(0.0, i1.z, i2.z, 1.0 ))
           + i.y + vec4(0.0, i1.y, i2.y, 1.0 )) 
           + i.x + vec4(0.0, i1.x, i2.x, 1.0 ));

// Gradients
// ( N*N points uniformly over a square, mapped onto an octahedron.)
  float n_ = 1.0/7.0; // N=7
  vec3  ns = n_ * D.wyz - D.xzx;

  vec4 j = p - 49.0 * floor(p * ns.z *ns.z);  //  mod(p,N*N)

  vec4 x_ = floor(j * ns.z);
  vec4 y_ = floor(j - 7.0 * x_ );    // mod(j,N)

  vec4 x = x_ *ns.x + ns.yyyy;
  vec4 y = y_ *ns.x + ns.yyyy;
  vec4 h = 1.0 - abs(x) - abs(y);

  vec4 b0 = vec4( x.xy, y.xy );
  vec4 b1 = vec4( x.zw, y.zw );

  vec4 s0 = floor(b0)*2.0 + 1.0;
  vec4 s1 = floor(b1)*2.0 + 1.0;
  vec4 sh = -step(h, vec4(0.0));

  vec4 a0 = b0.xzyw + s0.xzyw*sh.xxyy ;
  vec4 a1 = b1.xzyw + s1.xzyw*sh.zzww ;

  vec3 p0 = vec3(a0.xy,h.x);
  vec3 p1 = vec3(a0.zw,h.y);
  vec3 p2 = vec3(a1.xy,h.z);
  vec3 p3 = vec3(a1.zw,h.w);

//Normalise gradients
  vec4 norm = taylorInvSqrt(vec4(dot(p0,p0), dot(p1,p1), dot(p2, p2), dot(p3,p3)));
  p0 *= norm.x;
  p1 *= norm.y;
  p2 *= norm.z;
  p3 *= norm.w;

// Mix final noise value
  vec4 m = max(0.6 - vec4(dot(x0,x0), dot(x1,x1), dot(x2,x2), dot(x3,x3)), 0.0);
  m = m * m;
  return 42.0 * dot( m*m, vec4( dot(p0,x0), dot(p1,x1), 
                                dot(p2,x2), dot(p3,x3) ) );
}

float safe_snoise(vec3 p)
{
  float f = snoise(p);
  if (isinf(f))
    return 0.0;
  return f;
}

// https://github.com/blender/blender/blob/master/intern/cycles/kernel/shaders/node_noise.h

float fractal_noise(vec3 p, float details, float roughness)
{
  float fscale = 1.0;
  float amp = 1.0;
  float maxamp = 0.0;
  float sum = 0.0;
  float octaves = clamp(details, 0.0, 16.0);
  int n = int(octaves);
  for (int i = 0; i <= n; i++) {
    float t = safe_snoise(fscale * p);
    sum += t * amp;
    maxamp += amp;
    amp *= clamp(roughness, 0.0, 1.0);
    fscale *= 2.0;
  }
  float rmd = octaves - floor(octaves);
  if (rmd != 0.0) {
    float t = safe_snoise(fscale * p);
    float sum2 = sum + t * amp;
    sum /= maxamp;
    sum2 /= maxamp + amp;
    return (1.0 - rmd) * sum + rmd * sum2;
  }
  else {
    return sum / maxamp;
  }
}

// https://github.com/blender/blender/blob/master/intern/cycles/kernel/shaders/node_noise_texture.osl
vec3 random_vec3_offset(float seed)
{
  return vec3(100.0 + snoise(vec3(seed)) * 100.0,
                 100.0 + snoise(vec3(seed)) * 100.0,
                 100.0 + snoise(vec3(seed)) * 100.0);
}

vec3 noiseTexture(vec3 pos, float detail, float roughness){
    vec3 p = pos;
    float value = fractal_noise(p, detail, roughness);

    return (vec3(value,
                fractal_noise(p+random_vec3_offset(0.), detail, roughness),
                fractal_noise(p+random_vec3_offset(1.), detail, roughness)));
}





void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv *= 2.;

    float detail1 = 16.;
    float detail2 = 1.;
    float roughness1 = .5;
    float roughness2 = .5;
    vec3 pIn1 = vec3(uv,iTime/5.);
    vec3 pIn2 = noiseTexture(pIn1,detail1,roughness1);

    vec3 col = noiseTexture(pIn2,detail2,roughness2);

    // contrast
    col *= .7;
    // brightness
    col += .5;


    fragColor = vec4(col,1.0);
}

