/////////////////////////////////////////////////////////////////////////////

/////////////////////////////////////////////////////////////////////////////
// scene specific settings

#define GEAR_REPEAT   5.0
#define TEXTURE_GEARS 0.9

/////////////////////////////////////////////////////////////////////////////
// ray marching values

#define RAY_MARCH_STEPS    88
#define RAY_MARCH_TOO_FAR  float( RAY_MARCH_STEPS )
#define RAY_MARCH_CLOSE    .0001
#define RAY_EPSILON        vec2( RAY_MARCH_CLOSE, .0 )
#define RAY_SHORTENING
#define RAY_RELAX_        0.1

/////////////////////////////////////////////////////////////////////////////
// anti-aliasing

#define ANTI_ALIAS_STEPS    1
#define ANTI_ALIAS_DISTANCE 3.

/////////////////////////////////////////////////////////////////////////////
// reflections

#define REFLECTION_NONE   0.
#define REFLECTION_SIMPLE 1.
#define REFLECTION_NICER  2.

float REFLECTION_LEVEL_ = REFLECTION_SIMPLE; // toggle with 'r' key

/////////////////////////////////////////////////////////////////////////////
// camera

const float CAMERA_SCALE = 6.;

/////////////////////////////////////////////////////////////////////////////
// useful constants and macros

#define PI2             6.283185307179586
#define TRIG(d, a)      ( d * vec2( cos( a ), sin( a ) ) )
#define RGB(r,g,b)      vec3( float(r)/255., float(g)/255., float(b)/255. )
#define EQUALS(a,b)     step( a, b ) * step( b, a )
#define VEC3Y(y)        vec3( .0, y, .0 )
#define VEC3Z(z)        vec3( .0, .0, z )
#define RAND2(v)        fract( 333433.444469 * sin( dot( v, vec2( 449.457, 359.367 ) ) ) )

#define MODO(v,f) ( mod( v + .5 * f, f ) - .5 * f )
#define DOT_DIFFERENCE(a,b) dot( a - b, a - b )

/////////////////////////////////////////////////////////////////////////////
//

mat2 rotate2d( in float angle ) {
    vec2 t = TRIG( 1., angle );
    return mat2( t.x, -t.y, t.y, t.x );
}

/////////////////////////////////////////////////////////////////////////////
// signed distance functions for types

// https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
float ndot( in vec2 a, in vec2 b ) { return a.x*b.x - a.y*b.y; }

float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float sdRhombus(vec3 p, float la, float lb, float h, float ra)
{
  p = abs(p);
  vec2 b = vec2(la,lb);
  float f = clamp( (ndot(b,b-2.0*p.xz))/dot(b,b), -1.0, 1.0 );
  vec2 q = vec2(length(p.xz-0.5*b*vec2(1.0-f,1.0+f))*sign(p.x*b.y+p.z*b.x-b.x*b.y)-ra, p.y-h);
  return min(max(q.x,q.y),0.0) + length(max(q,0.0));
}

float sdRoundedCylinder( vec3 p, float ra, float rb, float h )
{
  vec2 d = vec2( length(p.xz)-2.0*ra+rb, abs(p.y) - h );
  return min(max(d.x,d.y),0.0) + length(max(d,0.0)) - rb;
}

/////////////////////////////////////////////////////////////////////////////
// https://mercury.sexy/hg_sdf/

// Cylinder standing upright on the xz plane
float fCylinder(vec3 p, float r, float height) {
    float d = length(p.xz) - r;
    return max(d, abs(p.y) - height);
}

/////////////////////////////////////////////////////////////////////////////
// create the scene

#define GEAR  1.

vec2 which( in vec2 current, in vec2 test ) {
    return mix( current, test, step( test.x, current.x ) );
}

float gearAngleOffset( in vec3 point ) {    
    vec3 q = point.xzy;

#ifdef GEAR_REPEAT
    //q.xz = MODO( q.xz, GEAR_REPEAT );
    q = MODO( q, GEAR_REPEAT );
#endif

    vec2 id = point.xy - q.xz;
    float eo = mod( id.x + id.y + 77., 2. ) * 2. -1.;

    ///////////////////////////

    float angle_inc = .0; //offset for each gear
    angle_inc += floor( mod(id.x+id.y,2.) ) * .2; // alternate 
    angle_inc += eo * iTime; // spin the right way
    return angle_inc;
}

mat2 gearAngleMatrix( in vec3 point ) {
    return rotate2d( -gearAngleOffset( point ) * .4 );
}

float gearDistance( in vec3 point ) {
    vec3 q = point.xzy;

#ifdef GEAR_REPEAT
    q = MODO( q, GEAR_REPEAT );    
#endif

    vec2 id = point.xy - q.xz;
    float eo = mod( id.x + id.y + 77., 2. ) * 2. -1.;

    vec3 og = q;

    ///////////////////////////

    float angle = atan( q.x, q.z );

    float angle_inc = .0; //offset for each gear
    angle_inc += floor( mod(id.x+id.y,2.) ) * .2; // alternate 
    angle_inc += eo *iTime; // spin the right way

    mat2 rot = rotate2d( -angle_inc * .4 ); // sync is off...
    vec3 xo = og;
    xo.xz *= rot;  

    ///////////////////////////

    // this took a lot of fiddling...
    float r = 2.5 - .2 * step(.0,sin( 48.* angle + 20. * angle_inc ) );

    float gear = fCylinder( xo, r, .1 );
    gear = max( gear, -fCylinder( q, 1.86, .33 ) );

    ///////////////////////////
    // the inset cross

    float inset = fCylinder( xo, 2., .1 );

    r = .8;
    float f = -.77;
    float o = 1.1;
    o = 1.18;
    vec3 nSpot = vec3( -o, f - .4, +o );
    vec3 nSize = vec3( o * .5 );
    nSize.xz /= 2.;    

    float negative_inset = 1e33;
    negative_inset = min( negative_inset, sdBox( xo + nSpot.xyx, nSize ) );
    negative_inset = min( negative_inset, sdBox( xo + nSpot.xyz, nSize ) );
    negative_inset = min( negative_inset, sdBox( xo + nSpot.zyx, nSize ) );
    negative_inset = min( negative_inset, sdBox( xo + nSpot.zyz, nSize ) );
    negative_inset -= r;
    //return min(inset,negative_inset);

    inset = max( inset, -negative_inset );
    //return inset; // like a squared off flat cross

    gear = min( gear, inset );

    // cross
    float crozz = 1e33;
    crozz = min( crozz, sdRhombus( xo.xzy + VEC3Z( .18 ), 2.5, .5,.04,.1 ) -.04 );
    crozz = min( crozz, sdRhombus( xo.zxy + VEC3Z( .18 ), 2.5, .5,.04,.1 ) -.04 );

    // topper

    float topper = 1e33;
    topper = min( topper, sdRoundedCylinder( xo.xyz, .25,.2, .2 ) );
    topper = min( topper, sdRoundedCylinder( xo.xyz - VEC3Y( .3 ), .15,.06, .1 ) );
    //return topper;

    crozz = min( crozz, topper );
    crozz = max( crozz, -xo.y );
    gear = min( gear, crozz );

    return gear;
}

vec2 sceneDistance( in vec3 point ) {
    float gear = gearDistance( point );
    float back = point.z + 11.;

    vec2 closest = vec2( RAY_MARCH_TOO_FAR );
    closest = which( closest, vec2( gear, GEAR ));
#ifdef GEARS_REPEAT
    closest = which( closest, vec2( back, GEAR ));
#endif
    return closest;
}

//set color per object
vec3 colorFor( in float which ) {
    return vec3( .33 );
}

//set reflection per object
float reflectionFor(  in float which ) {
    return .6;
}

/////////////////////////////////////////////////////////////////////////////
// the ray marching bits

vec2 rayMarch( in vec3 origin, in vec3 direction ) {
    vec2 total = vec2(.0);
    for ( int i = 0 ; i < RAY_MARCH_STEPS ; i++ ) {
        vec3 point = origin + direction * total.x;
        vec2 current = sceneDistance( point );

#ifdef RAY_SHORTENING
        if ( total.x > RAY_MARCH_TOO_FAR || abs( current.x ) < RAY_MARCH_CLOSE ) {
            break;
        }
        // Note: Ray advancement occurs after checking for a surface hit.
        // Ray shortening: Shorter for the first few iterations.
        total.x += i<22? current.x*.33 : current.x*.88; 
        total.y = current.y;
#else
        total.x += current.x;
        total.y = current.y;

        if ( total.x > RAY_MARCH_TOO_FAR || abs(current.x) < RAY_MARCH_CLOSE ) {
            break;
        }     
#ifdef RAY_RELAX
        total.x += RAY_RELAX * current.x;
#endif

#endif
    }
    return total;
}

vec3 sceneNormal( in vec3 point ) {
    return normalize( sceneDistance( point ).x - vec3(
        sceneDistance( point - RAY_EPSILON.xyy ).x,
        sceneDistance( point - RAY_EPSILON.yxy ).x,
        sceneDistance( point - RAY_EPSILON.yyx ).x
    ));
}

/////////////////////////////////////////////////////////////////////////////
// lighting

vec3 textureForPoint( in vec3 point ) {
    vec2 center = iChannelResolution[1].xy * .5;
    vec2 uv = point.xy;
    uv *= gearAngleMatrix( point );
    uv += center;
    return texture( iChannel1, uv, .0 ).xyz;
}

float pointLight( vec3 point, vec3 normal, vec4 light ) {  
    vec3 towardLight = light.xyz - point;
    float toLight = length( towardLight );

#ifdef TEXTURE_GEARS
    towardLight = mix( towardLight, textureForPoint( point ), TEXTURE_GEARS );
#endif
    towardLight = normalize( towardLight );

    float diffuse = clamp( dot( normal, towardLight ), 0., 1. );

    vec3 lightStart = point + normal * RAY_MARCH_CLOSE * 2.;
    float d = rayMarch( lightStart, towardLight ).x;
    diffuse *= 1. - 0.5 * smoothstep( d * 0.9, d, toLight );

    float lightStrength = .7 + .3 * light.w / dot( toLight, toLight );  
    return diffuse * lightStrength;
}

vec3 colorPoint( vec3 point, vec3 normal, vec2 d, vec3 eye ) {
    eye.z-=5.;
    eye.x-=2.;

    vec4 light    = vec4( eye, 180. );
    float ambient = 0.07;
    float gamma   = .33;

    float lighting = pointLight( point, normal, light );
    lighting = ( 1. -  ambient ) * gamma * lighting;

    vec3 color = colorFor( d.y );

    //meh.. color += texture( iChannel1, point.xy, .0 ).xyz;
	return vec3( color * ambient + color * lighting );
}

vec3 mixInReflection( vec3 point, float which, vec3 normal, vec3 color, vec3 eye ) {
    float reflectionFactor = reflectionFor( which );
    float noReflection = step( .0, reflectionFactor );

    vec3 reflectionStart = point + normal * RAY_MARCH_CLOSE * 2.;
    vec2 reflectAt       = noReflection * rayMarch( reflectionStart, normal );
    float reflected      = noReflection * ( 1. - step( RAY_MARCH_TOO_FAR, reflectAt.x ) );

    vec3 reflectPoint = reflectionStart + normal * reflectAt.x;
    vec3 reflectNormal = reflected * sceneNormal( reflectPoint  );
    //reflectNormal += .25 * texture( iChannel1, point.xy, .0 ).xyz;

#ifdef REFLECTION_LEVEL
    vec3 reflection;  
    if ( REFLECTION_NICER == REFLECTION_LEVEL ) { 
        reflection = reflected * colorPoint( reflectPoint, reflectNormal, reflectAt, eye );
    } else {
        reflection = reflected * colorFor( reflectAt.y );
    }
    color = mix( color, mix( color, reflection, reflectionFactor ), reflected );
#endif 

    return color;
}

